import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'

class ModifService extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idService: '',
            nomService: '',
            imgService: '',
            tarif: '',
        }
        this.modifNomService = this.modifNomService.bind(this);
        this.modifTarif = this.modifTarif.bind(this);
        this.modif = this.modif.bind(this)
    }

    componentDidMount() {
        let id_service = this.props.location.state.id_service;
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/GetServiceById.php?id=' + id_service).then(res => {
            console.log(res)
            this.setState({
                idService: res.data.id_service,
                nomService: res.data.nomService,
                pathPhotoService: res.data.pathPhotoService,
                tarif: res.data.tarif
            })
        })
    }

    modifNomService(event) {
        this.setState({
            nomService: event.target.value
        });
    }

    modifImgService(e) {
        this.setState({ imgService: e.target.files })
    }

    modifTarif(event) {
        this.setState({
            tarif: event.target.value
        });
    }

    modif() {

        console.log(this.state.nomService)
        console.log(this.state.imgService)
        console.log(this.state.tarif)

        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_snaii/UpdService.php?id=' + this.state.idService,
            type: 'POST',
            data: {
                nomService: this.state.nomService,
                pathImgService: "images/photos_services/" + this.state.imgService[0].name,
                tarif: this.state.tarif
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    // alert("Félicitation la modification des informations du nouveau service a été effectuée avec succès !");
                    // document.location = "/Services";
                    this.props.history.push('/Services');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });

        let files = this.state.imgService;
        console.warn('data files', files)
        let reader = new FileReader();
        reader.readAsDataURL(files[0])
        reader.onload = (e) => {
            console.log(e)
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_snaii/UploadPhotoService.php',
                type: 'POST',
                data: {
                    file: e.target.result,
                    nomfile: this.state.imgService[0].name
                },
                dataType: 'json',
                success: function (code_html, statut) {
                }
            }
            )
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div style={{backgroundColor:'white'}} className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification Service</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}

                    <form>
                        <div class="row">
                            <div class="col-md-6">
                                <div class="form-group">
                                    <label>Nom service</label>
                                    <input type="text" class="form-control" value={this.state.nomService} onChange={this.modifNomService.bind(this)} />
                                </div>
                            </div>
                            <div class="col-md-6">
                                <div class="form-group">
                                    <label>Photo service (png/jpg/jpeg/gif) </label>
                                    <input type="file" class="form-control" name="file" onChange={(e) => this.modifImgService(e)} />
                                </div>
                            </div>
                            <div class="col-md-6">
                                <div class="form-group">
                                    <label>Tarif</label>
                                    <input type="number" class="form-control" value={this.state.tarif} onChange={this.modifTarif.bind(this)} />
                                </div>
                            </div>
                        </div>
                    </form>

                    <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif.bind(this)}>Confirmer</button>
                    </div>

                </div>
            </div>
        );
    }
}

export default withRouter(ModifService);
