import React, { Component } from 'react';
import SimpleSchema from 'simpl-schema';
import { SimpleSchema2Bridge } from 'uniforms-bridge-simple-schema-2';
import { AutoForm } from 'uniforms-bootstrap4';

const signupSchema = new SimpleSchema({
    firstName: String,
    lastName: String,
    gender: {
        type: String,
        allowedValues: ['male', 'female']
    },
    email: {
        label: 'Email Address',
        type: String,
        regEx: SimpleSchema.RegEx.EmailWithTLD
    },
    password: {
        type: String,
        min: 6,
        uniforms: {
            type: 'password'
        }
    },
    confirmPassword: {
        type: String,
        uniforms: {
            type: 'password'
        },
        custom() {
            if (this.field('password').value != this.value) {
                return 'passwordMismatch';
            }
        }
    }
});

signupSchema.messageBox.messages({
    en: {
        "passwordMismatch": "Passwords mismatch",
    },
});

const formSignupSchema = new SimpleSchema2Bridge(signupSchema);

class Forms extends Component {

    handleSubmit(user) {
        console.log(user)
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '257px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Validation Forms</h4>
                            <ol className="breadcrumb">
                                <li><a href="#">Dashboard</a></li>
                                <li className="active">Validation Forms Page</li>
                            </ol>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* row */}
                    <div className="row" style={{display:'flex',justifyContent:'center'}}>
                        <div className="col-sm-4">
                            <div className="white-box">
                                <h3>Form Validation</h3>
                                <AutoForm schema={formSignupSchema} onSubmit={this.handleSubmit} />
                            </div>
                        </div>
                    </div>
                    {/* /.row */}
                </div>
                {/* /.container-fluid */}
            </div>
        );
    }
}

export default Forms;
