import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'

class StatiquePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listpermissions: [],
            faveurGestionRole: [],
            faveurGestionUtilisateur: [],
            faveurFamilleArticles: [],
            faveurArticles: [],
            faveurUnites: [],
            firstInterface: 0,
        }
        this.clearStorage = this.clearStorage.bind(this);
        this.firstInterface = React.createRef()
        this.changeColorWhenClicked = this.changeColorWhenClicked.bind(this);
    }

    componentDidMount() {
        var array_permission = JSON.parse((localStorage.getItem('tab_permission')));
        if (array_permission != null) {
            this.setState({ listpermissions: array_permission })
            var gestionRole = array_permission[0]
            this.setState({ faveurGestionRole: gestionRole })
            var gestionUtilisateur = array_permission[1]
            this.setState({ faveurGestionUtilisateur: gestionUtilisateur })
            var familleArticles = array_permission[2]
            this.setState({ faveurFamilleArticles: familleArticles })
            var articles = array_permission[3]
            this.setState({ faveurArticles: articles })
            var unites = array_permission[4]
            this.setState({ faveurUnites: unites })
        }
        else {
            this.props.history.push('/');
        }
    }

    clearStorage() {
        localStorage.clear();
        this.props.history.push('/');
    }

    changeColorWhenClicked() {
        var firstInterface = this.firstInterface.current;
        firstInterface.style.backgroundColor = "#13DAFE"
        firstInterface.style.color = "white"
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header"> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <div className="top-left-part"><a className="logo"><img style={{ backgroundColor: 'white' }} className="img-circle" width='30px' height='30px' src="images/lagora.png" alt="Agora" />&nbsp;<span className="hidden-xs">LAGORA</span></a></div>
                        <ul className="nav navbar-top-links navbar-left hidden-xs">
                            <li><a href="javascript:void(0)" className="open-close hidden-xs waves-effect waves-light"><i className="ti-menu icon-arrow-left-circle" /></a></li>
                        </ul>
                        <ul className="nav navbar-top-links navbar-right pull-right">
                            <li>
                                <span className="profile-pic"> <img onClick={this.clearStorage.bind(this)} src="images/users/logout.png" alt="user-img" width={25} className="img-circle" /><b className="hidden-xs">Déconnexion</b> </span>
                            </li>
                        </ul>
                    </div>
                    {/* /.navbar-header */}
                    {/* /.navbar-top-links */}
                    {/* /.navbar-static-side */}
                </nav>
                <div className="navbar-default sidebar nicescroll" role="navigation">
                    <div className="sidebar-nav navbar-collapse">
                        <ul className="nav in" id="side-menu">
                            <li className="sidebar-search hidden-sm hidden-md hidden-lg">
                                <div className="input-group custom-search-form">
                                    <input type="text" className="form-control" placeholder="Search..." />
                                    <span className="input-group-btn">
                                        <button className="btn btn-default" type="button"><i className="ti-search" /> </button>
                                    </span>
                                </div>
                            </li>
                            {(this.state.faveurGestionRole.ajouter == 1 || this.state.faveurGestionRole.explorer == 1 || this.state.faveurGestionRole.modifier == 1 || this.state.faveurGestionRole.supprimer == 1) ?
                                (<li>
                                    <Link to="/Roles" onClick={this.changeColorWhenClicked.bind(this)} ref={this.firstInterface} className="waves-effect" ><i class="fas fa-user-edit"></i> Gestion de role</Link>
                                </li>) : (null)}
                            {(this.state.faveurGestionUtilisateur.ajouter == 1 || this.state.faveurGestionUtilisateur.explorer == 1 || this.state.faveurGestionUtilisateur.modifier == 1 || this.state.faveurGestionUtilisateur.supprimer == 1) ?
                                (<li >
                                    <Link to="/Utilisateurs" className="waves-effect" ><i class="fas fa-users"></i> Gestion d'utilisateur</Link>
                                </li>) : (null)}
                            {(this.state.faveurUnites.ajouter == 1 || this.state.faveurUnites.explorer == 1 || this.state.faveurUnites.modifier == 1 || this.state.faveurUnites.supprimer == 1) ?
                                (<li>
                                    <Link to="/Unites" className="waves-effect" ><i class="fas fa-people-carry"></i> Unités</Link>
                                </li>) : (null)}
                            {(this.state.faveurFamilleArticles.ajouter == 1 || this.state.faveurFamilleArticles.explorer == 1 || this.state.faveurFamilleArticles.modifier == 1 || this.state.faveurFamilleArticles.supprimer == 1) ?
                                (<li>
                                    <Link to="/FamilleArticles" className="waves-effect"><i className="fas fa-book-reader" /> Familles articles </Link>
                                </li>) : (null)}
                            {(this.state.faveurArticles.ajouter == 1 || this.state.faveurArticles.explorer == 1 || this.state.faveurArticles.modifier == 1 || this.state.faveurArticles.supprimer == 1) ?
                                (<li>
                                    <Link to="/Articles" className="waves-effect"><i className="fas fa-utensils" /> Articles </Link>
                                </li>) : (null)}
                        </ul>
                    </div>
                    {/* /.sidebar-collapse */}
                </div>
                {this.props.children}
            </div>
        );
    }
}

export default withRouter(StatiquePage);
