import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            barreNavigation: [{ nomElement: 'Eleve', arraySousElement: [{ nomSousElement: 'Apprenants', link: '/Apprenants' }, { nomSousElement: 'Groupes', link: '/Groupes' }, { nomSousElement: 'Types paiements eleves', link: '/TypesPaiementEleve' }, { nomSousElement: 'Reglements eleves', link: '/ReglApprenant' }] }]
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Menu</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    {this.state.barreNavigation.map((el, index) =>
                                        <div className="col-md-4">
                                            <div className="white-box">
                                                <h3>{el.nomElement}</h3>
                                                <div className="myadmin-dd dd" id="nestable">
                                                    <ol className="dd-list">
                                                        <li className="dd-item" data-id={index}>
                                                            {el.arraySousElement.map(ele =>
                                                                <Link to={ele.link}><div className="dd-handle">{ele.nomSousElement}</div></Link>)}
                                                        </li>
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Menu;
