import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'

class AjoutEnseignant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            prenom: '',
            nom: '',
            selectedType: 'CIN',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: new Date(),
            villeDelivreSelonType: '',
            dateNaissance: new Date(),
            lieuNaissance: '',
            adresseOfficiel: '',
            adresseTun: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            imageVerso: null,
            qualification: '',
        }
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addNumSelonType = (event) => {
        this.setState({
            numSelonType: event.target.value
        });
    }

    addNationalite = (event) => {
        this.setState({
            nationalite: event.target.value
        });
    }

    addDateLivraison = d => {
        this.setState({
            dateDelivSelonType: d
        });
    };

    addVilleDelivreSelonType = (event) => {
        this.setState({
            villeDelivreSelonType: event.target.value
        });
    }

    addDateNaissance = d => {
        this.setState({
            dateNaissance: d
        });
    };

    addLieuNaissance = (event) => {
        this.setState({
            lieuNaissance: event.target.value
        });
    }

    addAdresseOfficiel = (event) => {
        this.setState({
            adresseOfficiel: event.target.value
        });
    }

    addAdresseTun = (event) => {
        this.setState({
            adresseTun: event.target.value
        });
    }

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addGender = (selectedGender) => {
        this.setState({ selectedGender: selectedGender });
    }

    addMobile = (event) => {
        this.setState({
            mobile: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    addQualification = (event) => {
        this.setState({
            qualification: event.target.value
        });
    }

    addTarif = (event) => {
        this.setState({
            tarif: event.target.value
        });
    }

    ajout = () => {
        console.log(this.state.prenom)
        console.log(this.state.nom)
        console.log(this.state.selectedType)
        console.log(this.state.numSelonType)
        console.log(this.state.nationalite)
        // console.log(this.state.dateDelivSelonType)
        let dateDelivSelonType = this.state.dateDelivSelonType
        let month_one = '' + (dateDelivSelonType.getMonth() + 1);
        let day_one = '' + dateDelivSelonType.getDate();
        let year_one = dateDelivSelonType.getFullYear();
        if (month_one.length < 2)
            month_one = '0' + month_one;
        if (day_one.length < 2)
            day_one = '0' + day_one;
        let convertDateDelivSelonType = [year_one, month_one, day_one].join('-');
        console.log(convertDateDelivSelonType)
        console.log(this.state.villeDelivreSelonType)
        // console.log(this.state.dateNaissance)
        let dateNaissance = this.state.dateNaissance
        let month_two = '' + (dateNaissance.getMonth() + 1);
        let day_two = '' + dateNaissance.getDate();
        let year_two = dateNaissance.getFullYear();
        if (month_two.length < 2)
            month_two = '0' + month_two;
        if (day_two.length < 2)
            day_two = '0' + day_two;
        let convertDateNaissance = [year_two, month_two, day_two].join('-');
        console.log(convertDateNaissance)
        console.log(this.state.lieuNaissance)
        console.log(this.state.adresseOfficiel)
        console.log(this.state.adresseTun)
        console.log(this.state.email)
        console.log(this.state.selectedGender)
        console.log(this.state.mobile)
        console.log(this.state.tel)
        console.log(this.state.imageVerso)
        console.log(this.state.qualification)
        // Test si photo vide
        var pathImageVerso = '';
        console.log(this.state.imageVerso)
        if (this.state.imageVerso != null) {
            pathImageVerso = "images/Photo_Enseignants/" + this.state.imageVerso[0].name
        }
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddEnseignant.php',
            type: 'POST',
            data: {
                prenom: this.state.prenom,
                nom: this.state.nom,
                type: this.state.selectedType,
                numero: this.state.numSelonType,
                nationalite: this.state.nationalite,
                dateType: convertDateDelivSelonType,
                villeType: this.state.villeDelivreSelonType,
                dateNaissance: convertDateNaissance,
                lieuNaissance: this.state.lieuNaissance,
                adresseOfficiel: this.state.adresseOfficiel,
                adresseTun: this.state.adresseTun,
                email: this.state.email,
                sexe: this.state.selectedGender,
                mobile: this.state.mobile,
                tel: this.state.tel,
                photo: pathImageVerso,
                qualification: this.state.qualification,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Enseignants');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
        // Test si photo vide
        if (this.state.imageVerso != null) {
            let files = this.state.imageVerso;
            console.warn('data files', files)
            let reader = new FileReader();
            reader.readAsDataURL(files[0])
            reader.onload = (e) => {
                console.log(e)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Uploadphoto.php',
                    type: 'POST',
                    data: {
                        file: e.target.result,
                        nomfile: this.state.imageVerso[0].name
                    },
                    dataType: 'json',
                    success: function (code_html, statut) {
                    }
                }
                )
            }
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout enseignant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Prnom</label>
                                            <input type="text" class="form-control" placeholder="Prnom" onChange={this.addPrenom} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom</label>
                                            <input type="text" class="form-control" placeholder="Nom" onChange={this.addNom} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Sexe</label>
                                        <RadioGroup
                                            name="sexe"
                                            selectedValue={this.state.selectedGender}
                                            onChange={this.addGender}>
                                            <label>
                                                <Radio value="Masculin" />Masculin
                                                </label>
                                            <label>
                                                <Radio value="Feminin" />Feminin
                                                </label>
                                        </RadioGroup>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Type</label>
                                        <RadioGroup
                                            name="type"
                                            selectedValue={this.state.selectedType}
                                            onChange={this.addType}>
                                            <label>
                                                <Radio value="CIN" />CIN
                                                </label>
                                            <label>
                                                <Radio value="Passeport" />Passeport
                                                </label>
                                        </RadioGroup>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Numero</label>
                                            <input type="text" class="form-control" placeholder="Numero selon le type" onChange={this.addNumSelonType} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nationalit</label>
                                            <input type="text" class="form-control" placeholder="Nationalit" onChange={this.addNationalite} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Delivr le</label>
                                        <div class="form-group">
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDelivSelonType}
                                                onChange={this.addDateLivraison}
                                                customInput={<input style={{ height: '38px', borderRadius: '5px', width: '520px', border: 'solid 1px', borderColor: '#B3B3B3' }} />}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Delivr ville de</label>
                                            <input type="text" class="form-control" placeholder="Delivr dans ville de" onChange={this.addVilleDelivreSelonType} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Date naissance</label>
                                        <div class="form-group">
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateNaissance}
                                                onChange={this.addDateNaissance}
                                                customInput={<input style={{ height: '38px', borderRadius: '5px', width: '520px', border: 'solid 1px', borderColor: '#B3B3B3' }} />}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Lieu de naissance</label>
                                            <input type="text" class="form-control" placeholder="Lieu de naissance" onChange={this.addLieuNaissance} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Adresse officiel</label>
                                            <input type="text" class="form-control" placeholder="Adresse officiel" onChange={this.addAdresseOfficiel} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Adresse en tunisie</label>
                                            <input type="text" class="form-control" placeholder="Adresse en tunisie" onChange={this.addAdresseTun} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Email</label>
                                            <input type="text" class="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Mobile</label>
                                            <input type="text" class="form-control" placeholder="Mobile" onChange={this.addMobile} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Tel</label>
                                            <input type="text" class="form-control" placeholder="Tel" onChange={this.addTel} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Photo verso </label>
                                            <input type="file" class="form-control" name="file" onChange={(e) => this.addImageVerso(e)} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Qualification</label>
                                            <input type="text" class="form-control" placeholder="Qualification" onChange={this.addQualification} style={{ borderRadius: '5px',border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutEnseignant);
