import React, { Component } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'

class AjoutFraisFormation extends Component {
    constructor(props) {
        super(props);
        this.state = {
            frais: '',
            mntFrais: ''
        }
    }

    addFrais = (event) => {
        this.setState({
            frais: event.target.value
        });
    }

    addMntFrais = (event) => {
        this.setState({
            mntFrais: event.target.value
        });
    }

    ajout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddFraisFormation.php',
            type: 'POST',
            data: {
                frais: this.state.frais,
                mntFrais: this.state.mntFrais,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/FraisFormation');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout frais</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Frais</label>
                                            <input type="text" class="form-control" placeholder="frais" onChange={this.addFrais} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Montant frais</label>
                                            <input type="number" class="form-control" placeholder="Montant frais" onChange={this.addMntFrais} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutFraisFormation);
