import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import axios from 'axios'
import jQuery from 'jquery'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ImpayeMois extends Component {
    constructor(props) {
        super(props);
        this.state = {
            arrayElvImp: [],
            mois: [{ value: 10, label: 'Octobre (1ére tranche)' }, { value: 11, label: 'Novembre (2éme tranche)' }, { value: 12, label: 'Decembre (3éme tranche)'}, {value: 1, label: 'Janvier (4éme tranche)' }, { value: 2, label: 'Férvier (5éme tranche)' }, { value: 3, label: 'Mars (6éme tranche)' }, { value: 4, label: 'Avril (7éme tranche)' }, { value: 5, label: 'Mai (8éme tranche)' }, { value: 6, label: 'Juin (9éme tranche)' }, { value: 7, label: 'Juillet (10éme tranche)' }],
            selectedMois: '',
            initStatus: 1,
            listEleve: [],
            selectedIdEleve: '',
            listGroup: [],
            selectedIdGroup: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        listEleve: code_html
                    });
                }
            });
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupesInscrip.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        listGroup: code_html
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    searchEleve = selectedIdEleve => {
        if (selectedIdEleve !== null) {
            this.setState({ selectedIdEleve: selectedIdEleve });
        }
        else {
            this.setState({
                selectedIdEleve: ''
            });
        }
    };

    searchMois = selectedMois => {
        if (selectedMois != null) {
            this.setState({ selectedMois: selectedMois });
        }
        else {
            this.setState({
                selectedMois: '',
            });
        }
    }

    searchGroup = selectedIdGroup => {
        if (selectedIdGroup !== null) {
            this.setState({ selectedIdGroup: selectedIdGroup });
        }
        else {
            this.setState({
                selectedIdGroup: ''
            });
        }
    };

    recherche = () => {
        if (this.state.selectedMois != '') {
            var mois = this.state.selectedMois.value
            // if (this.state.selectedIdEleve != '') {
            //     var eleve = this.state.selectedIdEleve.value
            // }
            // else {
            //     var eleve = ''
            // }
            if (this.state.selectedIdGroup != '') {
                var groupe = this.state.selectedIdGroup.value
            }
            else {
                var groupe = ''
            }
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImpayerMens.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire,
                    mois: mois,
                    // eleve: eleve,
                    groupe: groupe
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        arrayElvImp: code_html,
                        initStatus: 0
                    });
                }
            });
        }
        else {
            toast.error('⛔ Veuillez saisir le mois !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Les impayés étudiants</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Menu" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    {/* <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Apprenant</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.searchEleve}
                                                options={this.state.listEleve}
                                            />
                                        </div>
                                    </div> */}
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Mois</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.searchMois(e)}
                                                options={this.state.mois}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Groupe</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.searchGroup}
                                                options={this.state.listGroup}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.recherche}>Rechercher</button>
                                    </div>
                                </div>
                                <div className="row">
                                    {this.state.arrayElvImp.length > 0 ?
                                        (<Fragment>
                                            <h3 style={{ marginLeft: '10px' }}>Impayer par mois :</h3>
                                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>N° inscription</th>
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Etudiant</th>
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Spécialité</th>
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Formation</th>
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Niveau</th>
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Groupe</th>
                                                        {/* <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>N° tranche</th> */}
                                                        <th scope="col" style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>Reste</th>
                                                    </tr>
                                                </thead>
                                                {this.state.arrayElvImp.map(el =>
                                                    <tbody>
                                                        <tr>
                                                            <td style={{ textAlign: 'center' }}>{el.numIns}</td>
                                                            <td style={{ textAlign: 'center' }}>{el.nomComplet}</td>
                                                            <td style={{ textAlign: 'center' }}>{el.specialite}</td>
                                                            <td style={{ textAlign: 'center' }}>{el.formation}</td>
                                                            <td style={{ textAlign: 'center' }}>{el.niveau}</td>
                                                            <td style={{ textAlign: 'center' }}>{el.groupe}</td>
                                                            {/* <td style={{ textAlign: 'center' }}>{el.numTrch}</td> */}
                                                            <td style={{ textAlign: 'center' }}>{el.reste}</td>
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                        </Fragment>) : this.state.initStatus == 1 ? (null) : (<div>
                                            <h3 style={{ marginLeft: '10px' }}>Impayer par mois :</h3>
                                            <p style={{ marginLeft: '10px' }}>Tous les éleves ont payé la totalité de cette tranche</p>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                        <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                    </div>
                </div>
            </div>
        );
    }
}
export default ImpayeMois;
