import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';

class ModifArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idArticle: '',
            nomUnite: '',
            poids: '',
            prixUnitaire: '',
            prixKg: '',
            sousArticle: '',
            familleArticles: [],
            selectedFamilleArticles: ''
        }
        this.modifNomUnite = this.modifNomUnite.bind(this);
        this.modifPoids = this.modifPoids.bind(this);
        this.modifPrixUnitaire = this.modifPrixUnitaire.bind(this);
        this.modifPrixKg = this.modifPrixKg.bind(this);
        this.modifSousArticle = this.modifSousArticle.bind(this);
        this.modif = this.modif.bind(this)
    }

    componentDidMount() {
        let id_article = this.props.location.state.id_article;
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/GetArticleById.php?id=' + id_article).then(res => {
            console.log(res)
            this.setState({
                idArticle: res.data[0][0].id_article,
                nomUnite: res.data[0][0].nomUnite,
                poids: res.data[0][0].poids,
                prixUnitaire: res.data[0][0].prixUnitaire,
                prixKg: res.data[0][0].prixKg,
                sousArticle: res.data[0][0].sousArticle,
                selectedFamilleArticles: res.data[1]
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/FamilleArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                familleArticles: res.data
            })
        })
    }

    modifNomUnite(event) {
        this.setState({
            nomUnite: event.target.value
        });
    }

    modifPoids(event) {
        this.setState({
            poids: event.target.value
        });
    }

    modifPrixUnitaire(event) {
        this.setState({
            prixUnitaire: event.target.value
        });
    }

    modifPrixKg(event) {
        this.setState({
            prixKg: event.target.value
        });
    }

    modifSousArticle(event) {
        this.setState({
            sousArticle: event.target.value
        });
    }

    modifFamilleArticles = selectedFamilleArticles => {
        this.setState({ selectedFamilleArticles });
        console.log(`Famille article selected:`, selectedFamilleArticles);
    };

    modif() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/UpdArticle.php?id=' + this.state.idArticle,
            type: 'POST',
            data: {
                nomUnite: this.state.nomUnite,
                poids: this.state.poids,
                prixUnitaire: this.state.prixUnitaire,
                prixKg: this.state.prixKg,
                sousArticle: this.state.sousArticle,
                idFamilleArticles: this.state.selectedFamilleArticles.value
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Articles');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification article</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom unite</label>
                                            <input type="text" class="form-control" value={this.state.nomUnite} onChange={this.modifNomUnite.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Poids</label>
                                            <input type="number" class="form-control" value={this.state.poids} onChange={this.modifPoids.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Prix unitaire</label>
                                            <input type="number" class="form-control" value={this.state.prixUnitaire} onChange={this.modifPrixUnitaire.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Prix par kg</label>
                                            <input type="number" class="form-control" value={this.state.prixKg} onChange={this.modifPrixKg.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Sous article</label>
                                            <input type="text" class="form-control" value={this.state.sousArticle} onChange={this.modifSousArticle.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Famille articles </label>
                                            <Select
                                                value={this.state.selectedFamilleArticles}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.modifFamilleArticles}
                                                options={this.state.familleArticles}
                                            />                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifArticle);
