import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import './paginate.css'
import { Fragment } from 'react';

class StatiquePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicScolarite: true
        }
    }

    clicScolarite = () => {
        this.setState({
            clicScolarite: true
        });
    }

    clearStorage = () => {
        this.props.history.push('/');
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header"> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <div className="top-left-part"><a className="logo"><img style={{ backgroundColor: 'white' }} className="img-circle" width='30px' height='30px' src="images/training.jpg" alt="Global Media Training" />&nbsp;<span style={{ fontSize: '13px' }} className="hidden-xs">Global Media Training</span></a></div>
                        <ul className="nav navbar-top-links navbar-left hidden-xs">
                            <li><a href="javascript:void(0)" className="open-close hidden-xs waves-effect waves-light"><i className="ti-menu icon-arrow-left-circle" /></a></li>
                        </ul>
                        <ul className="nav navbar-top-links navbar-right pull-right">
                            <li onClick={this.clicScolarite} className='hvr-back-pulse'><i class="fas fa-clipboard"></i>Scolarité</li>
                            <li>
                                <div onClick={this.clearStorage} className='hvr-shrink'><span style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="profile-pic"> <img src="images/users/logout.png" alt="user-img" width={25} className="img-circle" /><b className="hidden-xs">Déconnexion</b> </span></div>
                            </li>
                        </ul>
                    </div>
                    {/* /.navbar-header */}
                    {/* /.navbar-top-links */}
                    {/* /.navbar-static-side */}
                </nav>
                <div className="navbar-default sidebar nicescroll" role="navigation">
                    <div className="sidebar-nav navbar-collapse">
                        <ul className="nav in" id="side-menu">
                            <li className="sidebar-search hidden-sm hidden-md hidden-lg">
                                <div className="input-group custom-search-form">
                                    <input type="text" className="form-control" placeholder="Search..." />
                                    <span className="input-group-btn">
                                        <button className="btn btn-default" type="button"><i className="ti-search" /> </button>
                                    </span>
                                </div>
                            </li>
                            {this.state.clicScolarite == true ?
                                (
                                    <Fragment>
                                        <li>
                                            <Link to="/MatieresFormat" className="waves-effect" ><i class="fas fa-graduation-cap"></i> Matieres par formation</Link>
                                        </li>
                                        <li>
                                            <Link to="/FormationNiv" className="waves-effect" ><i class="fas fa-list-ol"></i> Formations</Link>
                                        </li>
                                        <li>
                                            <Link to="/Specialites" className="waves-effect" ><i class="fas fa-chalkboard"></i> Specialités</Link>
                                        </li>
                                        <li>
                                            <Link to="/Matieres" className="waves-effect" ><i class="fas fa-book-open"></i> Matieres</Link>
                                        </li>
                                        <li>
                                            <Link to="/Filieres" className="waves-effect" ><i class="fas fa-globe-africa"></i> Filiéres</Link>
                                        </li>
                                        <li>
                                            <Link to="/NivBase" className="waves-effect" ><i class="fas fa-book"></i> Niveau de base</Link>
                                        </li>
                                        <li>
                                            <Link to="/TypeEnseign" className="waves-effect" ><i class="fas fa-edit"></i> Type enseignement</Link>
                                        </li>
                                    </Fragment>) : (null)}
                        </ul>
                    </div>
                    {/* /.sidebar-collapse */}
                </div>
                {this.props.children}
            </div>
        );
    }
}

export default withRouter(StatiquePage);
