import React, { Component } from 'react'
import axios from 'axios'

class HistoriqueConnexion extends Component {
    constructor(props) {
        super(props);
        this.state = {
            historique: []
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/HistConnexion.php').then(res => {
            console.log(res)
            this.setState({
                historique: res.data
            })
        })
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Historique</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table className="table align-items-center table-flush">
                                        <thead className="thead-light">
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Utilisateur</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Date connexion</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Action</th>
                                            </tr>
                                        </thead>
                                        {this.state.historique.map(el =>
                                            <tbody>
                                                <tr>
                                                    <td>
                                                        {el.user}
                                                    </td>
                                                    <td>
                                                        {el.dateConnex}
                                                    </td>
                                                    <td>
                                                        <div>
                                                        {el.realisation.map(ele =>
                                                            <table className="table align-items-center table-flush">
                                                                <thead className="thead-light">
                                                                    <tr>
                                                                        <th style={{ textAlign: 'center' }} scope="col">Action</th>
                                                                        <th style={{ textAlign: 'center' }} scope="col">Nom table</th>
                                                                        <th style={{ textAlign: 'center' }} scope="col">Date action</th>
                                                                    </tr>
                                                                </thead>
                                                                <tbody>
                                                                    <td>
                                                                        {ele.action}
                                                                    </td><td>
                                                                        {ele.nomTable}
                                                                    </td><td>
                                                                        {ele.dateAction}
                                                                    </td>
                                                                </tbody>
                                                            </table>
                                                        )}
                                                        </div>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default HistoriqueConnexion;
