import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import Switch from "react-switch";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class ModifArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idArticle: '',
            nomArticle: '',
            selectedUnite: '',
            poids: '',
            prixUnitaire: '',
            prixKg: '',
            unites: [],
            familleArticles: [],
            selectedFamilleArticles: '',
            tousSousArticles: [],
            sousArticleAjouter: '',
            tousCombine: [],
            valueArticleAjouter: -1,
            labelArticleAjouter: '',
            quantiteAjouter: '',
            valueUniteCombineAjouter: '',
            labelUniteCombineAjouter: '',
            prixAjouter: '',
            checked: false,
            articles: [],
            modalSousArticle: false,
            modalArticleDuCompose: false,
            idSousArticleASupprimer: '',
            idArticleDuComposeASupprimer: '',
            vraiIdSousArticleASupprimer: '',
            idSousArticleDelete: [],
            vraiIdArticleDuComposeASupprimer: '',
            idArticleDuComposeDelete: []
        }
        this.modifNom = this.modifNom.bind(this);
        this.modifPoids = this.modifPoids.bind(this);
        this.modifPrixUnitaire = this.modifPrixUnitaire.bind(this);
        this.modifPrixKg = this.modifPrixKg.bind(this);
        this.additionSousArticle = this.additionSousArticle.bind(this);
        this.additionCombine = this.additionCombine.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.modif = this.modif.bind(this)
    }

    componentDidMount() {
        let id_article = this.props.location.state.id_article;
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/GetArticleByIdModif.php?id=' + id_article).then(res => {
            console.log('res retourné')
            console.log(res)
            this.setState({
                checked: res.data[0][0].checked,
                idArticle: res.data[0][0].id_article,
                nomArticle: res.data[0][0].nomArticle,
                selectedUnite: res.data[1],
                poids: res.data[0][0].poids,
                prixUnitaire: res.data[0][0].prixUnitaire,
                prixKg: res.data[0][0].prixKg,
                selectedFamilleArticles: res.data[2],
                tousSousArticles: res.data[3],
                tousCombine: res.data[4]
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/FamilleArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                familleArticles: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/UnitesSelect.php').then(res => {
            console.log(res)
            this.setState({
                unites: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/ArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                articles: res.data
            })
        })
    }

    handleChange(checked) {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    modifNom(event) {
        this.setState({
            nomArticle: event.target.value
        });
    }

    modifUnite = selectedUnite => {
        this.setState({ selectedUnite });
        console.log(`Unite selected:`, selectedUnite);
    };

    modifPoids(event) {
        this.setState({
            poids: event.target.value
        });
    }

    modifPrixUnitaire(event) {
        this.setState({
            prixUnitaire: event.target.value
        });
    }

    modifPrixKg(event) {
        this.setState({
            prixKg: event.target.value
        });
    }

    modifSousArticle = (event, idSousArticle) => {
        let nomSousArticle = event.target.value;
        this.setState({
            tousSousArticles: this.state.tousSousArticles.map((el, id) => (id === idSousArticle ? Object.assign(el, { sousArticle: nomSousArticle }) : el))
        });
    }

    modifFamilleArticles = selectedFamilleArticles => {
        this.setState({ selectedFamilleArticles });
        console.log(`Famille article selected:`, selectedFamilleArticles);
    };

    additionSousArticle() {
        let element = { sousArticle: this.state.sousArticleAjouter, idSousArticle: -1 }
        this.setState({
            tousSousArticles: [...this.state.tousSousArticles, element]
        })
    }

    additionCombine() {
        let element = { idDuCompose: -1, articleDuComposeSelect: { value: this.state.valueArticleAjouter, label: this.state.labelArticleAjouter }, quantite: this.state.quantiteAjouter, uniteDuComposeSelect: { value: this.state.valueUniteCombineAjouter, label: this.state.labelUniteCombineAjouter }, prix: this.state.prixAjouter }
        this.setState({
            tousCombine: [...this.state.tousCombine, element]
        })
    }

    modifArticle = (selectedArticle, idArticle) => {
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idArticle ? Object.assign(el, Object.assign(el.articleDuComposeSelect, selectedArticle)) : el))
        });
    }

    modifQuantite = (event, idQuantite) => {
        let quantite = event.target.value;
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idQuantite ? Object.assign(el, { quantite: quantite }) : el))
        });
    }

    modifUniteCombine = (selectedUniteCombine, idUniteCombine) => {
        this.setState({
            tousCombine: this.state.tousCombine.map((el, id) => (id === idUniteCombine ? Object.assign(el, Object.assign(el.uniteDuComposeSelect, selectedUniteCombine)) : el))
        });
        // Envoie id de l'article + quantité + unité
        var idArticle = this.state.tousCombine[idUniteCombine].articleDuComposeSelect.value;
        console.log('id article est = ' + idArticle)
        var quantiteArticle = this.state.tousCombine[idUniteCombine].quantite;
        console.log('quantite article est = ' + quantiteArticle)
        var uniteArticle = this.state.tousCombine[idUniteCombine].uniteDuComposeSelect.value;
        console.log('unite article est = ' + uniteArticle)
        // Reception prix
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/PrixArticles.php?id=' + idArticle,
            type: 'POST',
            data: {
                quantiteArticle: quantiteArticle,
                uniteArticle: uniteArticle
            },
            dataType: 'json',
            success: (code_html, statut) => {
                let prix = code_html;
                console.log(prix)
                this.setState({
                    tousCombine: this.state.tousCombine.map((el, id) => (id === idUniteCombine ? Object.assign(el, { prix: prix }) : el))
                },
                    function () {
                        var sommePrix = 0
                        for (var i = 0; i < this.state.tousCombine.length; i++) {
                            sommePrix += this.state.tousCombine[i].prix
                        }
                        this.setState({
                            prixUnitaire: sommePrix
                        });
                        console.log('La somme des prix est = ' + sommePrix)
                    });
            }
        });
    };

    // modifPrix = (event, idPrix) => {
    //     let prix = event.target.value;
    //     this.setState({
    //         tousCombine: this.state.tousCombine.map((el, id) => (id === idPrix ? Object.assign(el, { prix: prix }) : el))
    //     });
    // }

    toggleSousArticle = (idSousArticle, idSousArticleASupprimer) => {
        this.setState(prevState => ({
            modalSousArticle: !prevState.modalSousArticle,
            vraiIdSousArticleASupprimer: idSousArticle,
            idSousArticleASupprimer: idSousArticleASupprimer
        }));
    }

    toggleArticleDuCompose = (idDuCompose, idArticleDuComposeASupprimer) => {
        this.setState(prevState => ({
            modalArticleDuCompose: !prevState.modalArticleDuCompose,
            vraiIdArticleDuComposeASupprimer: idDuCompose,
            idArticleDuComposeASupprimer: idArticleDuComposeASupprimer
        }));
    }

    modalSousArticle = () => {
        this.setState(prevState => ({
            modalSousArticle: !prevState.modalSousArticle
        }));
    }

    modalArticleDuCompose = () => {
        this.setState(prevState => ({
            modalArticleDuCompose: !prevState.modalArticleDuCompose
        }));
    }

    deleteSousArticle = () => {
        let vraiIdSousArticleASupprimer = this.state.vraiIdSousArticleASupprimer
        this.setState({
            idSousArticleDelete: [...this.state.idSousArticleDelete, vraiIdSousArticleASupprimer]
        });
        this.state.tousSousArticles.splice(this.state.idSousArticleASupprimer, 1)
        this.setState(prevState => ({
            modalSousArticle: !prevState.modalSousArticle
        }));
    }

    deleteArticleDuCompose = () => {
        let vraiIdArticleDuComposeASupprimer = this.state.vraiIdArticleDuComposeASupprimer
        this.setState({
            idArticleDuComposeDelete: [...this.state.idArticleDuComposeDelete, vraiIdArticleDuComposeASupprimer]
        });
        let idArticleDuComposeASupprimer = this.state.idArticleDuComposeASupprimer
        let prixASupprimer = this.state.tousCombine[idArticleDuComposeASupprimer].prix
        let prixApresSoustraction = this.state.prixUnitaire - prixASupprimer
        this.setState({
            prixUnitaire: prixApresSoustraction
        });
        this.state.tousCombine.splice(this.state.idArticleDuComposeASupprimer, 1)
        this.setState(prevState => ({
            modalArticleDuCompose: !prevState.modalArticleDuCompose
        }));
    }

    modif() {
        // Initalitation
        // var valCompose = -1;
        // if (this.state.checked === true) {
        //     valCompose = 1;
        // }
        // else {
        //     valCompose = 0;
        // }
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_agora/UpdArticle.php?id=' + this.state.idArticle,
        //     type: 'POST',
        //     data: {
        //         nomArticle: this.state.nomArticle,
        //         poids: this.state.poids,
        //         prixUnitaire: this.state.prixUnitaire,
        //         prixKg: this.state.prixKg,
        //         arraySousArticles: this.state.tousSousArticles,
        //         idFamilleArticles: this.state.selectedFamilleArticles.value,
        //         idUnite: this.state.selectedUnite.value,
        //         arrayComposes: this.state.tousCombine,
        //         compose: valCompose,
        //         arrayIdSousArticlesSupprimer: this.state.idSousArticleDelete,
        //         arrayIdArticlesDuComposeSupprimer: this.state.idArticleDuComposeDelete
        //     },
        //     dataType: 'json',
        //     success: (code_html, statut) => {
        //         if (code_html.Result == "OK") {
        //             this.props.history.push('/Articles');
        //         }
        //         else if (code_html.Result == "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
        console.log(this.state.tousSousArticles)
        console.log(this.state.tousCombine)
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification article</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <label>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Combine</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                </div>
                                <div class="row">
                                    <table style={{ width: '85%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Unité</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Poids</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix unitaire piece</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix unitaire/kg</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Famille</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td><input type="text" value={this.state.nomArticle} onChange={this.modifNom.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ width: '20%' }}>
                                                    <Select
                                                        value={this.state.selectedUnite}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.modifUnite}
                                                        options={this.state.unites}
                                                    />  </td>
                                                <td><input type="number" value={this.state.poids} onChange={this.modifPoids.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                {this.state.checked == false ?
                                                    (<td><input type="number" value={this.state.prixUnitaire} onChange={this.modifPrixUnitaire.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    ) :
                                                    (<td><input type="number" disabled value={this.state.prixUnitaire} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    )}
                                                <td><input type="number" value={this.state.prixKg} onChange={this.modifPrixKg.bind(this)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ width: '20%' }}>
                                                    <Select
                                                        value={this.state.selectedFamilleArticles}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.modifFamilleArticles}
                                                        options={this.state.familleArticles}
                                                    />  </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                                <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionSousArticle.bind(this)} color="secondary">Ajouter ligne</Button>
                                    </div>
                                    <table style={{ width: '10%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Sous article</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Supprimer</th>
                                            </tr>
                                        </thead>
                                        {this.state.tousSousArticles.map((el, id) =>
                                            <tbody>
                                                <tr>
                                                    <td><input type="text" value={el.sousArticle} onChange={(e) => this.modifSousArticle(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '50%' }} className='buttonSupprimer' onClick={() => this.toggleSousArticle(el.idSousArticle, id)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                {this.state.checked === true ?
                                    (<div class="row">
                                        <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                            <Button style={{ borderRadius: '5px' }} onClick={this.additionCombine.bind(this)} color="secondary">Ajouter ligne</Button>
                                        </div>
                                        <table style={{ width: '65%', marginTop: '10px' }} class="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Article</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Quantité</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Unité</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Prix</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Supprimer</th>
                                                </tr>
                                            </thead>
                                            {this.state.tousCombine.map((el, id) =>
                                                <tbody>
                                                    <tr>
                                                        <td><Select
                                                            value={el.articleDuComposeSelect}
                                                            isClearable={true}
                                                            isSearchable={true}
                                                            onChange={(e) => this.modifArticle(e, id)}
                                                            options={this.state.articles}
                                                        />  </td>
                                                        <td style={{ width: '15%' }}><input type="number" value={el.quantite} onChange={(e) => this.modifQuantite(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                        <td><Select
                                                            value={el.uniteDuComposeSelect}
                                                            isClearable={true}
                                                            isSearchable={true}
                                                            onChange={(e) => this.modifUniteCombine(e, id)}
                                                            options={this.state.unites}
                                                        />  </td>
                                                        <td style={{ width: '15%' }}><input type="text" value={el.prix} onChange={(e) => this.modifPrix(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                        <td style={{ textAlign: 'center' }}><button style={{ width: '33%' }} className='buttonSupprimer' onClick={() => this.toggleArticleDuCompose(el.idDuCompose, id)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>) : (null)}
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif.bind(this)}>Confirmer</button>
                                </div>
                                <Modal isOpen={this.state.modalSousArticle} toggle={this.modalSousArticle} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.modalSousArticle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer ce sous article ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteSousArticle()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.modalSousArticle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <Modal isOpen={this.state.modalArticleDuCompose} toggle={this.modalArticleDuCompose} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.modalArticleDuCompose}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer ce article ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteArticleDuCompose()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.modalArticleDuCompose()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifArticle);
