import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import './paginate.css'
import { Fragment } from 'react';
import Collapsible from 'react-collapsible';

class StatiquePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicSession: true,
            clicScolarite: false,
            clicReglement: false,
            listpermissions: [],
            permissionGestionRoles: [],
            permissionGestionUtilisateurs: [],
            permissionTypeEnseign: [],
            permissionNivBase: [],
            permissionFilieres: [],
            permissionMatieres: [],
            permissionSpecialites: [],
            permissionNiveaux: [],
            permissionMatFor: [],
            permissionEnseignants: [],
            permissionModalPay: [],
            permissionGroupes: [],
            permissionApprenants: [],
            permissionFraisFormation: [],
            permissionTypePayEleve: [],
            permissionFormations: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        if (userPermissions != null) {
            this.setState({ listpermissions: userPermissions })
            var gestionRoles = userPermissions[0]
            this.setState({ permissionGestionRoles: gestionRoles })
            var gestionUtilisateurs = userPermissions[1]
            this.setState({ permissionGestionUtilisateurs: gestionUtilisateurs })
            var typeEnseign = userPermissions[2]
            this.setState({ permissionTypeEnseign: typeEnseign })
            var nivBase = userPermissions[3]
            this.setState({ permissionNivBase: nivBase })
            var filieres = userPermissions[4]
            this.setState({ permissionFilieres: filieres })
            var matieres = userPermissions[5]
            this.setState({ permissionMatieres: matieres })
            var specialites = userPermissions[6]
            this.setState({ permissionSpecialites: specialites })
            var niveaux = userPermissions[7]
            this.setState({ permissionNiveaux: niveaux })
            var matFor = userPermissions[8]
            this.setState({ permissionMatFor: matFor })
            var enseignants = userPermissions[9]
            this.setState({ permissionEnseignants: enseignants })
            var modalPay = userPermissions[10]
            this.setState({ permissionModalPay: modalPay })
            var groupes = userPermissions[12]
            this.setState({ permissionGroupes: groupes })
            var apprenants = userPermissions[14]
            this.setState({ permissionApprenants: apprenants })
            var fraisFormation = userPermissions[15]
            this.setState({ permissionFraisFormation: fraisFormation })
            var papiers = userPermissions[16]
            this.setState({ permissionPapiers: papiers })
            var typePayEleve = userPermissions[17]
            this.setState({ permissionTypePayEleve: typePayEleve })
            var formations = userPermissions[19]
            this.setState({ permissionFormations: formations })
            if (userPermissions[0].idRoleUser != 1) {
                this.setState({
                    clicScolarite: true
                });
            }
        }
        else {
            this.props.history.push('/');
        }
    }

    clicSession = () => {
        this.setState({
            clicSession: true,
            clicScolarite: false,
            clicReglement: false
        });
        this.props.history.push('/Users');
    }

    clicScolarite = () => {
        this.setState({
            clicScolarite: true,
            clicSession: false,
            clicReglement: false
        });
        this.props.history.push('/TypeEnseign');
    }

    clicReglement = () => {
        this.setState({
            clicReglement: true,
            clicScolarite: false,
            clicSession: false
        });
        this.props.history.push('/ReglApprenant');
    }

    clearStorage = () => {
        localStorage.clear();
        this.props.history.push('/');
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header" style={{ backgroundColor: '#3a3f51' }}> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <div className="top-left-part"><a className="logo"><img style={{ backgroundColor: 'white' }} className="img-circle" width='30px' height='30px' src="images/art_metier.png" alt="Art_Metier" />&nbsp;<span style={{ fontSize: '13px' }} className="hidden-xs">Arts et Métiers</span></a></div>
                        <ul className="nav navbar-top-links navbar-left hidden-xs">
                            {(this.state.permissionGestionRoles.ajouter == 1 || this.state.permissionGestionRoles.explorer == 1 || this.state.permissionGestionRoles.modifier == 1 || this.state.permissionGestionRoles.supprimer == 1) ?
                                (<li className="dropdown"> <a style={{ color: 'white' }} className="dropdown-toggle waves-effect waves-light" data-toggle="dropdown" href="#" aria-expanded="false"><i style={{ marginRight: '5px', color: 'white' }} class="fas fa-universal-access"></i>Habilitation</a>
                                </li>) : (null)}
                            <li className="dropdown"> <a style={{ color: 'white' }} className="dropdown-toggle waves-effect waves-light" data-toggle="dropdown" href="#" aria-expanded="false"><i style={{ marginRight: '5px', color: 'white' }} class="fas fa-clipboard"></i>Scolarite</a>
                            </li>
                            <li className="dropdown"> <a style={{ color: 'white' }} className="dropdown-toggle waves-effect waves-light" data-toggle="dropdown" href="#" aria-expanded="false"><i style={{ marginRight: '5px', color: 'white' }} class="far fa-id-card"></i>Eleve</a>
                            </li>
                            <li className="dropdown"> <a style={{ color: 'white' }} className="dropdown-toggle waves-effect waves-light" data-toggle="dropdown" href="#" aria-expanded="false"><i style={{ marginRight: '5px', color: 'white' }} class="fas fa-user-tie"></i>Formateur</a>
                            </li>
                            <li className="dropdown"> <a style={{ color: 'white' }} className="dropdown-toggle waves-effect waves-light" data-toggle="dropdown" href="#" aria-expanded="false"><i style={{ marginRight: '5px', color: 'white' }} class="far fa-credit-card"></i>Reglement</a>
                            </li>
                        </ul>
                        <ul className="nav navbar-top-links navbar-right pull-right">
                            <li>
                                <div onClick={this.clearStorage} className='hvr-shrink'><span style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="profile-pic"> <img src="images/users/logout.png" alt="user-img" width={25} className="img-circle" /><b style={{ color: 'white' }} className="hidden-xs">Déconnexion</b> </span></div>
                            </li>
                        </ul>
                    </div>
                </nav>
                {this.props.children}
            </div>
        );
    }
}

export default withRouter(StatiquePage);
