import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';

class ModifUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idUser: '',
            nom: '',
            roles: [],
            selectedRole: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            let idUser = this.props.location.state.idUser;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetUserById.php?id=' + idUser).then(res => {
                console.log(res)
                this.setState({
                    idUser: res.data.idUser,
                    nom: res.data.nom,
                    selectedRole: res.data.role
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectRoles.php').then(res => {
                console.log(res)
                this.setState({
                    roles: res.data
                })
            })
        }
    }

    modifNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    modifRole = selectedRole => {
        this.setState({ selectedRole });
        console.log(`Role selected:`, selectedRole);
    };

    modif = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdUser.php?id=' + this.state.idUser,
            type: 'POST',
            data: {
                nom: this.state.nom,
                idRole: this.state.selectedRole.value,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Users');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification utilisateur</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom</label>
                                            <input type="text" class="form-control" value={this.state.nom} onChange={this.modifNom} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Role</label>
                                            <Select
                                                value={this.state.selectedRole}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.modifRole}
                                                options={this.state.roles}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifUser);
