import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';

class AjoutInventaire extends Component {
    constructor(props) {
        super(props);
        this.state = {
            numeroInventaire: '',
            dateInventaire: new Date(),
            emplacements: [],
            selectedEmplacement: ''
        }
        this.addnumeroInventaire = this.addnumeroInventaire.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/EmplacementsSelect.php').then(res => {
            console.log(res);
            this.setState({ emplacements: res.data });
        });
    }

    addnumeroInventaire(event) {
        this.setState({
            numeroInventaire: event.target.value
        });
    }

    addDateInventaire = d => {
        this.setState({
            dateInventaire: d
        });
    };

    addEmplacement = selectedEmplacement => {
        this.setState({ selectedEmplacement });
        console.log(`Emplacement selected:`, selectedEmplacement);
    };

    ajout() {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/AddInventaire.php',
            type: 'POST',
            data: {
                numeroInventaire: this.state.numeroInventaire,
                dateInventaire: this.state.dateInventaire,
                idEmplacement: this.state.selectedEmplacement
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Inventaires');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout inventaire</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Numero</label>
                                            <input type="number" class="form-control" placeholder="Numero" onChange={this.addnumeroInventaire.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>

                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutInventaire);
