import React, { Component, Fragment } from "react";
import axios from "axios";
import jQuery from "jquery";
import {Link,BrowserRouter as Router,Route,Switch,withRouter,HashRouter, Redirect,} from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from "react-select";
import { RadioGroup, Radio } from "react-radio-group";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Slide, Zoom, Flip, Bounce } from "react-toastify";
class AjoutDevis extends Component {
  constructor(props) {
    super(props);
    this.state = {
      objectif: "",
      protocol:"",
      ingredient:"",
      protocolesoin:[],
      selectprotocol:"",
    };
  }
  componentDidMount() {
    if (JSON.parse(localStorage.getItem("userPermissions")) != null) {
        axios.get(process.env.REACT_APP_API_URL + 'Back_HOODA/Getprotocol.php').then(res => {
            console.log(res)
            this.setState({
                protocolesoin: res.data
            })
        })
    } else {
      document.location = "/";
    }
  }
  addobjectif = (event) => {
    this.setState({
      objectif: event.target.value,
    });
  };
  addingredient = (event) => {
    this.setState({
      ingredient: event.target.value,
    });
  };
  addprotocol = (event) => {
    this.setState({
      protocol: event.target.value,
    });
  };
  addprotocols = (selectprotocol) => {
    if (selectprotocol != null) {
      this.setState({ selectprotocol });
      console.log(this.state.selectprotocol)
    } else {
      this.setState({
        selectprotocol: "",
      });
    }
  };
  ajout = () => {
    if (
      this.state.objectif != ""
    ) {
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var userId = userPermissions[0].idUser;
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_gera/Addobjectif.php",
        type: "POST",
        data: {
          objectif: this.state.objectif,
          ingredient:this.state.ingredient,
          protocol:this.state.protocol,
          protocolchoisi:this.state.selectprotocol
        },
        dataType: "json",
        success: (code_html, statut) => {
          if (code_html.Result == "OK") {
              this.props.history.push("/Objectif");
            } else {
                toast.error(
                  ":sens_interdit: Veuillez vérifier les champs !",
                  { containerId: "A" }
                );
                }
            },
      });
} else {
    toast.error(
      ":sens_interdit: Veuillez vérifier les champs!",
      { containerId: "A" }
    );
    }
}
  render() {
    return (
      <div id="page-wrapper" style={{ minHeight: "263px", marginLeft: "0px" }}>
        <div className="container-fluid">
          <div className="row bg-title">
            <div className="col-lg-12">
              <h4 className="page-title">Ajout Un Devis</h4>
            </div>
            {/* /.col-lg-12 */}
          </div>
          {/* /.row */}
          <div className="row">
            <div className="col-sm-12">
              <div className="white-box">
                <div
                  style={{
                    display: "flex",
                    justifyContent: "flex-start",
                    alignItems: "center",
                    marginBottom: "10px",
                    marginRight: "20px",
                  }}
                >
                  <Link to="/Protection">
                    {" "}
                    <button
                      style={{
                        background: "#DC143C",
                        border: "1px solid #DC143C",
                        borderRadius: "5px",
                      }}
                      type="button"
                      className="btn btn-info"
                    >
                      {" "}
                      <i
                        style={{ paddingRight: "5px" }}
                        className="fas fa-arrow-circle-left"
                      ></i>
                      Retour
                    </button>
                  </Link>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>objectif</label>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="objectif"
                        onChange={this.addobjectif}
                        style={{
                          borderRadius: "5px",
                          border: "solid 1px #B3B3B3",
                        }}
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Ingredient</label>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="ingredient"
                        onChange={this.addingredient}
                        style={{
                          borderRadius: "5px",
                          border: "solid 1px #B3B3B3",
                        }}
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Protocole</label>
                      <input
                        type="text"
                        className="form-control"
                        placeholder="protocole"
                        onChange={this.addprotocol}
                        style={{
                          borderRadius: "5px",
                          border: "solid 1px #B3B3B3",
                        }}
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                      <div className="form-group">
                        <label>Protocole soin</label>
                        <Select
                          isMulti={true}
                          isClearable={true}
                          isSearchable={true}
                          onChange={this.addprotocols}
                          options={this.state.protocolesoin}
                        />
                      </div>
                    </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    marginBottom: "10px",
                  }}
                >
                  <button
                    style={{ borderRadius: "5px" }}
                    type="button"
                    className="btn btn-success"
                    onClick={this.ajout}
                  >
                    Confirmer
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <ToastContainer
          transition={Flip}
          enableMultiContainer
          containerId={"A"}
          position={toast.POSITION.TOP_RIGHT}
          autoClose={2500}
        />
      </div>
    );
  }
}
export default withRouter(AjoutDevis);
