import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class Cycles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Cycle',
                    selector: 'cycle',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Heure',
                    selector: 'heure',
                    sortable: false,
                    center: true,
                    style: { width: '5%' }
                },
                {
                    name: 'Tarif',
                    selector: 'tarif',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Tarif avec carte',
                    selector: 'carte',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Session/Horraire/Date',
                    cell: row => <div onClick={() => this.dateCycle(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="far fa-calendar-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Date</p></div></div>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    style: { width: '25%' }
                },
                {
                    name: 'Enseignant',
                    cell: row => <div onClick={() => this.infoEnseign(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#40E0D0' }} className="fas fa-chalkboard-teacher"></i></div><div style={{ marginLeft: '5px' }}><p>Information</p></div></div>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    style: { width: '30%' }
                },
                {
                    name: 'Modifier',
                    cell: row => <Link to={{
                        pathname: '/ModifCycle',
                        state: { idCycle: row.idCycle }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} class="fas fa-pencil-alt"></i></button></Link>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    style: { width: '5%' }
                },
                {
                    name: 'Desactiver',
                    cell: row => <button className='buttonSupprimer' onClick={() => this.toggletwo(row.idCycle)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    style: { width: '5%' }
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            idCycle: '',
            pending: true,
            permissionGestionRoles: '',
            detailCycle: [],
            infoEnseign: []
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    newElement(page, totalRows) {
        this.setState({
            pending: true
        },
            function () {
                console.log(page)
                console.log(totalRows)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: page
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }.bind(this)
        )
    }

    perPage(currentRowsPerPage, currentPage) {
        this.setState(
            {
                nbrEltperPage: currentRowsPerPage,
                pending: true
            }
            , function () {
                console.log(currentRowsPerPage)
                console.log(currentPage)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: currentRowsPerPage,
                        page: currentPage
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        );
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            function () {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    dateCycle = (row) => {
        this.setState({
            detailCycle: row.detailCycle,
        }, () => {
            console.log(this.state.detailCycle)
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    infoEnseign = (row) => {
        this.setState({
            infoEnseign: row.infoEnseignant,
        }, () => {
            console.log(this.state.infoEnseign)
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal,
        }));
    }

    // deleteCycle() {
    //     var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
    //     var userId = userPermissions[0].idUser
    //     jQuery.ajax({
    //         url: process.env.REACT_APP_API_URL + "Back_mediatraining/DeleteCycle.php?id=" + this.state.idCycle + "&userId=" + userId,
    //         type: 'POST',
    //         dataType: 'json',
    //         success: (code_html, statut) => {
    //             this.setState({
    //                 modal: false
    //             }, () => {
    //                 jQuery.ajax({
    //                     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
    //                     type: 'POST',
    //                     data: {
    //                         query: '',
    //                         limit: this.state.nbrEltperPage,
    //                         page: 1
    //                     },
    //                     dataType: 'json',
    //                     success: (code_html, statut) => {
    //                         console.log(code_html)
    //                         this.setState({
    //                             nombreTotalElement: code_html.total,
    //                             data: code_html.data
    //                         });
    //                     }
    //                 });
    //             })
    //         }
    //     });
    // }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Cycles</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/AjoutCycle" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i class="fas fa-plus"></i>Cycle</button></Link>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
