import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';

class ModifSpecialite extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idSpecialite: '',
            specialite: {},
            ListFilieres: [],
            selectedFiliere: ''
        }
    }

    componentDidMount() {
        let idSpecialite = this.props.location.state.idSpecialite;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetSpecialiteById.php?id=' + idSpecialite).then(res => {
            console.log(res)
            this.setState({
                idSpecialite: res.data.idSpecialite,
                specialite: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php').then(res => {
            console.log(res)
            this.setState({
                ListFilieres: res.data
            })
        })
    }

    updNomSpecialite = (event) => {
        let nomSpecialite = event.target.value;
        this.setState({
            specialite: Object.assign(this.state.specialite, { nomSpecialite: nomSpecialite })
        });
    }

    updFiliere = (selectedFiliere) => {
        let filiere = selectedFiliere;
        this.setState({
            specialite: Object.assign(this.state.specialite, { filiere: filiere })
        });
    }

    modif = () => {
        let year = new Date().getFullYear();
        console.log(this.state.specialite)
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdSpecialite.php?id=' + this.state.idSpecialite,
            type: 'POST',
            data: {
                specialite: this.state.specialite,
                year: year
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Specialites');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification specialité</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Filiére</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td><input type="text" value={this.state.specialite.nomSpecialite} onChange={(e) => this.updNomSpecialite(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ width: '20%' }}><Select
                                                    value={this.state.specialite.filiere}
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={(e) => this.updFiliere(e)}
                                                    options={this.state.ListTypeEns}
                                                />  </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifSpecialite);
