import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Fragment } from 'react';

class MenuParametrage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            barreNavigation: [{ titre: 'Paramétrage', element: [{ nomElement: "Habilitation", iconElement: 'fas fa-universal-access', linkElement: '', arraySousElement: [{ nomSousElement: 'Gestion role', linkSousElement: '/Roles', permission: 0, iconSousElement: 'fas fa-user-edit' }, { nomSousElement: 'Gestion utilisateur', linkSousElement: '/Users', permission: 0, iconSousElement: 'fas fa-users' }, { nomSousElement: 'Historique', linkSousElement: '/HistoriqueConnexion', permission: 0, iconSousElement: 'fas fa-calendar-alt' }] }, { nomElement: "Education", iconElement: 'fas fa-university', linkElement: '', arraySousElement: [{ nomSousElement: 'Type enseignement', linkSousElement: '/TypeEnseign', permission: 0, iconSousElement: 'fas fa-edit' }, { nomSousElement: 'Matieres', linkSousElement: '/Matieres', permission: 0, iconSousElement: 'fas fa-book-open' }, { nomSousElement: 'Matieres par formation', linkSousElement: '/MatieresFormat', permission: 0, iconSousElement: 'fas fa-graduation-cap' }] }, { nomElement: "Scolarite", iconElement: 'fas fa-clipboard', linkElement: '', arraySousElement: [{ nomSousElement: 'Filiéres', linkSousElement: '/Filieres', permission: 0, iconSousElement: 'fas fa-bookmark' }, { nomSousElement: 'Specialités', linkSousElement: '/Specialites', permission: 0, iconSousElement: 'fas fa-chalkboard' }, { nomSousElement: 'Formations', linkSousElement: '/Formations', permission: 0, iconSousElement: 'fas fa-scroll' }, { nomSousElement: 'Niveaux', linkSousElement: '/Niveaux', permission: 0, iconSousElement: 'fas fa-list-ol' }, { nomSousElement: 'Groupes', linkSousElement: '/Groupes', permission: 0, iconSousElement: 'fas fa-user-friends' }] }, { nomElement: "Paiement", iconElement: 'fas fa-comment-dollar', linkElement: '', arraySousElement: [{ nomSousElement: 'Frais formation', linkSousElement: '/FraisFormation', permission: 0, iconSousElement: 'fas fa-money-check-alt' }, { nomSousElement: 'Types paiement eleves', linkSousElement: '/TypesPaiementEleve', permission: 0, iconSousElement: 'fas fa-money-bill-wave' }, { nomSousElement: 'Types paiement enseignants', linkSousElement: '/ModPaiEnseignants', permission: 0, iconSousElement: 'fas fa-hand-holding-usd' }] }, { nomElement: "Papiers Apportées", iconElement: 'fas fa-paste', linkElement: '/Papiers', arraySousElement: [] }, { nomElement: "Niveau de base", iconElement: 'fas fa-book', linkElement: '/NivBase', arraySousElement: [] }], iconTitre: 'fas fa-align-left' }]
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        if (userPermissions != null) {
        }
        else {
            this.props.history.push('/');
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Menu</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box" >
                                <div className="row">
                                    {this.state.barreNavigation.map((el, index) =>
                                        <div className="col-md-4">
                                            <div className="white-box" style={{ border: 'solid 1px', borderRadius: '5px' }}>
                                                <h3 style={{ border: 'solid 1px', backgroundColor: 'black', color: 'white' }}><i style={{ paddingRight: '5px', paddingLeft: '5px' }} className={el.iconTitre}></i>{el.titre}</h3>
                                                <div className="myadmin-dd dd" id="nestable">
                                                    <ol className="dd-list">
                                                        {el.element.map((ele, indice) =>
                                                            <div>
                                                                {
                                                                    (ele.arraySousElement.length > 0) ?
                                                                        // Noeud Parent Ayant des Fils Feuilles
                                                                        (<Fragment>
                                                                            <li className="dd-item" data-id={index}>
                                                                                {/* <button data-action="collapse" type="button">Collapse</button><button data-action="expand" type="button" style={{display: 'none'}}>Expand</button> */}
                                                                                <div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600', color: '#2b2b2b' }}><i style={{ paddingRight: '5px' }} className={ele.iconElement}></i>{ele.nomElement}</div>
                                                                                <ol className="dd-list">
                                                                                    {ele.arraySousElement.map(elm =>
                                                                                        <li className="dd-item" data-id={index + 1}>
                                                                                            <Link style={{ color: '#2b2b2b' }} to={elm.linkSousElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={elm.iconSousElement}></i>{elm.nomSousElement}</div></Link>
                                                                                        </li>)}
                                                                                </ol>
                                                                            </li> </Fragment>) :
                                                                        // Noeud Feuille (Terminaison)
                                                                        (<Link style={{ color: '#2b2b2b' }} to={ele.linkElement}><div style={{ border: '1px solid rgba(120,130,140,.13)', padding: '8px 16px', height: 'auto', fontWeight: '600' }}><i style={{ paddingRight: '5px' }} className={ele.iconElement}></i>{ele.nomElement}</div></Link>)
                                                                }
                                                            </div>
                                                        )}
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default MenuParametrage;
