import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';

class PayReglementEnseignant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomEns: '',
            enseignant: [],
            selectedEns: '',
            regEnsNv: [],
            regEnsAnc: [],
            initStatus: 1,
            blockRegl: false,
            blockPaiement: false,
            disabledForAnc: false,
            disabledCycleAnc: false,
            disabledSurveilAnc: false,
            disabledForNv: false,
            disabledCycleNv: false,
            disabledSurveilNv: false,
            datePaiement: new Date(),
            tabModPay: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date() }],
            modal: false,
            indexLignASupprimer: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEnsByAnneeScol.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    this.setState({
                        enseignant: code_html
                    })
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
                console.log(res)
                this.setState({
                    listModPay: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addEns = (selectedEns) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (selectedEns != null) {
            this.setState(
                {
                    selectedEns: selectedEns,
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetReglEnsByEnsId.php',
                        type: 'POST',
                        data: {
                            ensId: this.state.selectedEns.value,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            this.setState({
                                regEnsAnc: code_html[0],
                                regEnsNv: code_html[1],
                                nomEns: this.state.selectedEns.label,
                                initStatus: 0
                            }, () => {
                                this.setState({
                                    blockRegl: true
                                });
                            })
                        }
                    });
                });
        }
        else {
            this.setState(
                {
                    selectedEns: '',
                }, () => {
                    this.setState({
                        blockRegl: false,
                        nomEns: '',
                        initStatus: 1
                    });
                });
        }
    }

    // Année précédente
    mntPayerForAnc = (event, index, indice) => {
        var mntPayFor = event.target.value
        if (this.state.regEnsAnc[index].formation[indice].mntTotal >= parseInt(mntPayFor) + this.state.regEnsAnc[index].formation[indice].totalPayer) {
            var arrayFor = this.state.regEnsAnc
            var elFor = arrayFor[index].formation[indice]
            elFor: Object.assign(elFor, { mntAPayer: mntPayFor })
            this.setState({
                regEnsAnc: arrayFor
            }, () => {
                var mntRest = this.state.regEnsAnc[index].formation[indice].mntTotal - (this.state.regEnsAnc[index].formation[indice].totalPayer + parseInt(this.state.regEnsAnc[index].formation[indice].mntAPayer))
                var arrayFor = this.state.regEnsAnc
                var elFor = arrayFor[index].formation[indice]
                elFor: Object.assign(elFor, { reste: mntRest })
                this.setState({
                    regEnsAnc: arrayFor
                });
            });
        }
        else {
            toast.error('⛔ Le montant saisie est supérieur !!', { containerId: 'A' });
        }
    }

