import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import axios from 'axios'

class ExplRappJour extends Component {
    constructor(props) {
        super(props);
        this.state = {
            arrayRapport: [],
            magasin: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idRappJour = this.props.location.state.idRappJour;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetRapportJourById.php?id=' + idRappJour).then(res => {
                this.setState({
                    arrayRapport: res.data,
                    magasin: res.data[0].magasin
                }, () => {
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">{'Rapport magasin : ' + this.state.magasin}</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/RapportJour"> <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="table-responsive" style={{ textAlign: 'center', display: 'flex' }}>
                                        <table style={{ width: '50%' }} className="table align-items-center table-flush">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Photo avant</th>
                                                </tr>
                                            </thead>
                                            {this.state.arrayRapport.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        {el.typePhoto == "avant" ? (<td>
                                                            <img src={el.photo} alt="" />
                                                        </td>) : (<td></td>)}
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                        <table style={{ width: '50%' }} className="table align-items-center table-flush">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Photo Apres</th>
                                                </tr>
                                            </thead>
                                            {this.state.arrayRapport.map((el, index) =>
                                                <tbody>
                                                    <tr>
                                                        {el.typePhoto == "apres" ? (<td>
                                                            <img src={el.photo} alt="" />
                                                        </td>) : (<td></td>)}
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ExplRappJour);
