import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';

class ModifMatiere extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idFormation: '',
            formation: {},
            ListFilieres: [],
            selectedFiliere: '',
            ListSpecialites: [],
            selectedSpecialite: '',
            nomFormation: '',
            ListNivBase: [],
            selectedNivBase: ''
        }
    }

    componentDidMount() {
        let idFormation = this.props.location.state.idFormation;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetFormationById.php?id=' + idFormation).then(res => {
            console.log(res)
            this.setState({
                idFormation: res.data.idFormation,
                formation: res.data,
                selectedFiliere: res.data.filiere,
                selectedSpecialite: res.data.specialite,
                nomFormation: res.data.nomFormation,
                selectedNivBase: res.data.nivBase
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php').then(res => {
            console.log(res)
            this.setState({
                ListFilieres: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecialites.php').then(res => {
            console.log(res)
            this.setState({
                ListSpecialites: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                ListNivBase: res.data
            })
        })
    }

    updFiliere = selectedFiliere => {
        this.setState({ selectedFiliere });
        console.log(`Filiere selected:`, selectedFiliere);
    };

    updSpecialite = selectedSpecialite => {
        this.setState({ selectedSpecialite });
        console.log(`Specialite selected:`, selectedSpecialite);
    };

    updNomFormation = (event) => {
        this.setState({
            nomFormation: event.target.value
        });
    }

    updNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`NivBase selected:`, selectedNivBase);
    };

    updNiveau = (event) => {
        let niveau = event.target.value;
        this.setState({
            formation: Object.assign(this.state.formation, { niveau: niveau })
        });
    }

    updDuree = (event) => {
        let duree = event.target.value;
        this.setState({
            formation: Object.assign(this.state.formation, { duree: duree })
        });
    }

    modif = () => {
        let year = new Date().getFullYear();
        console.log(this.state.selectedFiliere)
        console.log(this.state.selectedSpecialite)
        console.log(this.state.nomFormation)
        console.log(this.state.selectedNivBase)
        console.log(this.state.formation)
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdFormationNiv.php?id=' + this.state.idFormation,
            type: 'POST',
            data: {
                filiereId: this.state.selectedFiliere.value,
                specialiteId: this.state.selectedSpecialite.value,
                nomFormation: this.state.nomFormation,
                nivBaseId: this.state.selectedNivBase.value,
                formations: this.state.formations,
                year: year
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/FormationNiv');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification formation</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Filiére</label>
                                            <Select
                                                value={this.state.selectedFiliere}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updFiliere}
                                                options={this.state.ListFilieres}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Spécialité</label>
                                            <Select
                                                value={this.state.selectedSpecialite}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updSpecialite}
                                                options={this.state.ListSpecialites}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom formation</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="text" class="form-control" value={this.state.nomFormation} onChange={this.updNomFormation} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Niveau de Base</label>
                                            <Select
                                                value={this.state.selectedNivBase}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updNivBase}
                                                options={this.state.ListNivBase}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Niveau</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Durée</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td style={{ textAlign: 'center' }}><input type="text" value={this.state.formation.niveau} onChange={(e) => this.addNiveau(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                <td style={{ textAlign: 'center' }}><input type="text" value={this.state.formation.duree} onChange={(e) => this.addDuree(e)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifMatiere);
