import React, { Component } from 'react'
import axios from 'axios'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import ReactApexChart from 'react-apexcharts'

class ExplStatService extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // Series est un Tableau d'objets et non un objet [{}]
            series: [],
            options: {
                chart: {
                    type: 'donut',
                    height: 350,
                    stacked: true,
                    toolbar: {
                        show: true
                    },
                    zoom: {
                        enabled: true
                    }
                },
                labels: [],
                responsive: [{
                    breakpoint: 480,
                    options: {
                        legend: {
                            position: 'bottom',
                            offsetX: -10,
                            offsetY: 0
                        }
                    }
                }],
                legend: {
                    position: 'right',
                    offsetY: 40
                },
            },
        };
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/StatDemandesServices.php').then(res => {
            this.setState({
                options: {
                    ...this.state.options,
                    xaxis: {
                        ...this.state.options.xaxis,
                        labels: res.data[0]
                    }
                },
                series: res.data[1]
            })
        })
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Statistique Service</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                        <div id="chart">
                                            <ReactApexChart options={this.state.options} series={this.state.series} type="donut" />
                                        </div>
                                    </div>
                                </div>
                                <Link to='/StatServices'>
                                    <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '20px' }}>
                                        <button style={{ width: '15%', backgroundColor: '#0083FF', borderColor: '#0083FF', borderRadius: '5px' }} type="button" className="btn btn-success">Retour</button>
                                    </div>
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default ExplStatService;
