import React, { Component } from 'react';
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import './paginate.css'
import { Fragment } from 'react';
import Collapsible from 'react-collapsible';

class StatiquePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicSession: true,
            clicScolarite: false,
            clicReglement: false,
            listpermissions: [],
            permissionGestionRoles: [],
            permissionGestionUtilisateurs: [],
            permissionTypeEnseign: [],
            permissionNivBase: [],
            permissionFilieres: [],
            permissionMatieres: [],
            permissionSpecialites: [],
            permissionNiveaux: [],
            permissionMatFor: [],
            permissionEnseignants: [],
            permissionModalPay: [],
            permissionGroupes: [],
            permissionApprenants: [],
            permissionFraisFormation: [],
            permissionTypePayEleve: [],
            permissionFormations: []
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        if (userPermissions != null) {
            this.setState({ listpermissions: userPermissions })
            var gestionRoles = userPermissions[0]
            this.setState({ permissionGestionRoles: gestionRoles })
            var gestionUtilisateurs = userPermissions[1]
            this.setState({ permissionGestionUtilisateurs: gestionUtilisateurs })
            var typeEnseign = userPermissions[2]
            this.setState({ permissionTypeEnseign: typeEnseign })
            var nivBase = userPermissions[3]
            this.setState({ permissionNivBase: nivBase })
            var filieres = userPermissions[4]
            this.setState({ permissionFilieres: filieres })
            var matieres = userPermissions[5]
            this.setState({ permissionMatieres: matieres })
            var specialites = userPermissions[6]
            this.setState({ permissionSpecialites: specialites })
            var niveaux = userPermissions[7]
            this.setState({ permissionNiveaux: niveaux })
            var matFor = userPermissions[8]
            this.setState({ permissionMatFor: matFor })
            var enseignants = userPermissions[9]
            this.setState({ permissionEnseignants: enseignants })
            var modalPay = userPermissions[10]
            this.setState({ permissionModalPay: modalPay })
            var groupes = userPermissions[12]
            this.setState({ permissionGroupes: groupes })
            var apprenants = userPermissions[14]
            this.setState({ permissionApprenants: apprenants })
            var fraisFormation = userPermissions[15]
            this.setState({ permissionFraisFormation: fraisFormation })
            var papiers = userPermissions[16]
            this.setState({ permissionPapiers: papiers })
            var typePayEleve = userPermissions[17]
            this.setState({ permissionTypePayEleve: typePayEleve })
            var formations = userPermissions[19]
            this.setState({ permissionFormations: formations })
            if (userPermissions[0].idRoleUser != 1) {
                this.setState({
                    clicScolarite: true
                });
            }
        }
        else {
            this.props.history.push('/');
        }
    }

    clicSession = () => {
        this.setState({
            clicSession: true,
            clicScolarite: false,
            clicReglement: false
        });
        this.props.history.push('/Users');
    }

    clicScolarite = () => {
        this.setState({
            clicScolarite: true,
            clicSession: false,
            clicReglement: false
        });
        this.props.history.push('/TypeEnseign');
    }

    clicReglement = () => {
        this.setState({
            clicReglement: true,
            clicScolarite: false,
            clicSession: false
        });
        this.props.history.push('/ReglApprenant');
    }

    clearStorage = () => {
        localStorage.clear();
        this.props.history.push('/');
    }

    render() {
        return (
            <div id="wrapper">
                {/* Navigation */}
                <nav className="navbar navbar-default navbar-static-top" style={{ marginBottom: 0 }}>
                    <div className="navbar-header"> <a className="navbar-toggle hidden-sm hidden-md hidden-lg " href="javascript:void(0)" data-toggle="collapse" data-target=".navbar-collapse"><i className="ti-menu" /></a>
                        <div className="top-left-part"><a className="logo"><img style={{ backgroundColor: 'white' }} className="img-circle" width='30px' height='30px' src="images/art_metier.png" alt="Art_Metier" />&nbsp;<span style={{ fontSize: '13px' }} className="hidden-xs">Arts et Mtiers</span></a></div>
                        <ul className="nav navbar-top-links navbar-left hidden-xs">
                            {(this.state.permissionGestionRoles.ajouter == 1 || this.state.permissionGestionRoles.explorer == 1 || this.state.permissionGestionRoles.modifier == 1 || this.state.permissionGestionRoles.supprimer == 1) ?
                                (<li onClick={this.clicSession} className='hvr-back-pulse'><a><i style={{ marginRight: '5px' }} class="fas fa-shoe-prints"></i>Habilitation</a></li>) : (null)}
                            <li onClick={this.clicScolarite} className='hvr-back-pulse'><a><i style={{ marginRight: '5px' }} class="fas fa-clipboard"></i>Scolarit</a></li>
                            <li onClick={this.clicReglement} className='hvr-back-pulse'><a><i style={{ marginRight: '5px' }} class="fas fa-wallet"></i>Reglement</a></li>
                        </ul>
                        <ul className="nav navbar-top-links navbar-right pull-right">
                            <li>
                                <div onClick={this.clearStorage} className='hvr-shrink'><span style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className="profile-pic"> <img src="images/users/logout.png" alt="user-img" width={25} className="img-circle" /><b className="hidden-xs">Dconnexion</b> </span></div>
                            </li>
                        </ul>
                    </div>
                    {/* /.navbar-header */}
                    {/* /.navbar-top-links */}
                    {/* /.navbar-static-side */}
                </nav>
                <div className="navbar-default sidebar nicescroll" role="navigation">
                    <div className="sidebar-nav navbar-collapse">
                        <ul className="nav in" id="side-menu">
                            <li className="sidebar-search hidden-sm hidden-md hidden-lg">
                                <div className="input-group custom-search-form">
                                    <input type="text" className="form-control" placeholder="Search..." />
                                    <span className="input-group-btn">
                                        <button className="btn btn-default" type="button"><i className="ti-search" /> </button>
                                    </span>
                                </div>
                            </li>

                            {this.state.clicReglement == true ? (
                                <Fragment>
                                    <li>
                                        <Link to="/ReglApprenant" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-child"></i> Reglements eleves</Link>
                                    </li>
                                </Fragment>
                            ) : (null)}

                            {this.state.clicScolarite == true ?
                                (
                                    <Fragment>
                                        <Collapsible triggerWhenOpen={<div style={{ display: 'flex', alignItems: 'center' }}><li className="waves-effect" style={{ padding: '15px 30px 15px 15px', color: 'white' }}><i style={{ marginRight: '4px', color: 'white' }} class="fas fa-people-arrows"></i>Gestion apprenants</li><i style={{ color: 'white' }} class="fas fa-chevron-down"></i></div>} trigger={<div style={{ display: 'flex', alignItems: 'center' }}><li className="waves-effect" style={{ padding: '15px 30px 15px 15px', color: 'rgba(255,255,255,.4)' }}><i style={{ marginRight: '4px' }} class="fas fa-people-arrows"></i>Gestion apprenants</li><i class="fas fa-chevron-left"></i></div>}>
                                            {(this.state.permissionApprenants.ajouter == 1 || this.state.permissionApprenants.explorer == 1 || this.state.permissionApprenants.modifier == 1 || this.state.permissionApprenants.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Apprenants" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-child"></i> Apprenants</Link>
                                                </li>) : (null)}
                                        </Collapsible>
                                        <Collapsible triggerWhenOpen={<div style={{ display: 'flex', alignItems: 'center' }}><li className="waves-effect" style={{ padding: '15px 30px 15px 15px', color: 'white' }}><i style={{ marginRight: '4px', color: 'white' }} class="fas fa-user-graduate"></i>Gestion enseignants</li><i style={{ color: 'white' }} class="fas fa-chevron-down"></i></div>} trigger={<div style={{ display: 'flex', alignItems: 'center' }}><li className="waves-effect" style={{ padding: '15px 30px 15px 15px', color: 'rgba(255,255,255,.4)' }}><i style={{ marginRight: '4px' }} class="fas fa-user-graduate"></i>Gestion enseignants</li><i class="fas fa-chevron-left"></i></div>}>
                                            {(this.state.permissionEnseignants.ajouter == 1 || this.state.permissionEnseignants.explorer == 1 || this.state.permissionEnseignants.modifier == 1 || this.state.permissionEnseignants.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Enseignants" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-chalkboard-teacher"></i> Enseignants</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionModalPay.ajouter == 1 || this.state.permissionModalPay.explorer == 1 || this.state.permissionModalPay.modifier == 1 || this.state.permissionModalPay.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/ModPaiEnseignants" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-hand-holding-usd"></i> Paiement enseignants</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionGroupes.ajouter == 1 || this.state.permissionGroupes.explorer == 1 || this.state.permissionGroupes.modifier == 1 || this.state.permissionGroupes.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Groupes" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-users"></i> Groupes</Link>
                                                </li>) : (null)}
                                        </Collapsible>
                                        <Collapsible triggerWhenOpen={<div style={{ display: 'flex', alignItems: 'center' }}><li className="waves-effect" style={{ padding: '15px 30px 15px 15px', color: 'white' }}><i style={{ marginRight: '4px', color: 'white' }} class="fas fa-book"></i>Gestion formations</li><i style={{ color: 'white', marginLeft: '6px' }} class="fas fa-chevron-down"></i></div>} trigger={<div style={{ display: 'flex', alignItems: 'center' }}><li className="waves-effect" style={{ padding: '15px 30px 15px 15px', color: 'rgba(255,255,255,.4)' }}><i style={{ marginRight: '4px' }} class="fas fa-book"></i>Gestion formations</li><i style={{ marginLeft: '6px' }} class="fas fa-chevron-left"></i></div>}>
                                            <li>
                                                <Link to="/ArchiFormation" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-align-center"></i> Architcture </Link>
                                            </li>
                                            {(this.state.permissionMatFor.ajouter == 1 || this.state.permissionMatFor.explorer == 1 || this.state.permissionMatFor.modifier == 1 || this.state.permissionMatFor.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/MatieresFormat" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-graduation-cap"></i> Matieres par formation</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionTypePayEleve.ajouter == 1 || this.state.permissionTypePayEleve.explorer == 1 || this.state.permissionTypePayEleve.modifier == 1 || this.state.permissionTypePayEleve.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/TypesPaiementEleve" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-money-bill-wave"></i> Types paiements eleves </Link>
                                                </li>) : (null)}
                                            {(this.state.permissionPapiers.ajouter == 1 || this.state.permissionPapiers.explorer == 1 || this.state.permissionPapiers.modifier == 1 || this.state.permissionPapiers.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Papiers" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-paste"></i> Papiers Apportes </Link>
                                                </li>) : (null)}
                                            {(this.state.permissionFraisFormation.ajouter == 1 || this.state.permissionFraisFormation.explorer == 1 || this.state.permissionFraisFormation.modifier == 1 || this.state.permissionFraisFormation.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/FraisFormation" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-money-check-alt"></i> Frais formation </Link>
                                                </li>) : (null)}
                                            {(this.state.permissionNiveaux.ajouter == 1 || this.state.permissionNiveaux.explorer == 1 || this.state.permissionNiveaux.modifier == 1 || this.state.permissionNiveaux.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Niveaux" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-list-ol"></i> Niveaux</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionFormations.ajouter == 1 || this.state.permissionFormations.explorer == 1 || this.state.permissionFormations.modifier == 1 || this.state.permissionFormations.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Formations" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-scroll"></i> Formations</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionSpecialites.ajouter == 1 || this.state.permissionSpecialites.explorer == 1 || this.state.permissionSpecialites.modifier == 1 || this.state.permissionSpecialites.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Specialites" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-chalkboard"></i> Specialits</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionMatieres.ajouter == 1 || this.state.permissionMatieres.explorer == 1 || this.state.permissionMatieres.modifier == 1 || this.state.permissionMatieres.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Matieres" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-book-open"></i> Matieres</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionFilieres.ajouter == 1 || this.state.permissionFilieres.explorer == 1 || this.state.permissionFilieres.modifier == 1 || this.state.permissionFilieres.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/Filieres" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-globe-africa"></i> Filires</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionNivBase.ajouter == 1 || this.state.permissionNivBase.explorer == 1 || this.state.permissionNivBase.modifier == 1 || this.state.permissionNivBase.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/NivBase" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-book"></i> Niveau de base</Link>
                                                </li>) : (null)}
                                            {(this.state.permissionTypeEnseign.ajouter == 1 || this.state.permissionTypeEnseign.explorer == 1 || this.state.permissionTypeEnseign.modifier == 1 || this.state.permissionTypeEnseign.supprimer == 1) ?
                                                (<li>
                                                    <Link to="/TypeEnseign" className="waves-effect" ><i style={{ paddingLeft: '20px', paddingBottom: '10px' }} class="fas fa-edit"></i> Type enseignement</Link>
                                                </li>) : (null)}
                                        </Collapsible>
                                    </Fragment>) : (null)}

                            {this.state.clicSession == true ?
                                (
                                    <Fragment>
                                        {(this.state.permissionGestionRoles.ajouter == 1 || this.state.permissionGestionRoles.explorer == 1 || this.state.permissionGestionRoles.modifier == 1 || this.state.permissionGestionRoles.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Roles" className="waves-effect" ><i class="fas fa-user-edit"></i> Gestion de role</Link>
                                            </li>) : (null)}
                                        {(this.state.permissionGestionUtilisateurs.ajouter == 1 || this.state.permissionGestionUtilisateurs.explorer == 1 || this.state.permissionGestionUtilisateurs.modifier == 1 || this.state.permissionGestionUtilisateurs.supprimer == 1) ?
                                            (<li>
                                                <Link to="/Users" className="waves-effect" ><i class="fas fa-users"></i> Gestion d'utilisateur</Link>
                                            </li>) : (null)}
                                    </Fragment>) : (null)}

                        </ul>
                    </div>
                    {/* /.sidebar-collapse */}
                </div>
                {this.props.children}
            </div>
        );
    }
}

export default withRouter(StatiquePage);
