import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class AjoutInventaire extends Component {
    constructor(props) {
        super(props);
        this.state = {
            numeroInventaire: '',
            dateInventaire: new Date(),
            emplacements: [],
            selectedEmplacement: '',
            detailInventaire: [{ article: '', quantite: '', prix: '' }],
            modal: false,
            idDetailInventaireASupprimer: '',
            articles: []
        }
        this.addnumeroInventaire = this.addnumeroInventaire.bind(this);
        this.ajout = this.ajout.bind(this)
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/EmplacementsSelect.php').then(res => {
            console.log(res);
            this.setState({ emplacements: res.data });
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/ArticlesSelect.php').then(res => {
            console.log(res)
            this.setState({
                articles: res.data
            })
        })
    }

    addnumeroInventaire(event) {
        this.setState({
            numeroInventaire: event.target.value
        });
    }

    addDateInventaire = d => {
        this.setState({
            dateInventaire: d
        });
    };

    addEmplacement = selectedEmplacement => {
        this.setState({ selectedEmplacement });
        console.log(`Emplacement selected:`, selectedEmplacement);
    };

    additionDetailInventaire = () => {
        let element = { article: '', quantite: '', prix: '' }
        this.setState({
            detailInventaire: [...this.state.detailInventaire, element]
        })
    }

    toggleDetailInventaire = (idDetailInventaireASupprimer) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            idDetailInventaireASupprimer: idDetailInventaireASupprimer
        }));
    }

    deleteDetailInventaire = () => {
        this.state.detailInventaire.splice(this.state.idDetailInventaireASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    addArticle = (selectedArticle, idDetailInventaire) => {
        let article = selectedArticle;
        this.setState({
            detailInventaire: this.state.detailInventaire.map((el, id) => (id === idDetailInventaire ? Object.assign(el, { article: article }) : el))
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_agora/PrixArticle.php?id=' + article).then(res => {
            var prix = res.data;
            this.setState({
                detailInventaire: this.state.detailInventaire.map((el, id) => (id === idDetailInventaire ? Object.assign(el, { prix: prix }) : el))
            });
        });
    }

    addQuantite = (quantite, idDetailInventaire) => {
        let qte = quantite;
        this.setState({
            detailInventaire: this.state.detailInventaire.map((el, id) => (id === idDetailInventaire ? Object.assign(el, { quantite: qte }) : el))
        });
    }

    ajout() {

        let dateInventaire = this.state.dateInventaire
        let month_one = '' + (dateInventaire.getMonth() + 1);
        let day_one = '' + dateInventaire.getDate();
        let year_one = dateInventaire.getFullYear();
        if (month_one.length < 2)
            month_one = '0' + month_one;
        if (day_one.length < 2)
            day_one = '0' + day_one;
        let convertDateInventaire = [year_one, month_one, day_one].join('-');
        console.log(convertDateInventaire)

        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_agora/AddInventaire.php',
            type: 'POST',
            data: {
                numeroInventaire: this.state.numeroInventaire,
                dateInventaire: convertDateInventaire,
                idEmplacement: this.state.selectedEmplacement.value
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Inventaires');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout inventaire</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Numero</label>
                                            <input type="number" class="form-control" placeholder="Numero" onChange={this.addnumeroInventaire.bind(this)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateInventaire}
                                                onChange={this.addDateInventaire}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Emplacement</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addEmplacement}
                                                options={this.state.emplacements}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionDetailInventaire} color="secondary">Ajouter ligne</Button>
                                    </div>
                                    <table style={{ width: '50%', marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Article</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Quantité</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Supprimer</th>
                                            </tr>
                                        </thead>
                                        {this.state.detailInventaire.map((el, id) =>
                                            <tbody>
                                                <tr>
                                                    <td><Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.addArticle(e, id)}
                                                        options={this.state.articles}
                                                    />  </td>
                                                    <td><input type="number" placeholder="Quantité" onChange={(e) => this.addQuantite(e, id)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td><input type="number" disabled placeholder="Prix" value={el.prix} style={{ textAlign: 'center', height: '38px', borderRadius: '5px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '50%' }} className='buttonSupprimer' onClick={() => this.toggleDetailInventaire(id)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.modal} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.modal}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>Êtes-Vous sûr de vouloir supprimer ce detail ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteDetailInventaire()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout.bind(this)}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutInventaire);
