import React, { Component } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Collapsible from 'react-collapsible';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class RapportJournalier extends Component {
    constructor(props) {
        super(props);
        this.state = {
            encaissFormat: [],
            decaissFormat: [],
            espFormat: '',
            checFormat: '',
            virFormat: '',
            totalFormat: '',
            sommeEncaissFormat: '',
            sommeDecaissFormat: '',
            encaissCycle: [],
            decaissCycle: [],
            espCycle: '',
            checCycle: '',
            virCycle: '',
            totalCycle: '',
            sommeEncaissCycle: '',
            sommeDecaissCycle: '',
            total: '',
            dateDebut: '',
            dateFin: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/RapportJour.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        encaissFormat: code_html[0],
                        decaissFormat: code_html[1],
                        espFormat: code_html[2][0].espFormat,
                        checFormat: code_html[2][0].checFormat,
                        virFormat: code_html[2][0].virFormat,
                        totalFormat: code_html[2][1].totalResteFormat,
                        encaissCycle: code_html[3],
                        decaissCycle: code_html[4],
                        espCycle: code_html[5][0].espCycle,
                        checCycle: code_html[5][0].checCycle,
                        virCycle: code_html[5][0].virCycle,
                        totalCycle: code_html[5][1].totalResteCycle
                    }, () => {
                        // formation
                        var sommeEncaissFormat = 0
                        for (var i = 0; i < this.state.encaissFormat.length; i++) {
                            sommeEncaissFormat += parseInt(this.state.encaissFormat[i].montant)
                        }
                        this.setState({
                            sommeEncaissFormat: sommeEncaissFormat
                        });
                        var sommeDecaissFormat = 0
                        for (var i = 0; i < this.state.decaissFormat.length; i++) {
                            sommeDecaissFormat += parseInt(this.state.decaissFormat[i].montant)
                        }
                        this.setState({
                            sommeDecaissFormat: sommeDecaissFormat
                        });
                        // cycle
                        var sommeEncaissCycle = 0
                        for (var i = 0; i < this.state.encaissCycle.length; i++) {
                            sommeEncaissCycle += parseInt(this.state.encaissCycle[i].montant)
                        }
                        this.setState({
                            sommeEncaissCycle: sommeEncaissCycle
                        });
                        var sommeDecaissCycle = 0
                        for (var i = 0; i < this.state.decaissCycle.length; i++) {
                            sommeDecaissCycle += parseInt(this.state.decaissCycle[i].montant)
                        }
                        this.setState({
                            sommeDecaissCycle: sommeDecaissCycle
                        });
                        // Total formation + cycle 
                        var total = parseInt(this.state.totalFormat) + parseInt(this.state.totalCycle)
                        this.setState({
                            total: total
                        });
                    });
                }
            });
        }
        else {
            document.location = '/'
        }
    }

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        });
    };

    recherche = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        // convertir date debut
        if (this.state.dateDebut != '') {
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
        }
        else {
            var convertDateDebut = '';
        }
        // convertir date fin
        if (this.state.dateFin != '') {
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
        }
        else {
            var convertDateFin = '';
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SearchRapportJour.php',
            type: 'POST',
            data: {
                dateDebut: convertDateDebut,
                dateFin: convertDateFin,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                console.log(code_html)
                this.setState({
                    encaissFormat: code_html[0],
                    decaissFormat: code_html[1],
                    espFormat: code_html[2][0].espFormat,
                    checFormat: code_html[2][0].checFormat,
                    virFormat: code_html[2][0].virFormat,
                    totalFormat: code_html[2][1].totalResteFormat,
                    encaissCycle: code_html[3],
                    decaissCycle: code_html[4],
                    espCycle: code_html[5][0].espCycle,
                    checCycle: code_html[5][0].checCycle,
                    virCycle: code_html[5][0].virCycle,
                    totalCycle: code_html[5][1].totalResteCycle
                }, () => {
                    // formation
                    var sommeEncaissFormat = 0
                    for (var i = 0; i < this.state.encaissFormat.length; i++) {
                        sommeEncaissFormat += parseInt(this.state.encaissFormat[i].montant)
                    }
                    this.setState({
                        sommeEncaissFormat: sommeEncaissFormat
                    });
                    var sommeDecaissFormat = 0
                    for (var i = 0; i < this.state.decaissFormat.length; i++) {
                        sommeDecaissFormat += parseInt(this.state.decaissFormat[i].montant)
                    }
                    this.setState({
                        sommeDecaissFormat: sommeDecaissFormat
                    });
                    // cycle
                    var sommeEncaissCycle = 0
                    for (var i = 0; i < this.state.encaissCycle.length; i++) {
                        sommeEncaissCycle += parseInt(this.state.encaissCycle[i].montant)
                    }
                    this.setState({
                        sommeEncaissCycle: sommeEncaissCycle
                    });
                    var sommeDecaissCycle = 0
                    for (var i = 0; i < this.state.decaissCycle.length; i++) {
                        sommeDecaissCycle += parseInt(this.state.decaissCycle[i].montant)
                    }
                    this.setState({
                        sommeDecaissCycle: sommeDecaissCycle
                    });
                    // Total formation + cycle 
                    var total = parseInt(this.state.totalFormat) + parseInt(this.state.totalCycle)
                    this.setState({
                        total: total
                    });
                });
            }
        });
    }

    imprFor = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        // convertir date debut
        if (this.state.dateDebut != '') {
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
        }
        else {
            var convertDateDebut = '';
        }
        // convertir date fin
        if (this.state.dateFin != '') {
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
        }
        else {
            var convertDateFin = '';
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImprRapportFor.php',
            type: 'POST',
            data: {
                dateDebut: convertDateDebut,
                dateFin: convertDateFin,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    imprCycle = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        // convertir date debut
        if (this.state.dateDebut != '') {
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
        }
        else {
            var convertDateDebut = '';
        }
        // convertir date fin
        if (this.state.dateFin != '') {
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
        }
        else {
            var convertDateFin = '';
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImprRapportCycle.php',
            type: 'POST',
            data: {
                dateDebut: convertDateDebut,
                dateFin: convertDateFin,
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: function (code_html, statut) {
                var server_path = code_html.server_path;
                var file_path = code_html.file_path
                var path = server_path + file_path;
                window.open(path, "_blank")
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Rapport journalier</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDebut}
                                                onChange={this.addDateDebut}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateFin}
                                                onChange={this.addDateFin}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                                    <div>
