import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { RadioGroup, Radio } from 'react-radio-group'

class Cycles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Cycle',
                    selector: 'cycle',
                    sortable: false,
                    center: true,
                    width: '15%'
                },
                {
                    name: 'Heure',
                    selector: 'heure',
                    sortable: false,
                    center: true,
                    width: '%15'
                },
                {
                    name: 'Tarif',
                    selector: 'tarif',
                    sortable: false,
                    center: true,
                    width: '10%'
                },
                {
                    name: 'Tarif avec carte',
                    selector: 'carte',
                    sortable: false,
                    center: true,
                    width: '10%'
                },
                {
                    name: 'Session',
                    cell: row => <div onClick={() => this.dateCycle(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="far fa-calendar-alt"></i></div><div style={{ marginLeft: '5px' }}><p>Date</p></div></div>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Enseignant',
                    cell: row => <div onClick={() => this.infoEnseign(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#40E0D0' }} className="fas fa-chalkboard-teacher"></i></div><div style={{ marginLeft: '5px' }}><p>Information</p></div></div>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Confirm',
                    cell: row => <button style={{ backgroundColor: '#FF69B4', borderRadius: '5px' }} onClick={() => this.confirm(row.idCycle)}><i style={{ color: 'white' }} className="fas fa-check-circle"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Modifier',
                    cell: row => <Link to={{
                        pathname: '/ModifCycle',
                        state: { idCycle: row.idCycle }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>,
                    // <button onClick={() => this.modifCycle(row)} className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Desactiver',
                    cell: row => <button className='buttonSupprimer' onClick={() => this.toggletwo(row.idCycle)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            pending: true,
            permissionGestionRoles: '',
            detailCycle: [],
            infoEnseign: [],
            listChoix: ['Oui', 'Non'],
            selectedChoix: 'Non',
            idCycleConfirm: '',
            modalSession: false,
            modalEnseign:false,
            modalConfirm:false
        }
        this.newElement = this.newElement.bind(this);
        this.filterSearch = this.filterSearch.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    newElement(page, totalRows) {
        this.setState({
            pending: true
        },
            function () {
                console.log(page)
                console.log(totalRows)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: page
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }.bind(this)
        )
    }

    perPage(currentRowsPerPage, currentPage) {
        this.setState(
            {
                nbrEltperPage: currentRowsPerPage,
                pending: true
            }
            , function () {
                console.log(currentRowsPerPage)
                console.log(currentPage)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: currentRowsPerPage,
                        page: currentPage
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                });
            }
        );
    }

    filterSearch(event) {
        this.setState({
            filterElement: event.target.value,
            resetFirstPage: !this.state.resetFirstPage,
            pending: true
        },
            function () {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
                    type: 'POST',
                    data: {
                        query: this.state.filterElement,
                        limit: this.state.nbrEltperPage,
                        page: 1
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            nombreTotalElement: code_html.total,
                            data: code_html.data,
                            pending: false
                        });
                    }
                })
            }
        );
    }

    dateCycle = (row) => {
        this.setState({
            detailCycle: row.detailCycle,
        }, () => {
            console.log(this.state.detailCycle)
            this.setState(prevState => ({
                modalSession: !prevState.modalSession,
            }));
        });
    }

    infoEnseign = (row) => {
        this.setState({
            infoEnseign: row.infoEnseignant,
        }, () => {
            console.log(this.state.infoEnseign)
            this.setState(prevState => ({
                modalEnseign: !prevState.modalEnseign,
            }));
        });
    }

    confirm = (idCycle) => {
        this.setState({
            idCycleConfirm: idCycle,
        }, () => {
            this.setState(prevState => ({
                modalConfirm: !prevState.modalConfirm,
            }));
        });
    }

    addChoix = (selectedChoix) => {
        this.setState({ selectedChoix: selectedChoix }, () => {
            // if (this.state.selectedChoix === "Oui") {
            //     <Link to={{
            //         pathname: '/ConfirmationCycle',
            //         state: { idCycle: this.state.idCycleConfirm }
            //     }}></Link>
            // }
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modalSession: !prevState.modalSession,
            modalEnseign: !prevState.modalEnseign,
            modalConfirm: !prevState.modalConfirm
        }));
    }

    // deleteCycle() {
    //     var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
    //     var userId = userPermissions[0].idUser
    //     jQuery.ajax({
    //         url: process.env.REACT_APP_API_URL + "Back_mediatraining/DeleteCycle.php?id=" + this.state.idCycle + "&userId=" + userId,
    //         type: 'POST',
    //         dataType: 'json',
    //         success: (code_html, statut) => {
    //             this.setState({
    //                 modal: false
    //             }, () => {
    //                 jQuery.ajax({
    //                     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Cycles.php',
    //                     type: 'POST',
    //                     data: {
    //                         query: '',
    //                         limit: this.state.nbrEltperPage,
    //                         page: 1
    //                     },
    //                     dataType: 'json',
    //                     success: (code_html, statut) => {
    //                         console.log(code_html)
    //                         this.setState({
    //                             nombreTotalElement: code_html.total,
    //                             data: code_html.data
    //                         });
    //                     }
    //                 });
    //             })
    //         }
    //     });
    // }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Cycles</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/AjoutCycle" > <button style={{ background: '#3A3F51', border: '1px solid #3A3F51', borderRadius: '5px' }} type="button" className="btn btn-info"> <i className="fas fa-plus"></i>Cycle</button></Link>
                                </div>
                                <div className="mb-0 form-group">
                                    <div className="input-group-alternative input-group">
                                        <div className="input-group-prepend">
                                            <span className="input-group-text">
                                                <i className="fas fa-search"></i>
                                            </span>
                                            <input onChange={(currentRowsPerPage, currentPage) => this.filterSearch(currentRowsPerPage, currentPage)} placeholder="Search" type="text" className="search" />
                                        </div>
                                    </div>
                                </div>
                                <DataTable
                                    columns={this.state.columns}
                                    data={this.state.data}
                                    highlightOnHover={true}
                                    pagination={true}
                                    paginationServer={true}
                                    onChangePage={(page, totalRows) => this.newElement(page, totalRows)}
                                    paginationTotalRows={this.state.nombreTotalElement}
                                    paginationPerPage={this.state.nbrEltperPage}
                                    paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                                    onChangeRowsPerPage={(currentRowsPerPage, currentPage) => this.perPage(currentRowsPerPage, currentPage)}
                                    paginationResetDefaultPage={this.state.resetFirstPage}
                                    paginationComponentOptions={{ rowsPerPageText: 'Elements par page:', rangeSeparatorText: 'de', noRowsPerPage: false }}
                                    progressPending={this.state.pending}
                                />
                            </div>
                        </div>
                    </div>
                </div>

                {/* <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                    <ModalBody>
                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                <p>Êtes-Vous sûr de vouloir supprimer ce cycle ?</p>
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.deleteCycle()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal> */}

                <Modal isOpen={this.state.modalSession} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F' }} className="far fa-calendar-alt"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Date Cycle</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th style={{ textAlign: 'center' }} scope="col">Session</th>
                                        <th style={{ textAlign: 'center' }} scope="col">Horraire</th>
                                        <th style={{ textAlign: 'center' }} scope="col">Date debut</th>
                                        <th style={{ textAlign: 'center' }} scope="col">Date fin</th>
                                    </tr>
                                </thead>
                                {this.state.detailCycle.map(el =>
                                    <tbody>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}>{el.mois}</td>
                                            <td style={{ textAlign: 'center' }}>{el.horaire}</td>
                                            <td style={{ textAlign: 'center' }}>{el.dateD}</td>
                                            <td style={{ textAlign: 'center' }}>{el.dateF}</td>
                                        </tr>
                                    </tbody>)}
                            </table>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} className="fas fa-sign-out-alt"></i></button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>

                {/* <Modal isOpen={this.state.modalEnseign} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#40E0D0' }} className="fas fa-chalkboard-teacher"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Information</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Enseignant</th>
                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Nombre d'heure</th>
                                        <th className="col-md-2" style={{ textAlign: 'center' }} scope="col">Honoraire</th>
                                    </tr>
                                </thead>
                                {this.state.infoEnseignant.map(el =>
                                    <tbody>
                                        <tr>
                                            <td style={{ textAlign: 'center' }}>{el.nbrHeures}</td>
                                            <td style={{ textAlign: 'center' }}>{el.honoraireEnseignant}</td>
                                            <td style={{ textAlign: 'center' }}>{el.nomEnseignant}</td>
                                        </tr>
                                    </tbody>)}
                            </table>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} className="fas fa-sign-out-alt"></i></button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal> */}

                <Modal isOpen={this.state.modalConfirm} toggle={this.toggle} className={this.props.className} fade={false}>
                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#FF69B4' }} className="fas fa-check-circle"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Confirmation Cycle</p></div></div></ModalHeader>
                    <ModalBody>
                        <div>
                            <h4>Voulez-vous confirmer ce cycle ?</h4>
                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                {this.state.listChoix.map(elm =>
                                    <RadioGroup
                                        name="type"
                                        selectedValue={this.state.selectedChoix}
                                        onChange={(e) => this.addChoix(e)}>
                                        <span style={{ paddingRight: '10px' }}>
                                            <label>
                                                <Radio value={elm} />{elm}
                                            </label>
                                        </span>
                                    </RadioGroup>
                                )}
                            </div>
                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                <div><button onClick={() => this.toggle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white', width: '40px ' }}><i style={{ color: 'white' }} className="fas fa-sign-out-alt"></i></button></div>
                            </div>
                        </div>
                    </ModalBody>
                </Modal>

            </div>
        );
    }
}

export default Cycles;
