import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

const groupStyles = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
};
const groupBadgeStyles = {
    backgroundColor: '#EBECF0',
    borderRadius: '2em',
    color: '#172B4D',
    display: 'inline-block',
    fontSize: 12,
    fontWeight: 'normal',
    lineHeight: '1',
    minWidth: 1,
    padding: '0.16666666666667em 0.5em',
    textAlign: 'center',
};

const formatGroupLabel = data => (
    <div style={groupStyles}>
        <span>{data.label}</span>
        <span style={groupBadgeStyles}>{data.options.length}</span>
    </div>
);

class InscripCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listTypeApp: [{ value: 1, label: 'Eléve deja inscrit a une formation' }, { value: 2, label: 'Nouveau éleve' }],
            selectedTypeApp: 2,
            listApprenant: [],
            selectedApprenant: '',
            listConnaissance: [],
            numInscripCycle: '',
            numCarteAdh: '',
            listTypeFormat: [{ value: 1, label: 'Societé' }, { value: 2, label: 'Particulier' }],
            selectedTypeFiche: 2,
            nbrPersonne: 0,
            matFisc: '',
            nbrPersTenu: 0,
            societe: '',
            responsable: '',
            mobileResp: '',
            age: '',
            lieuResid: [],
            seletedLieuResid: '',
            listNivBase: [],
            selectedNivBase: '',
            facebook: '',
            fonction: '',
            selectedTypeFormat: 2,
            motivation: '',
            numIdentite: '',
            prenom: '',
            nom: '',
            disabled: false,
            connaissChoisi: [],
            listCycle: [],
            selectedCycle: [],
            email: '',
            tel: '',
            fax: '',
            photoVerso: null,
            nbrHeure: 0,
            blockReglement: false,
            carteFid: '',
            tenu: '',
            propCycle: '',
            reglInsCycle: [],
            societeCoch: false,
            tva: '',
            numCarteFid: '',
            mntCarte: '',
            mntTenu: '',
            nomCycle: '',
            dateDCycle: '',
            dateFCycle: '',
            horaire: '',
            mntTotalPayer: 0,
            listModPay: [],
            tabModPayFormat: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            tabModPayCycle: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            indexLignASupprimer: '',
            modalFormat: false,
            modalCycle: false,
            datePaiement: new Date(),
            mntCycleSansTva: '',
            selectedPayCycle: 2,
            arrayRegl: [],
            reglEleve: [],
            mntFraisTotalPayer: 0,
            mntIncripTotalPayer: 0,
            selectedBolPayFormat: 2,
            inforEleve: '',
            fraisReglEleve: [],
            modPayform: ''
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumInscripCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numInscripCycle: code_html
                })
            }
        });
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/NumAdheCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    numCarteAdh: code_html,
                    carteFid: code_html
                })
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectResid.php').then(res => {
            this.setState({
                lieuResid: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                listNivBase: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectConnaissance.php').then(res => {
            console.log(res)
            this.setState({
                listConnaissance: res.data
            })
        })
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectDetailCycle.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listCycle: code_html
                })
            }
        });
    }

    addTypeApp = (selectedTypeApp) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (selectedTypeApp) {
            this.setState({ selectedTypeApp: selectedTypeApp }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            listApprenant: code_html
                        })
                    }
                });
            });
        }
    }

    addApprenant = (selectedApprenant) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedApprenant: selectedApprenant }, () => {
            if (selectedApprenant != null) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetInforApprenant.php?id=' + selectedApprenant.value,
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            numIdentite: code_html.numIdentite,
                            nom: code_html.nom,
                            prenom: code_html.prenom,
                            age: code_html.age,
                            selectedNivBase: code_html.niveau,
                            email: code_html.email,
                            tel: code_html.tel
                        })
                    }
                })
            };
        })
    }

    addType = (selectedTypeFiche) => {
        this.setState({ selectedTypeFiche: selectedTypeFiche });
    }

    addCarteFid = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                carteFid: ''
            });
        }
        else {
            var numCarteFid = this.state.numCarteAdh
            this.setState({
                carteFid: numCarteFid
            });
        }
    }

    addTenu = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            this.setState({
                tenu: true
            });
        }
        else {
            this.setState({
                tenu: false
            });
        }
    }

    addNbrPersonne = (event) => {
        this.setState({
            nbrPersonne: event.target.value
        });
    }

    addMatFisc = (event) => {
        this.setState({
            matFisc: event.target.value
        });
    }

    addNbrPersTenu = (event) => {
        this.setState({
            nbrPersTenu: event.target.value
        });
    }

    addSociete = (event) => {
        this.setState({
            societe: event.target.value
        });
    }

    addResponsable = (event) => {
        this.setState({
            responsable: event.target.value
        });
    }

    addMobile = (event) => {
        this.setState({
            mobileResp: event.target.value
        });
    }

    addAge = (event) => {
        this.setState({
            age: event.target.value
        });
    }

    addLieuResid = (seletedLieuResid) => {
        this.setState({ seletedLieuResid: seletedLieuResid });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
    };

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addFax = (event) => {
        this.setState({
            fax: event.target.value
        });
    }

    addFacebook = (event) => {
        this.setState({
            facebook: event.target.value
        });
    }

    addFct = (event) => {
        this.setState({
            fonction: event.target.value
        });
    }

    addPhotoVerso(e) {
        this.setState({ photoVerso: e.target.files })
    }

    addTypeFormat = (selectedTypeFormat) => {
        this.setState({ selectedTypeFormat: selectedTypeFormat });
    }

    addMotivation = (event) => {
        this.setState({
            motivation: event.target.value
        });
    }

    addNumIdentite = (event) => {
        this.setState({
            numIdentite: event.target.value
        });
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addConnaiss = (e, el) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.value
            this.setState({
                connaissChoisi: [...this.state.connaissChoisi, element]
            });
        }
        else {
            let element = el.value
            let indice = this.state.connaissChoisi.indexOf(element)
            this.state.connaissChoisi.splice(indice, 1)
        }
    }

    addCycle = selectedCycle => {
        this.setState({ selectedCycle });
    };

    addNbrHeure = (event) => {
        this.setState({
            nbrHeure: event.target.value
        });
    }

    addPropCycle = (event) => {
        this.setState({
            propCycle: event.target.value
        });
    }

    addMntCartePay = (event) => {
        var mntCartePay = event.target.value;
        if (parseInt(mntCartePay) <= parseInt(this.state.reglInsCycle.carte)) {
            this.setState({
                reglInsCycle: Object.assign(this.state.reglInsCycle, { mntCartePay: mntCartePay })
            }, () => {
                var sommeTotPayer = parseInt(this.state.reglInsCycle.mntCartePay) + parseInt(this.state.reglInsCycle.mntTenuPay) + parseInt(this.state.reglInsCycle.mntCyclePay)
                this.setState({
                    mntTotalPayer: sommeTotPayer
                });
            });
        }
        else {
            toast.error('⛔ Le montant saisie est supérieur !!', { containerId: 'A' });
        }
    }

    addMntTenuPay = (event) => {
        var mntTenuPay = event.target.value;
        if (parseInt(mntTenuPay) <= parseInt(this.state.reglInsCycle.tenu)) {
            this.setState({
                reglInsCycle: Object.assign(this.state.reglInsCycle, { mntTenuPay: mntTenuPay })
            }, () => {
                var sommeTotPayer = parseInt(this.state.reglInsCycle.mntCartePay) + parseInt(this.state.reglInsCycle.mntTenuPay) + parseInt(this.state.reglInsCycle.mntCyclePay)
                this.setState({
                    mntTotalPayer: sommeTotPayer
                });
            });
        } else {
            toast.error('⛔ Le montant saisie est supérieur !!', { containerId: 'A' });
        }
    }

    addMntCyclePay = (event) => {
        var mntCyclePay = event.target.value;
        if (parseInt(mntCyclePay) <= parseInt(this.state.reglInsCycle.mnt)) {
            this.setState({
                reglInsCycle: Object.assign(this.state.reglInsCycle, { mntCyclePay: mntCyclePay })
            }, () => {
                var sommeTotPayer = parseInt(this.state.reglInsCycle.mntCartePay) + parseInt(this.state.reglInsCycle.mntTenuPay) + parseInt(this.state.reglInsCycle.mntCyclePay)
                this.setState({
                    mntTotalPayer: sommeTotPayer
                });
            });
        }
        else {
            toast.error('⛔ Le montant saisie est supérieur !!', { containerId: 'A' });
        }
    }

    addTva = (e) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            var mntCycle = parseInt(this.state.reglInsCycle.mnt)
            var tva = parseInt(this.state.tva)
            var mntCycleAvecTva = mntCycle + ((mntCycle * tva) / 100)
            this.setState({
                reglInsCycle: Object.assign(this.state.reglInsCycle, { mnt: mntCycleAvecTva })
            });
        }
        else {
            var mntCycleSansTva = this.state.mntCycleSansTva
            this.setState({
                reglInsCycle: Object.assign(this.state.reglInsCycle, { mnt: mntCycleSansTva })
            });
        }
    }

    addDatePaiement = d => {
        this.setState({
            datePaiement: d
        });
    };

    changePayCycle = (selectedPayCycle) => {
        this.setState({
            selectedPayCycle: selectedPayCycle
        }, () => {
            if (selectedPayCycle == 1) {
                if (this.state.reglInsCycle.carte != '') {
                    var mntPayCarte = this.state.reglInsCycle.carte

                }
                else {
                    var mntPayCarte = '0'

                }
                if (this.state.reglInsCycle.tenu != '') {
                    var mntPayTenu = this.state.reglInsCycle.tenu

                }
                else {
                    var mntPayTenu = '0'
                }
                var mntPayCycle = this.state.reglInsCycle.mnt
                this.setState({
                    reglInsCycle: Object.assign(this.state.reglInsCycle, { mntCartePay: mntPayCarte }),
                    reglInsCycle: Object.assign(this.state.reglInsCycle, { mntTenuPay: mntPayTenu }),
                    reglInsCycle: Object.assign(this.state.reglInsCycle, { mntCyclePay: mntPayCycle })
                }, () => {
                    var sommeTotPayer = parseInt(mntPayCarte) + parseInt(mntPayTenu) + parseInt(mntPayCycle)
                    this.setState({
                        mntTotalPayer: sommeTotPayer
                    });
                });
            }
            else {
                this.setState({
                    reglInsCycle: Object.assign(this.state.reglInsCycle, { mntCartePay: 0 }),
                    reglInsCycle: Object.assign(this.state.reglInsCycle, { mntTenuPay: 0 }),
                    reglInsCycle: Object.assign(this.state.reglInsCycle, { mntCyclePay: 0 })
                }, () => {
                    var sommeTotPayer = parseInt(this.state.reglInsCycle.mntCartePay) + parseInt(this.state.reglInsCycle.mntTenuPay) + parseInt(this.state.reglInsCycle.mntCyclePay)
                    this.setState({
                        mntTotalPayer: sommeTotPayer
                    });
                });
            }
        });
    }

    // mode paiement formation
    additionLignFormat = () => {
        let element = { modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }
        this.setState({
            tabModPayFormat: [...this.state.tabModPayFormat, element]
        })
    }

    addModPayFormat = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
            });
        }
    }

    addMntModPayFormat = (event, indexModPay) => {
        let mntModPay = event.target.value;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { mntModPay: Number.parseFloat(mntModPay).toFixed(3) }) : el))
        });
    }

    addNumChecFormat = (event, indexModPay) => {
        let numCheque = event.target.value;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: numCheque }) : el))
        });
    }

    addEcheanceChecFormat = (d, indexModPay) => {
        let dateChec = d;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: dateChec }) : el))
        });
    }

    addDateEncaissFormat = (d, indexModPay) => {
        let dateEncaiss = d;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: dateEncaiss }) : el))
        });
    }

    addTitulaireChecFormat = (event, indexModPay) => {
        let titulaireChec = event.target.value;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: titulaireChec }) : el))
        });
    }

    addBanqueFormat = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPayFormat: this.state.tabModPayFormat.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    toggleLignFormat = (index) => {
        this.setState(prevState => ({
            modalFormat: !prevState.modalFormat,
            indexLignASupprimer: index
        }));
    }

    deleteLignFormat = () => {
        this.state.tabModPayFormat.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modalFormat: !prevState.modalFormat
        }));
    }

    nonDeleteLignFormat = () => {
        this.setState(prevState => ({
            modalFormat: !prevState.modalFormat
        }));
    }

    // mode paiement cycle
    additionLignCycle = () => {
        let element = { modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }
        this.setState({
            tabModPayCycle: [...this.state.tabModPayCycle, element]
        })
    }

    addModPayCycle = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
            });
        }
    }

    addMntModPayCycle = (event, indexModPay) => {
        let mntModPay = event.target.value;
        this.setState({
            tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { mntModPay: Number.parseFloat(mntModPay).toFixed(3) }) : el))
        });
    }

    addNumChecCycle = (event, indexModPay) => {
        let numCheque = event.target.value;
        this.setState({
            tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: numCheque }) : el))
        });
    }

    addEcheanceChecCycle = (d, indexModPay) => {
        let dateChec = d;
        this.setState({
            tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: dateChec }) : el))
        });
    }

    addDateEncaissCycle = (d, indexModPay) => {
        let dateEncaiss = d;
        this.setState({
            tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: dateEncaiss }) : el))
        });
    }

    addTitulaireChecCycle = (event, indexModPay) => {
        let titulaireChec = event.target.value;
        this.setState({
            tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: titulaireChec }) : el))
        });
    }

    addBanqueCycle = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPayCycle: this.state.tabModPayCycle.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    toggleLignCycle = (index) => {
        this.setState(prevState => ({
            modalCycle: !prevState.modalCycle,
            indexLignASupprimer: index
        }));
    }

    deleteLignCycle = () => {
        this.state.tabModPayCycle.splice(this.state.indexLignASupprimer, 1)
        this.setState(prevState => ({
            modalCycle: !prevState.modalCycle
        }));
    }

    nonDeleteLignCycle = () => {
        this.setState(prevState => ({
            modalCycle: !prevState.modalCycle
        }));
    }

    //
    addBolPayFormat = (selectedBolPayFormat) => {
        this.setState({ selectedBolPayFormat: selectedBolPayFormat });
    }

    updMntFraisPayer = (event, indexMntFraisPayer) => {
        var mntFraisPay = event.target.value
        var mntTotal = parseInt(this.state.fraisReglEleve[indexMntFraisPayer].mntTotal)
        var mntDejaPayer = parseInt(this.state.fraisReglEleve[indexMntFraisPayer].mntPay)
        if (mntDejaPayer == 0) {
            if (mntTotal >= mntFraisPay) {
                this.setState({
                    fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mnt: Number.parseFloat(mntFraisPay).toFixed(3) }) : el))
                }, () => {
                    var mntRestNv = mntTotal - mntFraisPay
                    this.setState({
                        fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntRest: Number.parseFloat(mntRestNv).toFixed(3) }) : el))
                    });
                    var sommeMntFraisPayer = 0;
                    for (var i = 0; i < this.state.fraisReglEleve.length; i++) {
                        if (this.state.fraisReglEleve[i].mnt != "") {
                            sommeMntFraisPayer += parseInt(this.state.fraisReglEleve[i].mnt)
                        }
                        else {
                            sommeMntFraisPayer += 0
                        }
                    }
                    this.setState({
                        mntFraisTotalPayer: Number.parseFloat(sommeMntFraisPayer).toFixed(3)
                    });
                })
            }
            else {
                toast.error('⛔ Le montant saisie est supérieur !!', { containerId: 'A' });
            }
        }
        else if (mntDejaPayer > 0) {
            var restMnt = mntTotal - mntDejaPayer
            if (parseInt(restMnt) >= parseInt(mntFraisPay)) {
                this.setState({
                    fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mnt: Number.parseFloat(mntFraisPay).toFixed(3) }) : el))
                }, () => {
                    if (mntFraisPay > 0) {
                        var mntRestNv = restMnt - mntFraisPay
                        this.setState({
                            fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntRest: Number.parseFloat(mntRestNv).toFixed(3) }) : el))
                        });
                    }
                    else if (mntFraisPay == 0) {
                        var mntRestNv = mntTotal - mntDejaPayer
                        this.setState({
                            fraisReglEleve: this.state.fraisReglEleve.map((el, id) => (id === indexMntFraisPayer ? Object.assign(el, { mntRest: Number.parseFloat(mntRestNv).toFixed(3) }) : el))
                        });
