                                            </div>
                                        </div>
                                        <div className='row'>
                                            <h3 style={{ paddingLeft: '15px' }}>Mode de paiement :</h3>
                                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th className="col-md-3" style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Numéro cheque</th>
                                                        <th className="col-md-2" style={{ textAlign: 'center' }} scope="col">Echéance cheque</th>
                                                        <th className="col-md-2" style={{ textAlign: 'center' }} scope="col">Date encaissement</th>
                                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                        <th className="col-md-1" style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                                    </tr>
                                                </thead>
                                                {this.state.tabModPayCycle.map((el, index) =>
                                                    <tbody>
                                                        <tr>
                                                            <td><Select
                                                                value={el.modPay}
                                                                isClearable={true}
                                                                isSearchable={true}
                                                                onChange={(e) => this.addModPayCycle(e, index)}
                                                                options={this.state.listModPay}
                                                            />  </td>
                                                            <td style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.addMntModPayCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            {this.state.tabModPayCycle[index].modPay.value == 2 ?
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.addNumChecCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.echeanceChec} onChange={(e) => this.addEcheanceChecCycle(e, index)} /></td>
                                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateEncaiss} onChange={(e) => this.addDateEncaissCycle(e, index)} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.addTitulaireChecCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.addBanqueCycle(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLignCycle(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                                </Fragment>) :
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><button className='buttonSupprimer' onClick={() => this.toggleLignCycle(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                                </Fragment>)}
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                        </div>
                                        <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                            <Button style={{ borderRadius: '5px' }} onClick={this.additionLignCycle} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                        </div>
                                    </Fragment>
                                    ) : (null)}
                                {this.state.blockReglement == false ?
                                    (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutInscripCycle}>Confirmer</button>
                                    </div>) : (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscripCycle}>Confirmer</button>
                                    </div>)}
                            </div>
                        </div>
                    </div>
                    <Modal isOpen={this.state.modalFormat} toggle={this.nonDeleteLignFormat} className={this.props.className} fade={false}>
                        <ModalHeader toggle={this.nonDeleteLignFormat}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                        <ModalBody>
                            <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                </div>
                                <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                    <div><button onClick={() => this.deleteLignFormat()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                    <div><button onClick={() => this.nonDeleteLignFormat()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                </div>
                            </div>
                        </ModalBody>
                    </Modal>
                    <Modal isOpen={this.state.modalCycle} toggle={this.nonDeleteLignCycle} className={this.props.className} fade={false}>
                        <ModalHeader toggle={this.nonDeleteLignCycle}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                        <ModalBody>
                            <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                </div>
                                <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                    <div><button onClick={() => this.deleteLignCycle()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                    <div><button onClick={() => this.nonDeleteLignCycle()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                </div>
                            </div>
                        </ModalBody>
                    </Modal>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(InscripCycle);
