import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Switch from "react-switch";

class AjoutFormationNiv extends Component {
    constructor(props) {
        super(props);
        this.state = {
            formations: [{ niveau: '', duree: '', nbrMois: '', prixAnnuel: 0, prixAnnuelRed: '', prixMensuel: '', preTranche: '', deuxTranche: '', troisTranche: '' }],
            indexLignFormationASupprimer: '',
            modal: false,
            ListFilieres: [],
            selectedFiliere: '',
            ListSpecialites: [],
            selectedSpecialite: '',
            nomFormation: '',
            ListNivBase: [],
            selectedNivBase: '',
            reducFormat: 0,
            checked: false
        }
    }

    componentDidMount() {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    ListFilieres: code_html
                })
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                ListNivBase: res.data
            })
        })
    }

    additionLignFormation = () => {
        let element = { niveau: '', duree: '', nbrMois: '', prixAnnuel: 0, prixAnnuelRed: '', prixMensuel: '', preTranche: '', deuxTranche: '', troisTranche: '' }
        this.setState({
            formations: [...this.state.formations, element]
        })
    }

    addFiliere = selectedFiliere => {
        this.setState({ selectedFiliere }, () => {
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecialites.php?id=' + selectedFiliere.value).then(res => {
                console.log(res)
                this.setState({
                    ListSpecialites: res.data
                })
            })
        });
    };

    addSpecialite = selectedSpecialite => {
        this.setState({ selectedSpecialite });
        console.log(`Specialite selected:`, selectedSpecialite);
    };

    addNomFormation = (event) => {
        this.setState({
            nomFormation: event.target.value
        });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`NivBase selected:`, selectedNivBase);
    };

    handleChange = (checked) => {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    addReducFormat = (event) => {
        this.setState({
            reducFormat: event.target.value
        });
    }

    addNiveau = (event, indexNiveau) => {
        let niveau = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNiveau ? Object.assign(el, { niveau: niveau }) : el))
        });
    }

    addDuree = (event, indexNiveau) => {
        let duree = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNiveau ? Object.assign(el, { duree: duree }) : el))
        });
    }

    addNombreMois = (event, indexNbrMois) => {
        let nombreMois = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexNbrMois ? Object.assign(el, { nombreMois: nombreMois }) : el))
        });
    }

    addPrixAnnuel = (event, indexPrixAnnuel) => {
        let prixAnnuel = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexPrixAnnuel ? Object.assign(el, { prixAnnuel: prixAnnuel }) : el))
        });
        if (this.state.checked === true && this.state.reducFormat !== 0 && this.state.formations[indexPrixAnnuel].prixAnnuel !== 0) {
            let prixAnnuelRed = document.getElementsByClassName('prixAnn')[indexPrixAnnuel].value / (this.state.reducFormat);
            this.setState({
                formations: this.state.formations.map((el, id) => (id === indexPrixAnnuel ? Object.assign(el, { prixAnnuelRed: prixAnnuelRed }) : el))
            });
        }
    }

    // addPrixAnnuelRed = (indexPrixAnnuelRed) => {
    //     if (this.state.checked === true && this.state.reducFormat !== 0 && this.state.formations[indexPrixAnnuelRed].prixAnnuel !== 0) {
    //         let prixAnnuelRed = document.getElementsByClassName('prixAnn')[indexPrixAnnuelRed].value / (this.state.reducFormat);
    //         this.setState({
    //             formations: this.state.formations.map((el, id) => (id === indexPrixAnnuelRed ? Object.assign(el, { prixAnnuelRed: prixAnnuelRed }) : el))
    //         });
    //     }
    // }

    addPrixMensuel = (event, indexPrixMensuel) => {
        let prixMensuel = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexPrixMensuel ? Object.assign(el, { prixMensuel: prixMensuel }) : el))
        });
    }

    addPreTranche = (event, indexPreTranche) => {
        let preTranche = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexPreTranche ? Object.assign(el, { preTranche: preTranche }) : el))
        });
    }

    addDeuxTranche = (event, indexDeuxTranche) => {
        let deuxTranche = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexDeuxTranche ? Object.assign(el, { deuxTranche: deuxTranche }) : el))
        });
    }

    addTroisTranche = (event, indexTroisTranche) => {
        let troisTranche = event.target.value;
        this.setState({
            formations: this.state.formations.map((el, id) => (id === indexTroisTranche ? Object.assign(el, { troisTranche: troisTranche }) : el))
        });
    }

    toggleLignFormation = (indexLignFormation) => {
        this.setState(prevState => ({
            modal: !prevState.modal,
            indexLignFormationASupprimer: indexLignFormation
        }));
    }

    deleteLignFormation = () => {
        this.state.formations.splice(this.state.indexLignFormationASupprimer, 1)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLignFormation = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    ajout = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        let year = new Date().getFullYear();
        console.log(this.state.selectedFiliere)
        console.log(this.state.selectedSpecialite)
        console.log(this.state.nomFormation)
        console.log(this.state.selectedNivBase)
        console.log(this.state.formations)
        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddFormationNiv.php',
        //     type: 'POST',
        //     data: {
        //         filiereId: this.state.selectedFiliere.value,
        //         specialiteId: this.state.selectedSpecialite.value,
        //         nomFormation: this.state.nomFormation,
        //         nivBaseId: this.state.selectedNivBase.value,
        //         formations: this.state.formations,
        //         year: year,
        //         userId: userId
        //     },
        //     dataType: 'json',
        //     success: (code_html, statut) => {
        //         if (code_html.Result == "OK") {
        //             this.props.history.push('/FormationNiv');
        //         }
        //         else if (code_html.Result == "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout formation</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Filire</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addFiliere}
                                                options={this.state.ListFilieres}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Spcialit</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addSpecialite}
                                                options={this.state.ListSpecialites}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom formation</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="text" class="form-control" placeholder='Nom' onChange={this.addNomFormation} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Niveau de Base</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addNivBase}
                                                options={this.state.ListNivBase}
                                            />
                                        </div>
                                    </div>
                                    <label>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Rduction</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                    {this.state.checked == true ?
                                        (<div class="col-md-6">
                                            <div class="form-group">
                                                <label>Rduction par formation %</label>
                                                <input style={{ backgroundColor: 'white', borderRadius: '5px' }} type="number" class="form-control" placeholder='% Rduction' onChange={this.addReducFormat} />
                                            </div>
                                        </div>) : (null)}
                                </div>
                                <div class="row">
                                    <table style={{ marginTop: '10px' }} class="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Niveau</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Dure</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Nbr de mois</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix Annuel</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix Annuel rd</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Prix Mensuel</th>
                                                <th colspan="3" style={{ textAlign: 'center' }} scope="col">Paiement en tranche</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.formations.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Niveau' value={el.niveau} onChange={(e) => this.addNiveau(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '70px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Dure' value={el.duree} onChange={(e) => this.addDuree(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '70px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="number" placeholder='Nbr mois' value={el.NombreMois} onChange={(e) => this.addNombreMois(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '85px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" className='prixAnn' placeholder='Prix Annuel' value={el.prixAnnuel} onChange={(e) => this.addPrixAnnuel(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '84px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Prix Annuel rd' value={el.prixAnnuelRed} onChange={this.addPrixAnnuelRed(index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '102px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Prix Mensuel' value={el.prixMensuel} onChange={(e) => this.addPrixMensuel(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '88px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Premire' value={el.preTranche} onChange={(e) => this.addPreTranche(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '75px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Deuxime' value={el.deuxTranche} onChange={(e) => this.addDeuxTranche(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '75px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><input type="text" placeholder='Troisime' value={el.troisTranche} onChange={(e) => this.addTroisTranche(e, index)} style={{ textAlign: 'center', height: '38px', borderRadius: '5px', width: '75px' }} /></td>
                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '30%' }} className='buttonSupprimer' onClick={() => this.toggleLignFormation(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                    <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                        <Button style={{ borderRadius: '5px' }} onClick={this.additionLignFormation} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                    </div>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.nonDeleteLignFormation} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.nonDeleteLignFormation}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                    <ModalBody>
                                        <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                            <div style={{ display: 'flex', justifyContent: 'center' }}>
                                                <p>tes-Vous sr de vouloir supprimer cette formation ?</p>
                                            </div>
                                            <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                                <div><button onClick={() => this.deleteLignFormation()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                                <div><button onClick={() => this.nonDeleteLignFormation()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                            </div>
                                        </div>
                                    </ModalBody>
                                </Modal>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutFormationNiv);
