import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import axios from 'axios'

class ExplClient extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomClient: '',
            adresse: '',
            numeroTelephone: '',
            email: ''
        }
    }

    componentDidMount() {
        let id_client = this.props.location.state.id_client;
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/GetClientById.php?id=' + id_client).then(res => {
            console.log(res)
            this.setState({
                nomClient: res.data.nomClient,
                adresse: res.data.adresse,
                numeroTelephone: res.data.numeroTelephone,
                email: res.data.email
            })
        })
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div style={{backgroundColor:'white'}} className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Informations Client</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Nom client</label>
                                <input type="text" disabled Value={this.state.nomClient} class="form-control" style={{backgroundColor:'white'}} />
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Adresse</label>
                                <input type="text" disabled Value={this.state.adresse} class="form-control" style={{backgroundColor:'white'}} />
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Num√©ro de Telephone</label>
                                <input type="text" disabled Value={this.state.numeroTelephone} class="form-control" style={{backgroundColor:'white'}} />
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Email</label>
                                <input type="text" disabled Value={this.state.email} class="form-control" style={{backgroundColor:'white'}} />
                            </div>
                        </div>
                    </div>
                    <Link to="/Clients">
                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '20px' }}>
                            <button style={{ width: '15%', backgroundColor: '#0083FF', borderColor: '#0083FF', borderRadius: '5px' }} type="button" className="btn btn-success">Retour</button>
                        </div>
                    </Link>
                </div>
            </div>
        );
    }
}

export default ExplClient;
