                                                <label>Prénom</label>
                                                <input type="text" value={this.state.prenom} className="form-control" placeholder={"Prenom"} onChange={this.addPrenom} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Nom</label>
                                                <input type="text" value={this.state.nom} className="form-control" placeholder={"Nom"} onChange={this.addNom} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Age</label>
                                                <input type="number" value={this.state.age} className="form-control" onChange={this.addAge} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Lieu résidence</label>
                                                <Select
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addLieuResid}
                                                    options={this.state.lieuResid}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Niveau inscription</label>
                                                <Select
                                                    value={this.state.selectedNivBase}
                                                    isClearable={true}
                                                    isSearchable={true}
                                                    onChange={this.addNivBase}
                                                    options={this.state.listNivBase}
                                                />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Email</label>
                                                <input type="text" value={this.state.email} className="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Tel</label>
                                                <input type="number" value={this.state.tel} className="form-control" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Fax</label>
                                                <input type="number" className="form-control" onChange={this.addFax} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Facebook</label>
                                                <input type="text" className="form-control" placeholder={"Facebook"} onChange={this.addFacebook} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Fonction actuelle</label>
                                                <input type="text" className="form-control" placeholder={"Fonction actuelle"} onChange={this.addFct} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Photo verso </label>
                                                <input type="file" className="form-control" name="file" onChange={(e) => this.addPhotoVerso(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <div className="form-group">
                                                <label>Motivation</label>
                                                <input type="text" className="form-control" onChange={this.addMotivation} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-4">
                                            <label>Type Formation</label>
                                            <RadioGroup
                                                name="typeFormat"
                                                selectedValue={this.state.selectedTypeFormat}
                                                onChange={this.addTypeFormat}>
                                                <div>
                                                    <label>
                                                        <Radio value={1} />Indviduelle
                                                </label>
                                                    <label>
                                                        <Radio value={2} />Cycle
                                                </label>
                                                </div>
                                            </RadioGroup>
                                        </div>
                                        {this.state.selectedTypeFormat == 1 ?
                                            (<div className="col-md-4">
                                                <div className="form-group">
                                                    <label>Nombre d'heures</label>
                                                    <input type="number" className="form-control" onChange={this.addNbrHeure} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                                </div>
                                            </div>) : (null)}
                                    </div>
                                    )}
                                <div className="row">
                                    <div className="col-md-4">
                                        <label>Comment avez-vous connu notre centre de formation ?</label>
                                        <span style={{ display: 'flex', flexWrap: 'wrap', padding: '10px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }}>
                                            {this.state.listConnaissance.map(el =>
                                                <label>
                                                    <Checkbox
                                                        onChange={(e) => this.addConnaiss(e, el)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    {el.label}
                                                </label>
                                            )}
                                        </span>
                                    </div>
                                </div>
                                <div style={{ paddingTop: '5px' }} className="row">
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label>Cycles</label>
                                            <Select
                                                isMulti={true}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addCycle}
                                                options={this.state.listCycle}
                                                formatGroupLabel={formatGroupLabel}
                                            />
                                        </div>
                                    </div>
                                    {this.state.selectedTypeFormat == 1 ?
                                        (<div className="col-md-4">
                                            <div className="form-group">
                                                <label>Proposition de cycle</label>
                                                <input type="text" className="form-control" onChange={this.addPropCycle} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>) : (null)}
                                </div>

                                {this.state.arrayRegl.length > 0 ?
                                    (<Fragment>
                                        <div className="row">
                                            <div className="col-md-6">
                                                <label>Voulez vous payer aussi une partie ou la somme total de l'inscription de la formation?</label>
                                                <RadioGroup
                                                    name="bolFormat"
                                                    selectedValue={this.state.selectedBolPayFormat}
                                                    onChange={this.addBolPayFormat}>
                                                    <div>
                                                        <label style={{ paddingRight: '10px' }}>
                                                            <Radio value={1} />Oui
                                                </label>
                                                        <label>
                                                            <Radio value={2} />Non
                                                </label>
                                                    </div>
                                                </RadioGroup>
                                            </div>
                                        </div>
                                        {this.state.selectedBolPayFormat == 1 ?
                                            (<Fragment>
                                                <Fragment>
                                                    <h3>Paiement Frais Inscription formation:</h3>
                                                    <div className="row">
                                                        {this.state.fraisReglEleve.length > 0 ?
                                                            (<Fragment>
                                                                <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                                    <thead>
                                                                        <tr>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Nom</th>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Montant total</th>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Montant deja payé</th>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Montant a payé</th>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Montant restant</th>
                                                                        </tr>
                                                                    </thead>
                                                                    {this.state.fraisReglEleve.map((el, index) =>
                                                                        <tbody>
                                                                            <tr>
                                                                                <td style={{ textAlign: 'center' }}><p style={{ textAlign: 'center', borderRadius: '5px' }}>{el.nomFrais}</p></td>
                                                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={Number.parseFloat(el.mntTotal).toFixed(3)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={el.mntPay} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                                <td style={{ textAlign: 'center' }}><input type="number" value={Number.parseFloat(el.mnt).toFixed(3)} onChange={(e) => this.updMntFraisPayer(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                                <td style={{ textAlign: 'center' }}><input type="number" disabled value={el.mntRest} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                            </tr>
                                                                        </tbody>
                                                                    )}
                                                                </table>
                                                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                                                    <div style={{ marginRight: '10px' }}>
                                                                        <p>Montant total frais payer</p>
                                                                    </div>
                                                                    <div style={{ marginRight: '-40px' }}>
                                                                        <input disabled value={this.state.mntFraisTotalPayer} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                                                    </div>
                                                                </div> </Fragment>) : (<p style={{ paddingLeft: '15px' }}>{"L'eléve " + this.state.nomEleve + " a payé la totalité des frais inscription"}</p>)}
                                                    </div>
                                                </Fragment>
                                                <Fragment>
                                                    {paiementFormation}
                                                    {this.state.reglEleve.length > 0 ?
                                                        (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                                            <div style={{ marginRight: '10px' }}>
                                                                <p>Montant total inscription payer</p>
                                                            </div>
                                                            <div style={{ marginRight: '-40px' }}>
                                                                <input disabled value={this.state.mntIncripTotalPayer} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                                            </div>
                                                        </div>) : (null)}
                                                </Fragment>
                                            </Fragment>
                                            ) : (null)}
                                    </Fragment>) : (null)}

                                {this.state.blockReglement == true ?
                                    (<Fragment>
                                        {/* <h4>Case réservée à l'administration</h4> */}
                                        {paiementInscripCycle}
                                        {this.state.societeCoch == true ?
                                            (<label style={{ paddingRight: '5px', display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '10px' }}>
                                                Tva
                                                <Checkbox
                                                    onChange={(e) => this.addTva(e)}
                                                    disabled={this.state.disabled}
                                                />
                                                {this.state.tva}%
                                            </label>
                                            ) : (null)}
                                        <RadioGroup
                                            name="typePaiement"
                                            selectedValue={this.state.selectedPayCycle}
                                            onChange={this.changePayCycle}>
                                            <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginRight: '10px' }}>
                                                <label style={{ paddingRight: '5px' }}>
                                                    <Radio value={1} />Paiement total
                                                </label>
                                                <label style={{ paddingRight: '5px' }}>
                                                    <Radio value={2} />Paiement partiel
                                                </label>
                                            </div>
                                        </RadioGroup>
                                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '15px' }}>
                                            <div style={{ marginRight: '10px' }}>
                                                <p>Montant total payer</p>
                                            </div>
                                            <div style={{ marginRight: '-40px' }}>
                                                <input disabled value={Number.parseFloat(this.state.mntTotalPayer).toFixed(3)} style={{ border: 'solid', borderColor: '#DCDCDC', textAlign: 'center', width: '70%', borderRadius: '5px' }} type="text" />
                                            </div>
                                        </div>
                                        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }} className='row'>
                                            <div className="col-md-3">
                                                <label>Date paiement</label>
                                                <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                    selected={this.state.datePaiement}
                                                    onChange={this.addDatePaiement}
                                                />
                                            </div>
                                        </div>
                                        <div className='row'>
                                            <h3 style={{ paddingLeft: '15px' }}>Mode de paiement :</h3>
                                            <table style={{ marginTop: '10px' }} class="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Numéro cheque</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Echéance cheque</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Date encaissement</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                        <th style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                        <th style={{ textAlign: 'center', width: '15%' }} scope="col">Suppression</th>
                                                    </tr>
                                                </thead>
                                                {this.state.tabModPay.map((el, index) =>
                                                    <tbody>
                                                        <tr>
                                                            <td style={{ width: '90%' }}><Select
                                                                value={el.modPay}
                                                                isClearable={true}
                                                                isSearchable={true}
                                                                onChange={(e) => this.addModPay(e, index)}
                                                                options={this.state.listModPay}
                                                            />  </td>
                                                            <td style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.addMntModPay(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                            {this.state.tabModPay[index].modPay.value == 2 ?
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.addNumChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.echeanceChec} onChange={(e) => this.addEcheanceChec(e, index)} /></td>
                                                                    <td style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateEncaiss} onChange={(e) => this.addDateEncaiss(e, index)} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.addTitulaireChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.addBanque(e, index)} style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '35%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                                </Fragment>) :
                                                                (<Fragment>
                                                                    <td style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px', width: '100px' }} /></td>
                                                                    <td style={{ textAlign: 'center' }}><button style={{ width: '35%' }} className='buttonSupprimer' onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} class="fas fa-trash-alt"></i></button></td>
                                                                </Fragment>)}
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                        </div>
                                        <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                            <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i class="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                        </div>
                                    </Fragment>
                                    ) : (null)}
                                {this.state.blockReglement == false ?
                                    (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutInscripCycle}>Confirmer</button>
                                    </div>) : (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscripCycle}>Confirmer</button>
                                    </div>)}
                            </div>
                        </div>
                    </div>
                    <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                        <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                        <ModalBody>
                            <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                </div>
                                <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                    <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-up"></i>Oui</button></div>
                                    <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} class="far fa-thumbs-down"></i>Non</button></div>
                                </div>
                            </div>
                        </ModalBody>
                    </Modal>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(InscripCycle);
