                                            <div className="form-group">
                                                <label>Email</label>
                                                <input type="text" className="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Tel</label>
                                                <input type="number" className="form-control" onChange={this.addTel} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Fax</label>
                                                <input type="number" className="form-control" onChange={this.addFax} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Facebook</label>
                                                <input type="text" className="form-control" placeholder={"Facebook"} onChange={this.addFacebook} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Fonction actuelle</label>
                                                <input type="text" className="form-control" placeholder={"Fonction actuelle"} onChange={this.addFct} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Photo verso </label>
                                                <input type="file" className="form-control" name="file" onChange={(e) => this.addPhotoVerso(e)} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Motivation</label>
                                                <input type="text" className="form-control" onChange={this.addMotivation} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <label>Type Formation</label>
                                            <RadioGroup
                                                name="typeFormat"
                                                selectedValue={this.state.selectedTypeFormat}
                                                onChange={this.addTypeFormat}>
                                                <div>
                                                    <label>
                                                        <Radio value={1} />Indviduelle
                                                </label>
                                                    <label>
                                                        <Radio value={2} />Cycle
                                                </label>
                                                </div>
                                            </RadioGroup>
                                        </div>
                                        {this.state.selectedTypeFormat == 1 ?
                                            (<div className="col-md-6">
                                                <div className="form-group">
                                                    <label>Nombre d'heures</label>
                                                    <input type="number" className="form-control" onChange={this.addNbrHeure} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                                </div>
                                            </div>) : (null)}
                                    </div>
                                    )}
                                <div className="row">
                                    <div className="col-md-6">
                                        <label>Comment avez-vous connu notre centre de formation ?</label>
                                        <span style={{ display: 'flex', flexWrap: 'wrap', padding: '10px', borderRadius: '5px', border: 'solid 1px #B3B3B3' }}>
                                            {this.state.listConnaissance.map(el =>
                                                <label>
                                                    <Checkbox
                                                        onChange={(e) => this.addConnaiss(e, el)}
                                                        disabled={this.state.disabled}
                                                    />
                                                    {el.label}
                                                </label>
                                            )}
                                        </span>
                                    </div>
                                </div>
                                <div style={{ paddingTop: '5px' }} className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Cycles</label>
                                            <Select
                                                isMulti={true}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addCycle}
                                                options={this.state.listCycle}
                                            // formatGroupLabel={formatGroupLabel}
                                            />
                                        </div>
                                    </div>
                                    {this.state.selectedTypeFormat == 1 ?
                                        (<div className="col-md-6">
                                            <div className="form-group">
                                                <label>Proposition de cycle</label>
                                                <input type="text" className="form-control" onChange={this.addPropCycle} style={{ borderRadius: '5px', border: 'solid 1px', borderColor: '#B3B3B3' }} />
                                            </div>
                                        </div>) : (null)}
                                </div>
                                {this.state.blockReglement == true ?
                                    (<Fragment>
                                        <h4>Case réservée à l'administration</h4>
                                        {paiementInscrip}
                                    </Fragment>
                                    ) : (null)}
                                {this.state.blockReglement == false ?
                                    (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutInscripCycle}>Confirmer</button>
                                    </div>) : (<div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                        <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajoutRegInscripCycle}>Confirmer</button>
                                    </div>)}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(InscripCycle);
