import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'
import Switch from "react-switch";
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';

class Inscription extends Component {
    constructor(props) {
        super(props);
        this.state = {
            checked: false,
            prenom: '',
            nom: '',
            selectedType: '',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: new Date(),
            villeDelivreSelonType: '',
            dateNaissance: new Date(),
            lieuNaissance: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            adresseOfficiel: '',
            adresseTun: '',
            listNivBase: [],
            selectedNivBase: '',
            imageVerso: null,
            numInscrip: '',
            listApprenants: [],
            selectedApprenant: '',
            disabled: false,
            listPapiers: [],
            listTypePay: [],
            selectedTypePay: 'Annuel',
            listFilieres: [],
            selectedFiliere: '',
            listSpecialites: [],
            selectedSpecialite: '',
            listFormations: [],
            selectedFormation: '',
            listNiveaux: [],
            selectedNiveau: '',
            listGroupes: [],
            selectedGroup: '',
            papiersChoisi: [],
            nomPere: '',
            adresseParents: '',
            profession: '',
            telBureau: '',
            //
            blockReglement: true,
            listFrais: [],
            listTypePaiement: '',
            selectedTypePayFrais: ''
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivBase.php').then(res => {
            console.log(res)
            this.setState({
                listNivBase: res.data
            })
        })
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFilieres.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listFilieres: code_html
                });
            }
        });
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectApprenants.php').then(res => {
            console.log(res)
            this.setState({
                listApprenants: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/PapiersDemandee.php').then(res => {
            console.log(res)
            this.setState({
                listPapiers: res.data
            })
        })
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/TypesPaiementEleve.php').then(res => {
            console.log(res)
            this.setState({
                listTypePay: res.data
            })
        })

        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetFrais.php',
            type: 'POST',
            data: {
                anneeScolaire: anneeScolaire
            },
            dataType: 'json',
            success: (code_html, statut) => {
                this.setState({
                    listFrais: code_html
                });
            }
        });

    }

    handleChange = (checked) => {
        this.setState({ checked });
        console.log(this.state.checked)
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addNumSelonType = (event) => {
        this.setState({
            numSelonType: event.target.value
        });
    }

    addDateLivraison = d => {
        this.setState({
            dateDelivSelonType: d
        });
    };

    addVilleDelivreSelonType = (event) => {
        this.setState({
            villeDelivreSelonType: event.target.value
        });
    }

    addNationalite = (event) => {
        this.setState({
            nationalite: event.target.value
        });
    }

    addDateNaissance = d => {
        this.setState({
            dateNaissance: d
        });
    };

    addLieuNaissance = (event) => {
        this.setState({
            lieuNaissance: event.target.value
        });
    }

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addGender = (selectedGender) => {
        this.setState({ selectedGender: selectedGender });
    }

    addMobile = (event) => {
        this.setState({
            mobile: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addAdresseOfficiel = (event) => {
        this.setState({
            adresseOfficiel: event.target.value
        });
    }

    addAdresseTun = (event) => {
        this.setState({
            adresseTun: event.target.value
        });
    }

    addNivBase = selectedNivBase => {
        this.setState({ selectedNivBase });
        console.log(`nivBase selected:`, selectedNivBase);
    };

    addImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    addNumInscrip = (event) => {
        this.setState({
            numInscrip: event.target.value
        });
    }

    addApprenant = selectedApprenant => {
        if (selectedApprenant != null) {
            this.setState({ selectedApprenant }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetInfoEleveById.php?id=' + selectedApprenant.value).then(res => {
                    console.log(res)
                    this.setState({
                        selectedType: res.data.type,
                        numSelonType: res.data.numero,
                        nationalite: res.data.nationalite,
                        dateDelivSelonType: new Date(res.data.dateNum),
                        villeDelivreSelonType: res.data.villeNum,
                        dateNaissance: new Date(res.data.naissance),
                        lieuNaissance: res.data.villeNaiss,
                        mobile: res.data.mobile,
                        tel: res.data.tel,
                        adresseTun: res.data.adresse,
                        selectedNivBase: res.data.nivBase,
                        imageVerso: res.data.photo
                    })
                })
            })
        }
        else {
            this.setState({
                type: '',
                numSelonType: '',
                nationalite: '',
                dateDelivSelonType: '',
                villeDelivreSelonType: '',
                dateNaissance: '',
                lieuNaissance: '',
                mobile: '',
                tel: '',
                adresseTun: '',
                selectedNivBase: '',
                imageVerso: ''
            });
        }
    };

    addNomPere = (event) => {
        this.setState({
            nomPere: event.target.value
        });
    }

    addAdresseParents = (event) => {
        this.setState({
            adresseParents: event.target.value
        });
    }

    addProfession = (event) => {
        this.setState({
            profession: event.target.value
        });
    }

    addTelBureau = (event) => {
        this.setState({
            telBureau: event.target.value
        });
    }

    addFiliere = selectedFiliere => {
        if (selectedFiliere != null) {
            this.setState({ selectedFiliere }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectSpecByFiliere.php?id=' + selectedFiliere.value).then(res => {
                    console.log(res)
                    this.setState({
                        listSpecialites: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listSpecialites: []
            })
        }
    };

    addSpecialite = selectedSpecialite => {
        if (selectedSpecialite != null) {
            this.setState({ selectedSpecialite }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectFormBySpecialite.php?id=' + selectedSpecialite.value).then(res => {
                    console.log(res)
                    this.setState({
                        listFormations: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listFormations: []
            })
        }
    };

    addFormation = selectedFormation => {
        if (selectedFormation != null) {
            this.setState({ selectedFormation }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectNivByFormation.php?id=' + selectedFormation.value).then(res => {
                    console.log(res)
                    this.setState({
                        listNiveaux: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listNiveaux: []
            })
        }
    };

    addNiveau = selectedNiveau => {
        if (selectedNiveau != null) {
            this.setState({ selectedNiveau }, () => {
                axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectGroupByNiv.php?id=' + selectedNiveau.value).then(res => {
                    console.log(res)
                    this.setState({
                        listGroupes: res.data
                    })
                })
            })
        }
        else {
            this.setState({
                listGroupes: []
            })
        }
    };

    addGroup = selectedGroup => {
        this.setState({ selectedGroup })
    };

    addPapiers = (e, el, index) => {
        console.log('Checkbox checked:', (e.target.checked));
        if (e.target.checked === true) {
            let element = el.id
            this.setState({
                papiersChoisi: [...this.state.papiersChoisi, element]
            });
        }
        else {
            this.state.papiersChoisi.splice(index, 1)
        }
    }

    addTypePay = (selectedTypePay) => {
        this.setState({ selectedTypePay: selectedTypePay });
        console.log(selectedTypePay)
    }

    ajout = () => {
        // Ann√©e scolaire 
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        // convert dateInscription
        let dateInscrip = new Date()
        let month = '' + (dateInscrip.getMonth() + 1);
        let day = '' + dateInscrip.getDate();
        let year = dateInscrip.getFullYear();
        if (month.length < 2)
            month = '0' + month;
        if (day.length < 2)
            day = '0' + day;
        var convertInscrip = [year, month, day].join('-');
        // convert dateNum
        let dateDelivSelonType = this.state.dateDelivSelonType
        let month_one = '' + (dateDelivSelonType.getMonth() + 1);
        let day_one = '' + dateDelivSelonType.getDate();
        let year_one = dateDelivSelonType.getFullYear();
        if (month_one.length < 2)
            month_one = '0' + month_one;
        if (day_one.length < 2)
            day_one = '0' + day_one;
        var convertDateDelivSelonType = [year_one, month_one, day_one].join('-');
        // convert dateNaiss
        let dateNaissance = this.state.dateNaissance
        let month_two = '' + (dateNaissance.getMonth() + 1);
        let day_two = '' + dateNaissance.getDate();
        let year_two = dateNaissance.getFullYear();
        if (month_two.length < 2)
            month_two = '0' + month_two;
        if (day_two.length < 2)
            day_two = '0' + day_two;
        var convertDateNaissance = [year_two, month_two, day_two].join('-');
        // recuperer id user
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var userId = userPermissions[0].idUser
        if (this.state.checked === true) {
            // Test si photo vide
            var pathImageVerso = '';
            console.log(this.state.imageVerso)
            if (this.state.imageVerso != null) {
                pathImageVerso = "images/Photo_Apprenants/" + this.state.imageVerso[0].name
            }
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscription.php',
                type: 'POST',
                data: {
                    // Ajout nv eleve
                    prenom: this.state.prenom,
                    nom: this.state.nom,
                    type: this.state.selectedType,
                    numero: this.state.numSelonType,
                    nationalite: this.state.nationalite,
                    dateType: convertDateDelivSelonType,
                    villeType: this.state.villeDelivreSelonType,
                    dateNaissance: convertDateNaissance,
                    lieuNaissance: this.state.lieuNaissance,
                    adresseOfficiel: this.state.adresseOfficiel,
                    adresseTun: this.state.adresseTun,
                    email: this.state.email,
                    sexe: this.state.selectedGender,
                    mobile: this.state.mobile,
                    tel: this.state.tel,
                    photo: pathImageVerso,
                    nivBaseId: this.state.selectedNivBase.value,
                    checked: true,
                    // Inscription eleve
                    numInscrip: this.state.numInscrip,
                    dateInscrip: convertInscrip,
                    nomPere: this.state.nomPere,
                    adresseParents: this.state.adresseParents,
                    profession: this.state.profession,
                    telBureau: this.state.telBureau,
                    niveauId: this.state.selectedNiveau.value,
                    groupId: this.state.selectedGroup.value,
                    papier: this.state.papiersChoisi,
                    typePaiement: this.state.selectedTypePay,
                    anneeScolaire: anneeScolaire,
                    userId: userId
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.setState({
                            blockReglement: true
                        });
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
            // Test si photo vide
            if (this.state.imageVerso != null) {
                let files = this.state.imageVerso;
                console.warn('data files', files)
                let reader = new FileReader();
                reader.readAsDataURL(files[0])
                reader.onload = (e) => {
                    console.log(e)
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UploadphotoAppr.php',
                        type: 'POST',
                        data: {
                            file: e.target.result,
                            nomfile: this.state.imageVerso[0].name
                        },
                        dataType: 'json',
                        success: function (code_html, statut) {
                        }
                    }
                    )
                }
            }
        }
        else {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddInscription.php',
                type: 'POST',
                data: {
                    // Mise a jour ancien eleve
                    type: this.state.selectedType,
                    numero: this.state.numSelonType,
                    nationalite: this.state.nationalite,
                    dateType: convertDateDelivSelonType,
                    villeType: this.state.villeDelivreSelonType,
                    dateNaissance: convertDateNaissance,
                    lieuNaissance: this.state.lieuNaissance,
                    adresseTun: this.state.adresseTun,
                    mobile: this.state.mobile,
                    tel: this.state.tel,
                    nivBaseId: this.state.selectedNivBase.value,
                    checked: false,
                    // Inscription eleve
                    numInscrip: this.state.numInscrip,
                    eleveId: this.state.selectedApprenant.value,
                    dateInscrip: convertInscrip,
                    nomPere: this.state.nomPere,
                    adresseParents: this.state.adresseParents,
                    profession: this.state.profession,
                    telBureau: this.state.telBureau,
                    niveauId: this.state.selectedNiveau.value,
                    groupId: this.state.selectedGroup.value,
                    papier: this.state.papiersChoisi,
                    typePaiement: this.state.selectedTypePay,
                    anneeScolaire: anneeScolaire,
                    userId: userId
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.setState({
                            blockReglement: true
                        }, () => {
                            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                            var anneeScolaire = userPermissions[0].anneeScolaire
                            jQuery.ajax({
                                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetFrais.php',
                                type: 'POST',
                                data: {
                                    anneeScolaire: anneeScolaire
                                },
                                dataType: 'json',
                                success: (code_html, statut) => {
                                    this.setState({
                                        listFrais: code_html
                                    });
                                }
                            });
                            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectTypesPayEleve.php').then(res => {
                                console.log(res)
                                this.setState({
                                    listTypePaiement: res.data
                                })
                            })
                        });
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
    }

    addFrais = (selectedFrais, indexFrais) => {
        this.setState({ selectedFrais: selectedFrais });
        console.log(selectedFrais)
    }

    addTypePayFrais = selectedTypePayFrais => {
        this.setState({ selectedTypePayFrais });
        console.log(`typePayFrais selected:`, selectedTypePayFrais);
    };

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Inscription</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <label style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '10px' }}>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Apprenant introuvable dans la liste!!!! Ajouter un Apprenant</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                </div>
                                {this.state.checked === false ?
                                    (<Fragment>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Apprenant</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addApprenant}
                                                        options={this.state.listApprenants}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div style={{ display: 'grid' }} class="form-group">
                                                    <label>Photo</label>
                                                    <img style={{ width: '20%' }} src={this.state.imageVerso} alt="img" />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Date naissance</label>
                                                <div class="form-group">
                                                    <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                        value={this.state.dateNaissance}
                                                        selected={this.state.dateNaissance}
                                                        onChange={this.addDateNaissance}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Lieu de naissance</label>
                                                    <input type="text" class="form-control" value={this.state.lieuNaissance} onChange={this.addLieuNaissance} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nationalit√©</label>
                                                    <input type="text" class="form-control" value={this.state.nationalite} onChange={this.addNationalite} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Type</label>
                                                <RadioGroup
                                                    name="type"
                                                    selectedValue={this.state.selectedType}
                                                    onChange={this.addType}>
                                                    <label>
                                                        <Radio value="CIN" />CIN
                                                    </label>
                                                    <label>
                                                        <Radio value="Passeport" />Passeport
                                                    </label>
                                                </RadioGroup>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Numero</label>
                                                    <input type="text" class="form-control" value={this.state.numSelonType} onChange={this.addNumSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Delivr√© le</label>
                                                <div class="form-group">
                                                    <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                        value={this.state.dateDelivSelonType}
                                                        selected={this.state.dateDelivSelonType}
                                                        onChange={this.addDateLivraison}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Delivr√© ville de</label>
