import React, { Component } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'

class AjoutTypeEnseign extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nom: '',
            coeiff: ''
        }
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addCoeiff = (event) => {
        this.setState({
            coeiff: event.target.value
        });
    }

    ajout = () => {
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddTypeEnseign.php',
            type: 'POST',
            data: {
                nom: this.state.nom,
                coeiff: this.state.coeiff
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/TypeEnseign');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout type enseignement</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom</label>
                                            <input type="text" class="form-control" placeholder="Diplome" onChange={this.addNom} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Coefficient</label>
                                            <input type="number" class="form-control" placeholder="Coefficient" onChange={this.addCoeiff} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutTypeEnseign);
