import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import jQuery from 'jquery'
import './paginate.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { RadioGroup, Radio } from 'react-radio-group'
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class Remboursement extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Date',
                    selector: 'date',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Eléve',
                    selector: 'nomComplet',
                    sortable: false,
                    center: true,
                    style: { width: '20%' }
                },
                {
                    name: 'Description',
                    cell: row => <div onClick={() => this.toggleFrais(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#FFB6C1', fontSize: '20px' }} className="fas fa-info"></i></div><div style={{ marginLeft: '5px' }}></div>Description</div>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Numéro recu',
                    selector: 'numRecu',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Montant',
                    selector: 'mnt',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Mode',
                    selector: 'mode',
                    sortable: false,
                    center: true,
                    style: { width: '10%' }
                },
                {
                    name: 'Modification',
                    cell: row => <Link to={{
                        pathname: '/ModifRemboursement',
                        state: { idRemboursement: row.idRemboursement }
                    }}><button className='buttonModifier'><i style={{ color: 'white' }} className="fas fa-pencil-alt"></i></button></Link>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Recu',
                    cell: row => <div></div>
                    // <div onClick={() => this.recu(row)} style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87B87F', fontSize: '20px' }} className="fas fa-file-alt"></i></div><div style={{ marginLeft: '5px' }}></div></div>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                },
                {
                    name: 'Suppression',
                    cell: row => <button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggletwo(row.idRemboursement)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '10%'
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            modal: false,
            pending: true,
            typeRembours: '',
            descripRembours: '',
            // search
            selectedScol: '',
            listEleve: [],
            selectedEleve: '',
            dateD: '',
            dateF: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Remboursement.php',
                type: 'POST',
                data: {
                    query: '',
                    limit: this.state.nbrEltperPage,
                    page: 1,
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        nombreTotalElement: code_html.total,
                        data: code_html.data,
                        pending: false
                    });
                }
            });
        }
    }

    newElement = (page, totalRows) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (this.state.selectedEleve != '') {
            var eleveId = this.state.selectedEleve.value
        }
        else {
            var eleveId = ''
        }
        if ((eleveId == '') && (this.state.dateD == '') && (this.state.dateF == '')) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Remboursement.php',
                        type: 'POST',
                        data: {
                            query: this.state.filterElement,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
        else {
            // convert date debut 
            let dateD = this.state.dateD
            let month = '' + (dateD.getMonth() + 1);
            let day = '' + dateD.getDate();
            let year = dateD.getFullYear();
            if (month.length < 2)
                month = '0' + month;
            if (day.length < 2)
                day = '0' + day;
            var convertDateD = [year, month, day].join('-');
            // convert date fin
            let dateF = this.state.dateF
            let monthOne = '' + (dateF.getMonth() + 1);
            let dayOne = '' + dateF.getDate();
            let yearOne = dateF.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            var convertDateF = [yearOne, monthOne, dayOne].join('-');
            this.setState({
                pending: true
            },
                () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/RemboursementSearch.php',
                        type: 'POST',
                        data: {
                            eleveId: eleveId,
                            dateD: convertDateD,
                            dateF: convertDateF,
                            limit: this.state.nbrEltperPage,
                            page: page,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                nombreTotalElement: code_html.total,
                                data: code_html.data,
                                pending: false
                            });
                        }
                    });
                }
            )
        }
    }

    perPage = (currentRowsPerPage, currentPage) => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
