import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class ModifAutreFrais extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedScol: '1',
            idAutreFrais: '',
            mois: [{ value: 1, label: 'Janvier' }, { value: 2, label: 'FÃ©rvier' }, { value: 3, label: 'Mars' }, { value: 4, label: 'Avril' }, { value: 5, label: 'Mai' }, { value: 6, label: 'Juin' }, { value: 7, label: 'Juillet' }, { value: 8, label: 'Aout' }, { value: 9, label: 'Septembre' }, { value: 10, label: 'Octobre' }, { value: 11, label: 'Novembre' }, { value: 12, label: 'Decembre' }],
            selectedMois: '',
            description: '',
            numIns: '',
            listApprenant: [],
            selectedApprenant: '',
            nvNomApp: '',
            numTel: '',
            totalPay: 0,
            datePay: new Date(),
            tabModPay: [{ modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '', idAncienChec: '' }],
            modal: false,
            indexLignASupprimer: '',
            groupe: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            let idAutreFrais = this.props.location.state.idAutreFrais;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetAutreFraisById.php?id=' + idAutreFrais).then(res => {
                console.log(res)
                if (res.data.selectedScol == 1) {
                    this.setState({
                        idAutreFrais: res.data.idAutreFrais,
                        selectedScol: res.data.selectedScol,
                        selectedMois: res.data.mois,
                        description: res.data.description,
                        numIns: res.data.numIns,
                        selectedApprenant: res.data.apprenant,
                        numTel: res.data.numTel,
                        totalPay: res.data.montant,
                        datePay: new Date(res.data.date),
                        groupe: res.data.groupe
                    })
                    if (res.data.typePay.value == 2) {
                        this.setState({
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { modPay: res.data.typePay }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { mntModPay: Number.parseFloat(res.data.montant).toFixed(3) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { numCheque: res.data.numChec }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { echeanceChec: new Date(res.data.echeance) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { dateEncaiss: new Date(res.data.dateEncaiss) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { titulaireChec: res.data.proprietaire }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { banque: res.data.bank }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { idAncienChec: res.data.idAncienChec }) : el)),
                        });
                    }
                    else {
                        this.setState({
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { modPay: res.data.typePay }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { mntModPay: Number.parseFloat(res.data.montant).toFixed(3) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { idAncienChec: res.data.idAncienChec }) : el)),
                        });
                    }
                }
                else {
                    this.setState({
                        idAutreFrais: res.data.idAutreFrais,
                        selectedScol: res.data.selectedScol,
                        selectedMois: res.data.mois,
                        description: res.data.description,
                        numIns: res.data.numIns,
                        nvNomApp: res.data.nomApprenant,
                        numTel: res.data.numTel,
                        totalPay: res.data.montant,
                        datePay: new Date(res.data.date),
                        groupe: res.data.groupe
                    })
                    if (res.data.typePay.value == 2) {
                        this.setState({
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { modPay: res.data.typePay }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { mntModPay: Number.parseFloat(res.data.montant).toFixed(3) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { numCheque: res.data.numChec }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { echeanceChec: new Date(res.data.echeance) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { dateEncaiss: new Date(res.data.dateEncaiss) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { titulaireChec: res.data.proprietaire }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { banque: res.data.bank }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { idAncienChec: res.data.idAncienChec }) : el)),
                        });
                    }
                    else {
                        this.setState({
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { modPay: res.data.typePay }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { mntModPay: Number.parseFloat(res.data.montant).toFixed(3) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { idAncienChec: res.data.idAncienChec }) : el)),
                        });
                    }
                }
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleves.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        listApprenant: code_html
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
                console.log(res)
                this.setState({
                    listModPay: res.data
                })
            })
        }
    }

    updType = (selectedScol) => {
        this.setState({ selectedScol: selectedScol });
    }

    updMois = selectedMois => {
        this.setState({ selectedMois });
    }

    updDescript = (event) => {
        this.setState({
            description: event.target.value
        });
    }

    updNumIns = (event) => {
        this.setState({
            numIns: event.target.value
        });
    }

    updApprenant = selectedApprenant => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (selectedApprenant != null) {
            this.setState({ selectedApprenant }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetInfoInsEleve.php',
                    type: 'POST',
                    data: {
                        eleveId: selectedApprenant.value,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            numIns: code_html.numIns,
                            numTel: code_html.numTel,
                            groupe: code_html.groupe
                        });
                    }
                });
            });
        }
    };

    updNvNomApp = (event) => {
        this.setState({
            nvNomApp: event.target.value
        });
    }

    updNumTel = (event) => {
        this.setState({
            numTel: event.target.value
        });
    }

    updTotPay = (event) => {
        this.setState({
            totalPay: event.target.value
        }, () => {
            var mnt = this.state.totalPay
            this.setState({
                tabModPay: this.state.tabModPay.map((el, id) => (id === 0 ? Object.assign(el, { mntModPay: Number.parseFloat(mnt).toFixed(3) }) : el))
            });
        });
    }

    updDatePaiement = d => {
        this.setState({
            datePay: d
        });
    }

    updModPay = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
            });
        }
    }

    updMntModPay = (event, indexModPay) => {
        let mntModPay = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { mntModPay: Number.parseFloat(mntModPay).toFixed(3) }) : el))
        });
    }

    updNumChec = (event, indexModPay) => {
        let numCheque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: numCheque }) : el))
        });
    }

    updEcheanceChec = (d, indexModPay) => {
        let dateChec = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: dateChec }) : el))
        });
    }

    updDateEncaiss = (d, indexModPay) => {
        let dateEncaiss = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: dateEncaiss }) : el))
        });
    }

    updTitulaireChec = (event, indexModPay) => {
        let titulaireChec = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: titulaireChec }) : el))
        });
    }

    updBanque = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    modif = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        var userId = userPermissions[0].idUser
        // convert date seance
        let datePay = this.state.datePay
        let month = '' + (datePay.getMonth() + 1);
        let day = '' + datePay.getDate();
        let year = datePay.getFullYear();
        if (month.length < 2)
            month = '0' + month;
        if (day.length < 2)
            day = '0' + day;
        var convertDatePay = [year, month, day].join('-');
        // 
        if (this.state.selectedScol == 1) {
            jQuery.ajax({
