import React, { Component } from 'react'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { RadioGroup, Radio } from 'react-radio-group'

class AjoutEnseignant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            prenom: '',
            nom: '',
            selectedType: 'CIN',
            numSelonType: '',
            nationalite: '',
            dateDelivSelonType: new Date(),
            villeDelivreSelonType: '',
            dateNaissance: new Date(),
            lieuNaissance: '',
            adresseOfficiel: '',
            adresseTun: '',
            email: '',
            selectedGender: 'Masculin',
            mobile: '',
            tel: '',
            imageVerso: null,
            qualification: '',
            // TypePaiement: [],
            // selectedTypePaiement: '',
            tarif: ''
        }
    }

    addPrenom = (event) => {
        this.setState({
            prenom: event.target.value
        });
    }

    addNom = (event) => {
        this.setState({
            nom: event.target.value
        });
    }

    addType = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    addNumSelonType = (event) => {
        this.setState({
            numSelonType: event.target.value
        });
    }

    addNationalite = (event) => {
        this.setState({
            nationalite: event.target.value
        });
    }

    addDateLivraison = d => {
        this.setState({
            dateDelivSelonType: d
        });
    };

    addVilleDelivreSelonType = (event) => {
        this.setState({
            villeDelivreSelonType: event.target.value
        });
    }

    addDateNaissance = d => {
        this.setState({
            dateNaissance: d
        });
    };

    addLieuNaissance = (event) => {
        this.setState({
            lieuNaissance: event.target.value
        });
    }

    addAdresseOfficiel = (event) => {
        this.setState({
            adresseOfficiel: event.target.value
        });
    }

    addAdresseTun = (event) => {
        this.setState({
            adresseTun: event.target.value
        });
    }

    addEmail = (event) => {
        this.setState({
            email: event.target.value
        });
    }

    addGender = (selectedGender) => {
        this.setState({ selectedGender: selectedGender });
    }

    addMobile = (event) => {
        this.setState({
            mobile: event.target.value
        });
    }

    addTel = (event) => {
        this.setState({
            tel: event.target.value
        });
    }

    addImageVerso(e) {
        this.setState({ imageVerso: e.target.files })
    }

    addQualification = (event) => {
        this.setState({
            qualification: event.target.value
        });
    }

    // addTypePaiement = selectedTypePaiement => {
    //     this.setState({ selectedTypePaiement });
    //     console.log(`TypePaiement:`, selectedTypePaiement);
    // };

    addTarif = (event) => {
        this.setState({
            tarif: event.target.value
        });
    }

    ajout = () => {
        console.log()
        console.log()
        console.log()

        // Test si photo vide
        var pathImageVerso = '';
        console.log(this.state.imageVerso)
        if (this.state.imageVerso != null) {
            pathImageVerso = "images/Photo_Enseignants/" + this.state.imageVerso[0].name
        }
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/AddEnseignant.php',
            type: 'POST',
            data: {
                prenom: this.state.prenom,
                nom: this.state.nom,
                type: this.state.selectedType,
                numero: this.state.numSelonType,
                nationalite: this.state.nationalite,
                dateType: this.state.dateDelivSelonType,
                villeType: this.state.villeDelivreSelonType,
                dateNaissance: this.state.dateNaissance,
                lieuNaissance: this.state.lieuNaissance,
                adresseOfficiel: this.state.adresseOfficiel,
                adresseTun: this.state.adresseTun,
                email: this.state.email,
                sexe: this.state.selectedGender,
                mobile: this.state.mobile,
                tel: this.state.tel,
                photo: pathImageVerso,
                qualification: this.state.qualification,
                tarif: this.state.tarif
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Enseignants');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
        // Test si photo vide
        if (this.state.imageVerso != null) {
            let files = this.state.imageVerso;
            console.warn('data files', files)
            let reader = new FileReader();
            reader.readAsDataURL(files[0])
            reader.onload = (e) => {
                console.log(e)
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/Uploadphoto.php',
                    type: 'POST',
                    data: {
                        file: e.target.result,
                        nomfile: this.state.imageVerso[0].name
                    },
                    dataType: 'json',
                    success: function (code_html, statut) {
                    }
                }
                )
            }
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Ajout enseignant</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Prénom</label>
                                            <input type="text" class="form-control" placeholder="Prénom" onChange={this.addPrenom} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nom</label>
                                            <input type="text" class="form-control" placeholder="Nom" onChange={this.addNom} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Type</label>
                                        <RadioGroup
                                            name="fruit"
                                            selectedValue={this.state.selectedType}
                                            onChange={this.addType}>
                                            <label>
                                                <Radio value="CIN" />CIN
                                                </label>
                                            <label>
                                                <Radio value="Passeport" />Passeport
                                                </label>
                                        </RadioGroup>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Numero</label>
                                            <input type="text" class="form-control" placeholder="Numero selon le type" onChange={this.addNumSelonType} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Nationalité</label>
                                            <input type="text" class="form-control" placeholder="Nationalité" onChange={this.addNationalite} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Delivré le</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDelivSelonType}
                                                onChange={this.addDateLivraison}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Delivré ville de</label>
                                            <input type="text" class="form-control" placeholder="Delivré dans ville de" onChange={this.addVilleDelivreSelonType} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Date naissance</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateNaissance}
                                                onChange={this.addDateNaissance}
                                            />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Adresse officiel</label>
                                            <input type="text" class="form-control" placeholder="Adresse officiel" onChange={this.addAdresseOfficiel} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Adresse en tunisie</label>
                                            <input type="text" class="form-control" placeholder="Adresse en tunisie" onChange={this.addAdresseTun} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Email</label>
                                            <input type="text" class="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <label>Sexe</label>
                                        <RadioGroup
                                            name="sexe"
                                            selectedValue={this.state.selectedGender}
                                            onChange={this.addGender}>
                                            <label>
                                                <Radio value="Masculin" />Masculin
                                                </label>
                                            <label>
                                                <Radio value="Feminin" />Feminin
                                                </label>
                                        </RadioGroup>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Mobile</label>
                                            <input type="text" class="form-control" placeholder="Mobile" onChange={this.addMobile} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Tel</label>
                                            <input type="text" class="form-control" placeholder="Tel" onChange={this.addTel} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Photo verso </label>
                                            <input type="file" class="form-control" name="file" onChange={(e) => this.addImageVerso(e)} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Qualification</label>
                                            <input type="text" class="form-control" placeholder="Qualification" onChange={this.addQualification} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                    {/* <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Type Paiement</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.addTypePaiement}
                                                options={this.state.TypePaiement}
                                            />
                                        </div>
                                    </div> */}
                                    <div class="col-md-6">
                                        <div class="form-group">
                                            <label>Tarif</label>
                                            <input type="text" class="form-control" placeholder="Tarif" onChange={this.addTarif} style={{ borderRadius: '5px' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(AjoutEnseignant);
