import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import axios from 'axios'

class ExplService extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomService: '',
            pathPhotoService: '',
            tarif: ''
        }
    }

    componentDidMount() {
        let id_service = this.props.location.state.id_service;
        axios.get(process.env.REACT_APP_API_URL + 'Back_snaii/GetServiceById.php?id=' + id_service).then(res => {
            console.log(res)
            this.setState({
                nomService: res.data.nomService,
                pathPhotoService: res.data.pathPhotoService,
                tarif: res.data.tarif
            })
        })
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Informations service</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Nom service</label>
                                <input type="text" disabled Value={this.state.nomService} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                        <div class="col-md-6">
                            <div class="form-group">
                                <label>Photo service</label>
                                <a target="_blank" href={this.state.pathPhotoService}>
                                    <button type='button' class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}>Voir la photo</button>
                                </a>
                            </div>
                        </div>
                        <div class="col-md-5">
                            <div class="form-group">
                                <label>Tarif</label>
                                <input type="text" disabled Value={this.state.tarif} class="form-control" style={{backgroundColor:'white',borderRadius:'5px'}}/>
                            </div>
                        </div>
                    </div>
                    <Link to="/Services">
                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '20px' }}>
                            <button style={{ width: '15%', backgroundColor: '#0083FF', borderColor: '#0083FF', borderRadius: '5px' }} type="button" className="btn btn-success">Retour</button>
                        </div>
                    </Link>
                </div>
            </div>
        );
    }
}

export default ExplService;
