import React, { Component } from 'react'
import axios from 'axios'
import Select from 'react-select';
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'

class ExplArticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedFamille: '',
            selectedSousFamille: '',
            arrayArticle: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idSousFamilleArt = this.props.location.state.idSousFamilleArt;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetArticleById.php?id=' + idSousFamilleArt).then(res => {
                this.setState({
                    selectedFamille: res.data[0].famille,
                    selectedSousFamille: res.data[0].sousFamille,
                    arrayArticle: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Consultation article</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Articles" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Famille</label>
                                            <Select
                                                isDisabled
                                                value={this.state.selectedFamille}
                                                isClearable={true}
                                                isSearchable={true}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Sous-famille</label>
                                            <Select
                                                isDisabled
                                                value={this.state.selectedSousFamille}
                                                isClearable={true}
                                                isSearchable={true}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <table style={{ width: '100%', marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Rayon</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Code</th>
                                                <th style={{ textAlign: 'center' }} scope="col">EAN</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Libellé</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Societé</th>
                                            </tr>
                                        </thead>
                                        {this.state.arrayArticle.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td className="col-md-3"><Select
                                                        value={el.rayon}
                                                        isDisabled
                                                        isClearable={true}
                                                        isSearchable={true}
                                                    />  </td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-2"><input style={{ width: '100%' }} type="text" disabled value={el.code} /></td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-2"><input style={{ width: '100%' }} type="text" disabled value={el.ean} /></td>
                                                    <td style={{ textAlign: 'center' }} className="col-md-2"><input style={{ width: '100%' }} type="text" disabled value={el.libelle} /></td>
                                                    <td className="col-md-3"><Select
                                                        value={el.societe}
                                                        isDisabled
                                                        isClearable={true}
                                                        isSearchable={true}
                                                    />  </td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default withRouter(ExplArticle);
