    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Inscription</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <label style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '10px' }}>
                                        <span style={{ marginLeft: '15px', marginRight: '10px' }}>Apprenant introuvable dans la liste!!!! Ajouter un Apprenant</span>
                                        <Switch offColor='#FF0000' onChange={this.handleChange} checked={this.state.checked} />
                                    </label>
                                </div>
                                {this.state.checked === false ?
                                    (<Fragment>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Num d'inscription</label>
                                                    <input type="text" class="form-control" placeholder="Numro inscription" onChange={this.addNumInscrip} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Apprenant</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addApprenant}
                                                        options={this.state.listApprenants}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div style={{ display: 'grid' }} class="form-group">
                                                    <label>Photo</label>
                                                    <img style={{ width: '20%' }} src={this.state.imageVerso} alt="img" />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Date naissance</label>
                                                <input type="text" disabled class="form-control" value={this.state.dateNaissance} style={{ borderRadius: '5px' }} />
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Lieu de naissance</label>
                                                    <input type="text" disabled class="form-control" value={this.state.lieuNaissance} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nationalit</label>
                                                    <input type="text" disabled class="form-control" value={this.state.nationalite} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Type</label>
                                                    <input type="text" disabled class="form-control" value={this.state.type} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Numero</label>
                                                    <input type="text" disabled class="form-control" value={this.state.numSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Delivr le</label>
                                                <input type="text" disabled class="form-control" value={this.state.dateDelivSelonType} style={{ borderRadius: '5px' }} />
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Delivr ville de</label>
                                                    <input type="text" disabled class="form-control" value={this.state.villeDelivreSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Mobile</label>
                                                    <input type="text" disabled class="form-control" value={this.state.mobile} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tel</label>
                                                    <input type="text" disabled class="form-control" value={this.state.tel} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse en tunisie</label>
                                                    <input type="text" disabled class="form-control" value={this.state.adresseTun} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau de base</label>
                                                    <input type="text" disabled class="form-control" value={this.state.selectedNivBase.label} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom complet du pre</label>
                                                    <input type="text" class="form-control" placeholder="Nom pre" onChange={this.addNomPere} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse des parents</label>
                                                    <input type="text" class="form-control" placeholder="Adresse parents" onChange={this.addAdresseParents} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Profession</label>
                                                    <input type="text" class="form-control" placeholder="Profession" onChange={this.addProfession} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tl.Bur</label>
                                                    <input type="text" class="form-control" placeholder="Tel bureau" onChange={this.addTelBureau} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Filire</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFiliere}
                                                        options={this.state.listFilieres}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Specialit</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addSpecialite}
                                                        options={this.state.listSpecialites}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Formation</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFormation}
                                                        options={this.state.listFormations}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNiveau}
                                                        options={this.state.listNiveaux}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Groupe</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addGroupe}
                                                        options={this.state.listGroupes}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div style={{ marginBottom: '20px' }} class="row">
                                            <div class="col-md-6">
                                                <label>Papiers Apportes</label>
                                                <span style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', border: '1px solid', padding: '10px', borderRadius: '5px' }}>
                                                    {this.state.listPapiers.map((el, index) =>
                                                        <label>
                                                            <Checkbox
                                                                onChange={(e) => this.addPapiers(e, index)}
                                                                disabled={this.state.disabled}
                                                            />
                                                            {el.papier}
                                                        </label>
                                                    )}
                                                </span>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Paiement</label>
                                                <RadioGroup
                                                    name="typePay"
                                                    selectedValue={this.state.selectedTypePay}
                                                    onChange={this.addTypePay}>
                                                    <div style={{ border: '1px solid', borderRadius: '5px', height: '72px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px' }}>
                                                        {this.state.listTypePay.map(el =>
                                                            <div>
                                                                <label>
                                                                    <Radio value={el.type} />{el.type}
                                                                </label>
                                                            </div>
                                                        )}
                                                    </div>
                                                </RadioGroup>
                                            </div>
                                        </div>
                                    </Fragment>) : (null)}
                                {this.state.checked === true ?
                                    (<Fragment>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Prnom</label>
                                                    <input type="text" class="form-control" placeholder="Prnom" onChange={this.addPrenom} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom</label>
                                                    <input type="text" class="form-control" placeholder="Nom" onChange={this.addNom} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Sexe</label>
                                                <RadioGroup
                                                    name="sexe"
                                                    selectedValue={this.state.selectedGender}
                                                    onChange={this.addGender}>
                                                    <label>
                                                        <Radio value="Masculin" />Masculin
                                                    </label>
                                                    <label>
                                                        <Radio value="Feminin" />Feminin
                                                    </label>
                                                </RadioGroup>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Type</label>
                                                <RadioGroup
                                                    name="type"
                                                    selectedValue={this.state.selectedType}
                                                    onChange={this.addType}>
                                                    <label>
                                                        <Radio value="CIN" />CIN
                                                    </label>
                                                    <label>
                                                        <Radio value="Passeport" />Passeport
                                                    </label>
                                                </RadioGroup>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Numero</label>
                                                    <input type="text" class="form-control" placeholder="Numero selon le type" onChange={this.addNumSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nationalit</label>
                                                    <input type="text" class="form-control" placeholder="Nationalit" onChange={this.addNationalite} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Delivr le</label>
                                                <div class="form-group">
                                                    <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                        selected={this.state.dateDelivSelonType}
                                                        onChange={this.addDateLivraison}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Delivr ville de</label>
                                                    <input type="text" class="form-control" placeholder="Delivr dans ville de" onChange={this.addVilleDelivreSelonType} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Date naissance</label>
                                                <div class="form-group">
                                                    <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                        selected={this.state.dateNaissance}
                                                        onChange={this.addDateNaissance}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Lieu de naissance</label>
                                                    <input type="text" class="form-control" placeholder="Lieu de naissance" onChange={this.addLieuNaissance} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse officiel</label>
                                                    <input type="text" class="form-control" placeholder="Adresse officiel" onChange={this.addAdresseOfficiel} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse en tunisie</label>
                                                    <input type="text" class="form-control" placeholder="Adresse en tunisie" onChange={this.addAdresseTun} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Email</label>
                                                    <input type="text" class="form-control" placeholder="Email" onChange={this.addEmail} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Mobile</label>
                                                    <input type="text" class="form-control" placeholder="Mobile" onChange={this.addMobile} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tel</label>
                                                    <input type="text" class="form-control" placeholder="Tel" onChange={this.addTel} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau de base</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNivBase}
                                                        options={this.state.listNivBase}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Photo verso </label>
                                                    <input type="file" class="form-control" name="file" onChange={(e) => this.addImageVerso(e)} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Nom complet du pre</label>
                                                    <input type="text" class="form-control" placeholder="Nom pre" onChange={this.addNomPere} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Adresse des parents</label>
                                                    <input type="text" class="form-control" placeholder="Adresse parents" onChange={this.addAdresseParents} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Profession</label>
                                                    <input type="text" class="form-control" placeholder="Profession" onChange={this.addProfession} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Tl.Bur</label>
                                                    <input type="text" class="form-control" placeholder="Tel bureau" onChange={this.addTelBureau} style={{ borderRadius: '5px' }} />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Filire</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFiliere}
                                                        options={this.state.listFilieres}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Specialit</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addSpecialite}
                                                        options={this.state.listSpecialites}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Formation</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addFormation}
                                                        options={this.state.listFormations}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Niveau</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addNiveau}
                                                        options={this.state.listNiveaux}
                                                    />
                                                </div>
                                            </div>
                                            <div class="col-md-6">
                                                <div class="form-group">
                                                    <label>Groupe</label>
                                                    <Select
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={this.addGroupe}
                                                        options={this.state.listGroupes}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div style={{ marginBottom: '20px' }} class="row">
                                            <div class="col-md-6">
                                                <label>Papiers Apportes</label>
                                                <span style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', border: '1px solid', padding: '10px', borderRadius: '5px' }}>
                                                    {this.state.listPapiers.map(el =>
                                                        <label>
                                                            <Checkbox
                                                                onChange={this.addPapiers}
                                                                disabled={this.state.disabled}
                                                            />
                                                            {el.papier}
                                                        </label>
                                                    )}
                                                </span>
                                            </div>
                                            <div class="col-md-6">
                                                <label>Paiement</label>
                                                <RadioGroup
                                                    name="typePay"
                                                    selectedValue={this.state.selectedTypePay}
                                                    onChange={this.addTypePay}>
                                                    <div style={{ border: '1px solid', borderRadius: '5px', height: '72px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px' }}>
                                                        {this.state.listTypePay.map(el =>
                                                            <div>
                                                                <label>
                                                                    <Radio value={el.type} />{el.type}
                                                                </label>
                                                            </div>
                                                        )}
                                                    </div>
                                                </RadioGroup>
                                            </div>
                                        </div>
                                    </Fragment>) : (null)}

                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Inscription);
