import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            barreNavigation: [{ nomElement: 'Habilitation', arraySousElement: [{ nomSousElement: 'Gestion role', link: '/Roles',permission:0 }, { nomSousElement: 'Gestion utilisateur', link: '/Users',permission:0 }],permiss:0,icon:'fas fa-chevron-left' }, { nomElement: 'Scolarite', arraySousElement: [{ nomSousElement: 'Matieres par formation', link: '/MatieresFormat',permission:0 }, { nomSousElement: 'Papiers Apportées', link: '/Papiers',permission:0 }, { nomSousElement: 'Frais formation', link: '/FraisFormation',permission:0 }, { nomSousElement: 'Niveaux', link: '/Niveaux',permission:0 }, { nomSousElement: 'Formations', link: '/Formations',permission:0 }, { nomSousElement: 'Specialités', link: '/Specialites',permission:0 }, { nomSousElement: 'Matieres', link: '/Matieres',permission:0 }, { nomSousElement: 'Filiéres', link: '/Filieres',permission:0 }, { nomSousElement: 'Niveau de base', link: '/NivBase',permission:0 }, { nomSousElement: 'Type enseignement', link: '/TypeEnseign',permission:0 }],permiss:0,icon:'fas fa-chevron-left' }, { nomElement: 'Eleve', arraySousElement: [{ nomSousElement: 'Apprenants', link: '/Apprenants',permission:0 }, { nomSousElement: 'Groupes', link: '/Groupes',permission:0 }, { nomSousElement: 'Types paiements eleves', link: '/TypesPaiementEleve',permission:0 }, { nomSousElement: 'Reglements eleves', link: '/ReglApprenant',permission:0 }],permiss:0,icon:'fas fa-chevron-left' }, { nomElement: 'Formateur', arraySousElement: [{ nomSousElement: 'Enseignants', link: '/Enseignants',permission:0 }, { nomSousElement: 'Paiement enseignants', link: '/ModPaiEnseignants',permission:0 }],permiss:0,icon:'fas fa-chevron-left' }]
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Menu</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    {this.state.barreNavigation.map((el, index) =>
                                        <div className="col-md-4">
                                            <div className="white-box">
                                                <h3><i class={el.icon}></i>{el.nomElement}</h3>
                                                <div className="myadmin-dd dd" id="nestable">
                                                    <ol className="dd-list">
                                                        <li className="dd-item" data-id={index}>
                                                            {el.arraySousElement.map(ele =>
                                                                <Link to={ele.link}><div className="dd-handle">{ele.nomSousElement}</div></Link>)}
                                                        </li>
                                                    </ol>
                                                </div>
                                            </div>
                                        </div>)}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Menu;
