import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import Checkbox from 'rc-checkbox';
import 'rc-checkbox/assets/index.css';
import { Fragment } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class ConfirmationCycle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nomCycle: '',
            nbrHeure: '',
            tarifCycle: '',
            tarifAvecCarte: '',
            // type: ["Individuelle"],
            selectedType: '',
            anneScol: '',
            mois: [{ label: 'Janvier', value: '01' }, { label: 'Février', value: '02' }, { label: 'Mars', value: '03' }, { label: 'Avril', value: '04' }, { label: 'Mai', value: '05' }, { label: 'Juin', value: '06' }, { label: 'Juillet', value: '07' }, { label: 'Aout', value: '08' }, { label: 'Septembre', value: '09' }, { label: 'Octobre', value: '10' }, { label: 'November', value: '11' }, { label: 'Decembre', value: '12' }],
            enseignants: [],
            arrayCycle: [],
            indexLignEnseignASupprimer: '',
            indexSousLignEnseignASupprimer: '',
            modalEnseign: false
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            let idCycle = this.props.location.state.idCycle;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetCycleById.php?id=' + idCycle).then(res => {
                console.log(res)
                this.setState({
                    nomCycle: res.data[0].cycle,
                    nbrHeure: res.data[0].heure,
                    tarifCycle: res.data[0].tarif,
                    tarifAvecCarte: res.data[0].carte,
                    arrayCycle: res.data[0].detailCycle
                })
            })
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetAnneeScolaire.php',
                type: 'POST',
                data: {
                    anneeScolaire: anneeScolaire
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        anneScol: code_html.scolaire
                    });
                }
            });
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectCours.php').then(res => {
                console.log(res)
                this.setState({
                    cours: res.data
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEnseignants.php').then(res => {
                console.log(res)
                this.setState({
                    enseignants: res.data
                })
            })
        }
    }

    addEnseignent = (selectedEnseignent, index) => {

    }

    addNbrHeureParEnseign = (e, index) => {

    }

    addHonoraireParEnseign = (e, index) => {

    }

    additionEnseignant = (index) => {
        var x = this.state.arrayCycle[index].enseignant
        let element = { selectedEnseignant: '' }
        x.push(element)
        this.setState({
            arrayCycle: this.state.arrayCycle.map((el, id) => (id === index ? Object.assign(el, { enseignant: x }) : el))
        });
    }

    toggleLignEnseign = (index, indice) => {
        this.setState(prevState => ({
            modalEnseign: !prevState.modalEnseign,
            indexLignEnseignASupprimer: index,
            indexSousLignEnseignASupprimer: indice
        }));
    }

    deleteLignEnseign = () => {
        this.state.arrayCycle[this.state.indexLignEnseignASupprimer].enseignant.splice(this.state.indexSousLignEnseignASupprimer, 1)
        this.setState(prevState => ({
            modalEnseign: !prevState.modalEnseign
        }));
    }

    nonDeleteLignEnseign = () => {
        this.setState(prevState => ({
            modalEnseign: !prevState.modalEnseign
        }));
    }

    modif = () => {
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Confirmation cycle</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Cycle</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="text" disabled className="form-control" value={this.state.nomCycle} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Heure</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="number" disabled className="form-control" value={this.state.nbrHeure} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tarif cycle</label>
                                            <input style={{ backgroundColor: 'white', borderRadius: '5px', border: 'solid 1px #B3B3B3' }} type="number" disabled className="form-control" value={this.state.tarifCycle} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Tarif avec carte</label>
