import React, { Component } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter } from 'react-router-dom'
import axios from 'axios'
import jQuery from 'jquery'

class Permissions extends Component {
    constructor(props) {
        super(props);
        this.state = {
            services: [],
            permissions: [],
            role: ''
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/Services.php').then(res => {
            this.setState({ services: res.data });
        });
        let idRole = this.props.location.state.idRole;
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetPermissionByRoleId.php?id=' + idRole).then(res => {
            this.setState({
                role: res.data[0].role,
                permissions: res.data
            },
                function () {
                    var long = this.state.permissions.length
                    var ajout = document.getElementsByClassName('add')
                    console.log(ajout)
                    var addCan = []
                    var explorer = document.getElementsByClassName('view')
                    console.log(explorer)
                    var readCan = []
                    var modifier = document.getElementsByClassName('Update')
                    console.log(modifier)
                    var updateCan = []
                    var supprimer = document.getElementsByClassName('delet')
                    console.log(supprimer)
                    var deleteCan = []

                    for (var i = 0; i < long; i++) {
                        addCan.push(this.state.permissions[i].ajouter)
                        readCan.push(this.state.permissions[i].explorer)
                        updateCan.push(this.state.permissions[i].modifier)
                        deleteCan.push(this.state.permissions[i].supprimer)
                    }

                    console.log('le tableau addCan')
                    console.log(addCan)
                    console.log('le tableau readCan')
                    console.log(readCan)
                    console.log('le tableau updateCan')
                    console.log(updateCan)
                    console.log('le tableau deleteCan')
                    console.log(deleteCan)

                    for (var j = 0; j < long; j++) {
                        if (addCan[j] == 1) {
                            ajout[j].checked = true
                        }
                        else if (addCan[j] == 0) {
                            ajout[j].checked = false
                        }
                        if (readCan[j] == 1) {
                            explorer[j].checked = true
                        }
                        else if (readCan[j] == 0) {
                            explorer[j].checked = false
                        }
                        if (updateCan[j] == 1) {
                            modifier[j].checked = true
                        }
                        else if (updateCan[j] == 0) {
                            modifier[j].checked = false
                        }
                        if (deleteCan[j] == 1) {
                            supprimer[j].checked = true
                        }
                        else if (deleteCan[j] == 0) {
                            supprimer[j].checked = false
                        }
                    }
                }
            );
        });
    }

    modif = () => {
        ///
        var canAdd = [0, 0, 0, 0, 0, 0, 0, 0, 0];
        var canRead = [0, 0, 0, 0, 0, 0, 0, 0, 0];
        var canUpdate = [0, 0, 0, 0, 0, 0, 0, 0, 0];
        var canDelete = [0, 0, 0, 0, 0, 0, 0, 0, 0];
        ///
        var ajout = document.getElementsByClassName('add')
        var explorer = document.getElementsByClassName('view')
        var modifier = document.getElementsByClassName('Update')
        var supprimer = document.getElementsByClassName('delet')
        var longeur = this.state.services.length
        var tabPermission = []

        for (var i = 0; i < longeur; i++) {
            if (ajout[i].checked) {
                canAdd[i] = 1
            }
            else if (ajout[i].checked === false) {
                canAdd[i] = 0
            }
            if (explorer[i].checked) {
                canRead[i] = 1
            }
            else if (explorer[i].checked === false) {
                canRead[i] = 0
            }
            if (modifier[i].checked) {
                canUpdate[i] = 1
            }
            else if (modifier[i].checked === false) {
                canUpdate[i] = 0
            }
            if (supprimer[i].checked) {
                canDelete[i] = 1
            }
            else if (supprimer[i].checked === false) {
                canDelete[i] = 0
            }

            var idRole = this.props.location.state.idRole;
            var objetPermission = { service_id: this.state.services[i].id, role_id: idRole, ajouter: canAdd[i], explorer: canRead[i], modifier: canUpdate[i], supprimer: canDelete[i] }
            console.log(objetPermission)
            tabPermission.push(objetPermission)
        }
        console.log(tabPermission)

        // jQuery.ajax({
        //     url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdPermissionByRoleId.php?id=' + idRole,
        //     type: 'POST',
        //     data: {
        //         tabPermission: tabPermission
        //     },
        //     dataType: 'json',
        //     success: function (code_html, statut) {
        //         if (code_html.Result === "OK") {
        //             this.props.history.push('/Roles');
        //         }
        //         else if (code_html.Result === "KO") {
        //             alert("erreur,l'un de vos champs est vide!!");
        //         }
        //     }
        // });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification permissions</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginLeft: '5px' }}>
                                <h4 style={{ fontSize: '20px' }}>Role : {this.state.role}</h4>
                            </div>
                            <div class="white-box">
                                <div class="row">
                                    <div className="table-responsive" style={{ textAlign: 'center' }}>
                                        <table className="table align-items-center table-flush">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Services</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Ajouter</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Explorer</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Modifier</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Supprimer</th>
                                                </tr>
                                            </thead>
                                            {this.state.services.map(el =>
                                                <tbody>
                                                    <tr>
                                                        <td>
                                                            {el.service}
                                                        </td>
                                                        <td>
                                                            <input class='add' type="checkbox" />
                                                        </td>
                                                        <td>
                                                            <input class='view' type="checkbox" />
                                                        </td>
                                                        <td>
                                                            <input class='Update' type="checkbox" />
                                                        </td>
                                                        <td>
                                                            <input class='delet' type="checkbox" />
                                                        </td>
                                                    </tr>
                                                </tbody>
                                            )}
                                        </table>
                                    </div>
                                    <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '10px' }}>
                                        <button onClick={this.modif} style={{ width: '15%' }} type="button" className="btn btn-success">Confirmer</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Permissions;
