import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class Planning extends Component {
    constructor(props) {
        super(props);
        this.state = {
            marchandiseurs: [],
            selectedMarchandiseur: '',
            dateDebut: '',
            dateFin: '',
            magasins: [],
            modal: false,
            arrayMarchMag: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectMarchandiseurs.php').then(res => {
                this.setState({
                    marchandiseurs: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    addDateDebut = d => {
        this.setState({
            dateDebut: d
        });
    };

    addDateFin = d => {
        this.setState({
            dateFin: d
        });
    };

    planif = () => {
        if (this.state.dateDebut != "" && this.state.dateFin != "") {
            // date debut
            var dateDebut = this.state.dateDebut
            let monthOne = '' + (dateDebut.getMonth() + 1);
            let dayOne = '' + dateDebut.getDate();
            let yearOne = dateDebut.getFullYear();
            if (monthOne.length < 2)
                monthOne = '0' + monthOne;
            if (dayOne.length < 2)
                dayOne = '0' + dayOne;
            // date fin
            var convertDateDebut = [yearOne, monthOne, dayOne].join('-');
            var dateFin = this.state.dateFin
            let monthTwo = '' + (dateFin.getMonth() + 1);
            let dayTwo = '' + dateFin.getDate();
            let yearTwo = dateFin.getFullYear();
            if (monthTwo.length < 2)
                monthTwo = '0' + monthTwo;
            if (dayTwo.length < 2)
                dayTwo = '0' + dayTwo;
            var convertDateFin = [yearTwo, monthTwo, dayTwo].join('-');
            // Différence entre 2 dates
            var date1 = new Date(convertDateDebut);
            var date2 = new Date(convertDateFin);
            var time_diff = date2.getTime() - date1.getTime();
            var days_Diff = time_diff / (1000 * 3600 * 24);
            if (days_Diff == 7) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_magasin/Planning.php',
                    type: 'POST',
                    data: {
                        dateDebut: convertDateDebut,
                        dateFin: convertDateFin
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            arrayMarchMag: code_html
                        });
                    }
                });
            }
            else {
                toast.error("⛔ Veuillez choisir une periode d'une semaine !!", { containerId: 'A' });
            }
        }
        else {
            toast.error('⛔ Veuillez Verifier choisir une date debut et fin !!', { containerId: 'A' });
        }
    }

    addMarchandiseur = (selectedMarchandiseur, index, indice) => {
        if (selectedMarchandiseur != null) {
            var marchandiseur = selectedMarchandiseur;
            var arrayChange = this.state.arrayMarchMag
            arrayChange[index].ligneData[indice].marchandiseur = marchandiseur
            this.setState({
                arrayMarchMag: arrayChange
            }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_magasin/SelectMagasinsByMarch.php',
                    type: 'POST',
                    data: {
                        marchandiseurId: selectedMarchandiseur.value
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            magasins: code_html
                        });
                    }
                });
            });
        }
        else {
            this.setState({
                magasins: []
            });
        }
    }

    addMagasin = (selectedMagasin, index, indice) => {
        var magasin = selectedMagasin;
        var arrayChange = this.state.arrayMarchMag
        arrayChange[index].ligneData[indice].magasin = magasin
        this.setState({
            arrayMarchMag: arrayChange
        });
    }

    additionLigne = (index) => {
        var allArray = this.state.arrayMarchMag
        var arrayIndex = this.state.arrayMarchMag[index].ligneData
        var el = { marchandiseur: '', magasin: '' }
        arrayIndex.push(el)
        allArray[index].ligneData = arrayIndex
        this.setState({
            arrayMarchMag: allArray
        });
    }

    deleteLigne = (index, indice) => {
        var allArray = this.state.arrayMarchMag
        var arrayIndex = this.state.arrayMarchMag[index].ligneData
        arrayIndex.splice(indice, 1);
        allArray[index].ligneData = arrayIndex
        this.setState({
            arrayMarchMag: allArray
        });
    }

    ajout = () => {

    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Planning</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date debut</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateDebut}
                                                onChange={this.addDateDebut}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date fin</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateFin}
                                                onChange={this.addDateFin}
                                                dateFormat="dd/MM/yyy"
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center' }}>
                                    <div>
                                        <button style={{ borderRadius: '5px', backgroundColor: '#761C19', borderColor: '#761C19' }} type="button" className="btn btn-success" onClick={this.planif}>Planifier</button>
                                    </div>
                                </div>
                                {this.state.arrayMarchMag.length > 0 ?
                                    (<div style={{ marginTop: '10px' }} className="row">
                                        <table className="table table-bordered">
                                            <thead >
                                                <tr>
                                                    <th style={{ textAlign: 'center' }} scope="col">Date</th>
                                                    <th style={{ textAlign: 'center' }} scope="col">Marchandiseur-Magasins</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.arrayMarchMag.map((el, index) =>
                                                    <tr>
                                                        <td style={{ textAlign: 'center' }} className="col-md-2">
                                                            {el.date}
                                                        </td>
                                                        <td style={{ textAlign: 'center' }} className="col-md-10">
                                                            <div>
                                                                <table className="table table-bordered">
                                                                    <thead>
                                                                        <tr>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Marchandiseur</th>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Magasins</th>
                                                                            <th style={{ textAlign: 'center' }} scope="col">Supp</th>
                                                                        </tr>
                                                                    </thead>
                                                                    <tbody>
                                                                        {this.state.arrayMarchMag[index].ligneData.map((el, indice) =>
                                                                            <tr>
                                                                                <td className="col-md-5" style={{ textAlign: 'center' }}>
                                                                                    <div>
                                                                                        <Select
                                                                                            isClearable={true}
                                                                                            isSearchable={true}
                                                                                            onChange={(e) => this.addMarchandiseur(e, index, indice)}
                                                                                            options={this.state.marchandiseurs}
                                                                                        />
                                                                                    </div>
                                                                                </td>
                                                                                <td className="col-md-5" style={{ textAlign: 'center' }}>
                                                                                    <div>
                                                                                        <Select
                                                                                            isMulti={true}
                                                                                            isClearable={true}
                                                                                            isSearchable={true}
                                                                                            onChange={(e) => this.addMagasin(e, index, indice)}
                                                                                            options={this.state.magasins}
                                                                                        />
                                                                                    </div>
                                                                                </td>
                                                                                <td className="col-md-2">
                                                                                    <button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.deleteLigne(index, indice)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button>
                                                                                </td>
                                                                            </tr>
                                                                        )}
                                                                    </tbody>
                                                                </table>
                                                            </div>
                                                        Ajout
                                                        <i onClick={() => this.additionLigne(index)} className="fas fa-plus-circle"></i>
                                                        </td>
                                                    </tr>
                                                )}
                                            </tbody>
                                        </table>
                                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                            <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                        </div>
                                    </div>) :
                                    (null)}
                            </div>
                            <Modal isOpen={this.state.modal} toggle={this.nonDeleteLignEnseign} className={this.props.className} fade={false}>
                                <ModalHeader toggle={this.nonDeleteLignEnseign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                <ModalBody>
                                    <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <p>Êtes-Vous sûr de vouloir supprimer cette ligne ?</p>
                                        </div>
                                        <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                            <div><button onClick={() => this.deleteLignEnseign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                            <div><button onClick={() => this.nonDeleteLignEnseign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                        </div>
                                    </div>
                                </ModalBody>
                            </Modal>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(Planning);
