import React, { Component } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import Select from 'react-select';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';

class ModifSousFamille extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idSousFamilleArt: '',
            sousFamille: '',
            familles: [],
            selectedFamille: '',
            code: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var idSousFamilleArt = this.props.location.state.idSousFamilleArt;
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/GetSousFamilleById.php?id=' + idSousFamilleArt).then(res => {
                this.setState({
                    idSousFamilleArt: idSousFamilleArt,
                    selectedFamille: res.data.famille,
                    sousFamille: res.data.sousFamille,
                    code: res.data.code
                })
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_magasin/SelectFamilles.php').then(res => {
                console.log(res)
                this.setState({
                    familles: res.data
                })
            })
        }
        else {
            document.location = '/'
        }
    }

    updFamille = selectedFamille => {
        this.setState({ selectedFamille: selectedFamille });
    };

    updSousFamille = (event) => {
        this.setState({
            sousFamille: event.target.value
        });
    }

    updCode = (event) => {
        this.setState({
            code: event.target.value
        });
    }

    ajout = () => {
        if (this.state.selectedFamille != '') {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_magasin/UpdSousFamille.php',
                type: 'POST',
                data: {
                    familleId: this.state.selectedFamille.value,
                    sousFamille: this.state.sousFamille,
                    code: this.state.code
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    if (code_html.Result == "OK") {
                        this.props.history.push('/SousFamilleArt');
                    }
                    else if (code_html.Result == "KO") {
                        alert("erreur,l'un de vos champs est vide!!");
                    }
                }
            });
        }
        else {
            toast.error('â›” veuillez verifier le champs famille !!', { containerId: 'A' });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification sous-famille</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/SousFamilleArt" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Famille</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updFamille}
                                                options={this.state.familles}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Sous-famille</label>
                                            <input type="text" className="form-control" placeholder="Nom" onChange={this.updSousFamille} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Code</label>
                                            <input type="text" className="form-control" placeholder="Nom" onChange={this.updCode} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
                                    <button style={{ borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.ajout}>Confirmer</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                </div>
            </div>
        );
    }
}

export default withRouter(ModifSousFamille);
