import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import axios from 'axios'
import jQuery from 'jquery'
import Select from 'react-select';

class ImpayeMois extends Component {
    constructor(props) {
        super(props);
        this.state = {
            arrayElvImp: [],
            mois: [{ value: 1, label: 'Janvier' }, { value: 2, label: 'Férvier' }, { value: 3, label: 'Mars' }, { value: 4, label: 'Avril' }, { value: 5, label: 'Mai' }, { value: 6, label: 'Juin' }, { value: 7, label: 'Juillet' }, { value: 8, label: 'Aout' }, { value: 9, label: 'Septembre' }, { value: 10, label: 'Octobre' }, { value: 11, label: 'Novembre' }, { value: 12, label: 'Decembre' }],
            selectedMois: ''
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
        }
        else {
            document.location = '/'
        }
    }

    searchMois = selectedMois => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        if (selectedMois != null) {
            this.setState({ selectedMois: selectedMois }, () => {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/ImpayerMens.php',
                    type: 'POST',
                    data: {
                        anneeScolaire: anneeScolaire,
                        mois: selectedMois.value
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        this.setState({
                            arrayElvImp: code_html
                        });
                    }
                });
            });
        }
        else {
            this.setState({
                selectedMois: '',
                arrayElvImp: []
            });
        }
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Les impayés étudiants</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', marginBottom: '10px', marginRight: '20px' }}>
                                    <Link to="/Menu" > <button style={{ background: '#DC143C', border: '1px solid #DC143C', borderRadius: '5px' }} type="button" className="btn btn-info"> <i style={{ paddingRight: '5px' }} className="fas fa-arrow-circle-left"></i>Retour</button></Link>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Mois</label>
                                            <Select
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={(e) => this.searchMois(e)}
                                                options={this.state.mois}
                                            />
                                        </div>
                                    </div>
                                    {this.state.arrayElvImp.length > 0 ?
                                        (<Fragment>
                                            <h3>Impayer par mois :</h3>
                                            <table style={{ marginTop: '10px' }} className="table table-bordered">
                                                <thead>
                                                    <tr>
                                                        <th scope="col" style={{ textAlign: 'center' }}>N° inscription</th>
                                                        <th scope="col" style={{ textAlign: 'center' }}>Etudiant</th>
                                                        <th scope="col" style={{ textAlign: 'center' }}>N° tranche</th>
                                                        <th scope="col" style={{ textAlign: 'center' }}>Reste</th>
                                                    </tr>
                                                </thead>
                                                {this.state.arrayElvImp.map(el =>
                                                    <tbody>
                                                        <tr>
                                                            <td style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>{el.numIns}</td>
                                                            <td style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>{el.nomComplet}</td>
                                                            <td style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>{el.numTrch}</td>
                                                            <td style={{ textAlign: 'center', color: '#337AB7', fontWeight: 'bold' }}>{el.reste}</td>
                                                        </tr>
                                                    </tbody>
                                                )}
                                            </table>
                                        </Fragment>) : (<div><h3>Impayer par mois :</h3>
                                            <p>Tous les éleves ont payé cette tranche</p></div>
                                        )}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default ImpayeMois;
