import React, { Component, Fragment } from 'react'
import { Link, BrowserRouter as Router, Route, Switch, withRouter, HashRouter, Redirect } from 'react-router-dom'
import DataTable, { createTheme } from 'react-data-table-component';
import axios from 'axios'
import jQuery from 'jquery'
import './paginate.css'
import Collapsible from 'react-collapsible';
import Select from 'react-select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class RapportJour extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            columns: [
                {
                    name: 'Date',
                    selector: 'date',
                    sortable: false,
                    center: true,
                    style: { width: '20%' }
                },
                {
                    name: 'Marchandiseurs-Magasins',
                    cell: row =>
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th className="col-md-4" style={{ textAlign: 'center' }} scope="col">Marchandiseurs</th>
                                    <th className="col-md-8" style={{ textAlign: 'center' }} scope="col">Magasins</th>
                                </tr>
                            </thead>
                            {row.detailMarch.map((el, index) =>
                                <tbody>
                                    <tr>
                                        <td className="col-md-4" style={{ textAlign: 'center' }}><p>{el.marchandiseur}</p></td>
                                        <td className="col-md-8" style={{ textAlign: 'center' }}>
                                            <Collapsible trigger={<button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }}><i style={{ color: 'white' }} className="fas fa-info"></i></button>}>
                                                <table className="table table-bordered">
                                                    {el.magasin.map(ele =>
                                                        <tbody>
                                                            <tr>
                                                                <td>{ele.nomMag}</td>
                                                                <td><Link to={{
                                                                    pathname: '/ExplDispoMag',
                                                                    state: { idRappDispo: ele.idRappDispo }
                                                                }}><button className='buttonExplorer'><i style={{ color: 'white' }} className="far fa-eye"></i></button></Link></td>
                                                            </tr>
                                                        </tbody>
                                                    )}
                                                </table>
                                            </Collapsible>
                                        </td>
                                    </tr>
                                </tbody>
                            )}
                        </table>
                    ,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                    width: '80%'
                }
            ],
            nombreTotalElement: 0,
            filterElement: '',
            mycurrentPage: 1,
            resetFirstPage: false,
            nbrEltperPage: 5,
            pending: true,
            dateDebut: '',
            dateFin: '',
            marchandiseurs: [],
            selectedMarchandiseur: ''
        }
        this.newElement = this.newElement.bind(this);
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            jQuery.ajax({
