import React, { Component } from "react";
import jQuery from "jquery";
import Select from "react-select";
import "react-toastify/dist/ReactToastify.css";
import DataTable from "react-data-table-component";
import { Link } from "react-router-dom";

class AjoutNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      columns: [
        {
          name: "Eleve",
          selector: "nomEleve",
          sortable: false,
          center: true,
        },
        {
          name: "Modifier",
          cell: (row) => (
            <Link
              to={{
                pathname: "/ModifNoteMatiere",
                state: { idNote: row.idNote },
              }}
            >
              <button className="buttonModifier">
                <i style={{ color: "white" }} className="fas fa-pencil-alt"></i>
              </button>
            </Link>
          ),
          ignoreRowClick: true,
          allowOverflow: true,
          button: true,
        },
      ],
      listNiveau: [],
      listGroupes: [],
      listMatieres: [],
      listMatieresPratiques: [],
      listNatureNote: [],
      listTrimestre: [
        { value: 1, label: "Trimestre 1" },
        { value: 2, label: "Trimestre 2" },
        { value: 3, label: "Trimestre 3" },
      ],
      listEleves: [],
      selectedGroupe: "",
      selectedNiveau: "",
      selectedMatieres: "",
      selectedMatieresPratiques: "",
      selectedTrimestre: "",
      blockListEleve: false,
      blockSousMatiere: false,
      dateAjoutNote: new Date(),
      nombreTotalElement: 0,
      filterElement: "",
      mycurrentPage: 1,
      resetFirstPage: false,
      nbrEltperPage: 5,
      modal: false,
      pending: true,
    };
    this.newElement = this.newElement.bind(this);
    this.filterSearch = this.filterSearch.bind(this);
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("userPermissions")) != null) {
      var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
      var anneeScolaire = userPermissions[0].anneeScolaire;
      jQuery.ajax({
        url:
          process.env.REACT_APP_API_URL +
          "Back_mediatraining/SelectNiveauNonBts.php",
        type: "POST",
        data: {
          anneeScolaire: anneeScolaire,
        },
        dataType: "json",
        success: (code_html, statut) => {
          this.setState({
            listNiveau: code_html,
          });
        },
      });
    } else {
      document.location = "/";
    }
  }
  newElement(page, totalRows) {
    this.setState(
      {
        pending: true,
      },
      () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/ListNoteNonBts.php",
          type: "POST",
          data: {
            groupeId: this.state.selectedGroup.value,
            query: this.state.filterElement,
            limit: this.state.nbrEltperPage,
            page: page,
          },
          dataType: "json",
          success: (code_html, statut) => {
            console.log(code_html);
            this.setState({
              nombreTotalElement: code_html.total,
              data: code_html.data,
              pending: false,
            });
          },
        });
      }
    );
  }

  perPage(currentRowsPerPage, currentPage) {
    this.setState(
      {
        nbrEltperPage: currentRowsPerPage,
        pending: true,
      },
      () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/ListNoteNonBts.php",
          type: "POST",
          data: {
            groupeId: this.state.selectedGroup.value,
            query: this.state.filterElement,
            limit: currentRowsPerPage,
            page: currentPage,
          },
          dataType: "json",
          success: (code_html, statut) => {
            console.log(code_html);
            this.setState({
              nombreTotalElement: code_html.total,
              data: code_html.data,
              pending: false,
            });
          },
        });
      }
    );
  }

  filterSearch(event) {
    this.setState(
      {
        filterElement: event.target.value,
        resetFirstPage: !this.state.resetFirstPage,
        pending: true,
      },
      () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/ListNoteNonBts.php",
          type: "POST",
          data: {
            query: this.state.filterElement,
            limit: this.state.nbrEltperPage,
            page: 1,
          },
          dataType: "json",
          success: (code_html, statut) => {
            console.log(code_html);
            this.setState({
              nombreTotalElement: code_html.total,
              data: code_html.data,
              pending: false,
            });
          },
        });
      }
    );
  }

  addNiveau = (selectedNiveau) => {
    var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
    var anneeScolaire = userPermissions[0].anneeScolaire;
    if (selectedNiveau != null) {
      this.setState({ selectedNiveau }, () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/SelectedGroupe.php",
          type: "POST",
          data: {
            niveauId: selectedNiveau.value,
            anneeScolaire: anneeScolaire,
          },
          dataType: "json",
          success: (code_html, statut) => {
            this.setState({
              listGroupes: code_html,
            });
          },
        });
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/SelectedMatieres.php",
          type: "POST",
          data: {
            niveauId: selectedNiveau.value,
            anneeScolaire: anneeScolaire,
          },
          dataType: "json",
          success: (code_html, statut) => {
            this.setState({
              listMatieres: code_html,
            });
          },
        });
      });
    } else {
      this.setState({
        selectedGroupe: "",
        selectedNiveau: "",
        selectedMatieres: "",
        selectedMatieresPratiques: "",
      });
    }
  };

  addGroup = (selectedGroup) => {
    if (selectedGroup != null) {
      this.setState({ selectedGroup }, () => { });
    } else {
      this.setState({
        selectedGroup: "",
      });
    }
  };

  addMatiere = (selectedMatieres) => {
    var userPermissions = JSON.parse(localStorage.getItem("userPermissions"));
    var anneeScolaire = userPermissions[0].anneeScolaire;
    if (selectedMatieres != null) {
      this.setState({ selectedMatieres: selectedMatieres }, () => {
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/SelectedMatieresPratiques.php",
          type: "POST",
          data: {
            matiereId: selectedMatieres.value,
            anneeScolaire: anneeScolaire,
          },
          dataType: "json",
          success: (code_html, statut) => {
            this.setState({
              listMatieresPratiques: code_html,
            });
          },
        });
        jQuery.ajax({
          url:
            process.env.REACT_APP_API_URL +
            "Back_mediatraining/ListNoteNonBts.php",
          type: "POST",
          data: {
            groupeId: this.state.selectedGroup.value,
            query: "",
            limit: this.state.nbrEltperPage,
            page: 1,
          },
          dataType: "json",
          success: (code_html, statut) => {
            console.log(code_html);
            this.setState({
              nombreTotalElement: code_html.total,
              data: code_html.data,
              pending: false,
            });
          },
        });
      });
    } else {
      this.setState({
        selectedMatieres: "",
      });
    }
  };

  addTrimestre = (selectedTrimestre) => {
    this.setState({ selectedTrimestre }, () => {
      this.setState({
        blockListEleve: true,
      });
    });
  };

  changeNote = (indexOfLigne, indexOfColumn, event) => {
    var arrayEleve = this.state.listEleves;
    arrayEleve[indexOfLigne].note[indexOfColumn].noteMatiere = event;
    this.setState({ listEleve: arrayEleve });
  };

  addMatierePratique = (selectedMatieresPratiques) => {
    this.setState({ selectedMatieresPratiques: selectedMatieresPratiques });
  };

  render() {
    return (
      <div id="page-wrapper" style={{ minHeight: "263px", marginLeft: "0px" }}>
        <div className="container-fluid">
          <div className="row bg-title">
            <div className="col-lg-12">
              <h4 className="page-title">Les Notes</h4>
            </div>
          </div>
          {/* /.row */}
          <div className="row">
            <div className="col-sm-12">
              <div className="white-box">
                <h3>List des Notes :</h3>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Niveau</label>
                      <Select
                        isClearable={true}
                        isSearchable={true}
                        onChange={this.addNiveau}
                        options={this.state.listNiveau}
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Groupe</label>
                      <Select
                        isClearable={true}
                        isSearchable={true}
                        onChange={this.addGroup}
                        options={this.state.listGroupes}
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Matière</label>
                      <Select
                        isClearable={true}
                        isSearchable={true}
                        onChange={this.addMatiere}
                        options={this.state.listMatieres}
                      />
                    </div>
                  </div>
                </div>
                {this.state.listMatieresPratiques.length > 0 ? (
                  <div className="row">
                    <div className="col-md-6">
                      <div className="form-group">
                        <label> Sous Matière</label>
                        <Select
                          isClearable={true}
                          isSearchable={true}
                          onChange={this.addMatierePratique}
                          options={this.state.listMatieresPratiques}
                        />
                      </div>
                    </div>
                  </div>
                ) : null}
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Trimestre</label>
                      <Select
                        isClearable={true}
                        isSearchable={true}
                        onChange={this.addTrimestre}
                        options={this.state.listTrimestre}
                      />
                    </div>
                  </div>
                </div>
                {this.state.blockListEleve == true ? (
                  <div>
                    <button
                      style={{ borderRadius: "5px", marginLeft: "620px" }}
                      type="button"
                      className="btn btn-success"
                      onClick={this.ajoutNoteMatiere}
                    >
                      Confirmer
                    </button>
                  </div>
                ) : null}
              </div>
              <DataTable
                columns={this.state.columns}
                data={this.state.data}
                highlightOnHover={true}
                pagination={true}
                paginationServer={true}
                onChangePage={(page, totalRows) =>
                  this.newElement(page, totalRows)
                }
                paginationTotalRows={this.state.nombreTotalElement}
                paginationPerPage={this.state.nbrEltperPage}
                paginationRowsPerPageOptions={[5, 10, 15, 20, 25]}
                onChangeRowsPerPage={(currentRowsPerPage, currentPage) =>
                  this.perPage(currentRowsPerPage, currentPage)
                }
                paginationResetDefaultPage={this.state.resetFirstPage}
                paginationComponentOptions={{
                  rowsPerPageText: "Elements par page:",
                  rangeSeparatorText: "de",
                  noRowsPerPage: false,
                }}
                progressPending={this.state.pending}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default AjoutNote;
