import React, { Component, Fragment } from 'react'
import axios from 'axios'
import jQuery from 'jquery'
import { withRouter } from 'react-router-dom'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Select from 'react-select';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Slide, Zoom, Flip, Bounce } from 'react-toastify';
import { RadioGroup, Radio } from 'react-radio-group'

class ModifRemboursement extends Component {
    constructor(props) {
        super(props);
        this.state = {
            idRemboursement: '',
            dateRembours: '',
            selectedScol: '',
            listApprenant: [],
            selectedApprenant: '',
            listRecu: [],
            selectedNumRecu: '',
            description: '',
            mntRebours: 0,
            numPiece: '',
            tabModPay: [{ idRembours: '', modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }],
            modal: false,
            indexLignASupprimer: '',
            idRemboursementASupprimer: '',
            tabIdRemboursementDelete: []
        }
    }

    componentDidMount() {
        if (JSON.parse((localStorage.getItem('userPermissions'))) != null) {
            var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
            var anneeScolaire = userPermissions[0].anneeScolaire
            let idRemboursement = this.props.location.state.idRemboursement;
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/GetReboursById.php?id=' + idRemboursement).then(res => {
                console.log(res)
                var el = res.data[0];
                this.setState({
                    idRemboursement: el[0].idRemboursement,
                    dateRembours: new Date(el[0].date),
                    selectedScol: el[0].type,
                    selectedApprenant: el[0].apprenant,
                    selectedNumRecu: el[0].recu,
                    description: el[0].description,
                    mntRebours: el[0].mntRebours,
                    numPiece: el[0].numPiece,
                }, () => {
                    jQuery.ajax({
                        url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleveByScol.php',
                        type: 'POST',
                        data: {
                            selectedScol: this.state.selectedScol,
                            anneeScolaire: anneeScolaire
                        },
                        dataType: 'json',
                        success: (code_html, statut) => {
                            console.log(code_html)
                            this.setState({
                                listApprenant: code_html
                            });
                        }
                    });
                })
                var tabModPay = res.data[0]
                var arrayModPay = []
                for (var i = 1; i < tabModPay.length; i++) {
                    arrayModPay.push(tabModPay[i])
                }
                for (var j = 0; j < arrayModPay.length; j++) {
                    if (arrayModPay[j].typePay.value == 2) {
                        this.setState({
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { idRembours: arrayModPay[j].idRembours }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { modPay: arrayModPay[j].typePay }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { mntModPay: Number.parseFloat(arrayModPay[j].mntChec).toFixed(3) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { numCheque: arrayModPay[j].numChec }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { echeanceChec: new Date(arrayModPay[j].echeance) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { dateEncaiss: new Date(arrayModPay[j].dateEncaiss) }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { titulaireChec: arrayModPay[j].proprietaire }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { banque: arrayModPay[j].bank }) : el)),
                        });
                    }
                    else {
                        this.setState({
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { idRembours: arrayModPay[j].idRembours }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { modPay: arrayModPay[j].typePay }) : el)),
                            tabModPay: this.state.tabModPay.map((el, id) => (id === j ? Object.assign(el, { mntModPay: Number.parseFloat(arrayModPay[j].mnt).toFixed(3) }) : el)),
                        });
                    }
                }
            })
            axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectModePaiement.php').then(res => {
                console.log(res)
                this.setState({
                    listModPay: res.data
                })
            })
        }
    }

    updDateRembours = d => {
        this.setState({
            dateRembours: d
        });
    }

    updType = (selectedScol) => {
        if (selectedScol != null) {
            this.setState({ selectedScol: selectedScol }, () => {
                var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
                var anneeScolaire = userPermissions[0].anneeScolaire
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/SelectEleveByScol.php',
                    type: 'POST',
                    data: {
                        selectedScol: selectedScol,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            listApprenant: code_html
                        });
                    }
                });
            });
        }
    }

    updApprenant = selectedApprenant => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        this.setState({ selectedApprenant }, () => {
            if (selectedApprenant != null) {
                jQuery.ajax({
                    url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetRecuByEleve.php',
                    type: 'POST',
                    data: {
                        selectedScol: this.state.selectedScol,
                        idApprenant: selectedApprenant.value,
                        anneeScolaire: anneeScolaire
                    },
                    dataType: 'json',
                    success: (code_html, statut) => {
                        console.log(code_html)
                        this.setState({
                            listRecu: code_html
                        });
                    }
                });
            }
        });
    };

    updNumRecu = selectedNumRecu => {
        this.setState({ selectedNumRecu }, () => {
            jQuery.ajax({
                url: process.env.REACT_APP_API_URL + 'Back_mediatraining/GetMntRecu.php',
                type: 'POST',
                data: {
                    selectedScol: this.state.selectedScol,
                    idRecu: selectedNumRecu.value
                },
                dataType: 'json',
                success: (code_html, statut) => {
                    console.log(code_html)
                    this.setState({
                        mntRebours: code_html
                    });
                }
            })
        });
    };

    updDescript = (event) => {
        this.setState({
            description: event.target.value
        });
    }

    updMntRebours = (event) => {
        this.setState({
            mntRebours: event.target.value
        });
    }

    updNumPiece = (event) => {
        this.setState({
            numPiece: event.target.value
        });
    }

    additionLign = () => {
        let element = { idRembours: -1, modPay: '', mntModPay: '', numCheque: '', echeanceChec: new Date(), dateEncaiss: new Date(), titulaireChec: '', banque: '' }
        this.setState({
            tabModPay: [...this.state.tabModPay, element]
        })
    }

    updModPay = (selectedModPay, indexModPay) => {
        if (selectedModPay != null) {
            let modPay = selectedModPay;
            this.setState({
                tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { modPay: modPay }) : el))
            }, () => {
                if (this.state.tabModPay[indexModPay].modPay.value == 1) {
                    this.setState({
                        tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: '' }) : el)),
                        tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: '' }) : el)),
                        tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: '' }) : el)),
                        tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: '' }) : el)),
                        tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: '' }) : el)),
                    });
                }
            });
        }
    }

    updMntModPay = (event, indexModPay) => {
        let mntModPay = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { mntModPay: Number.parseFloat(mntModPay).toFixed(3) }) : el))
        });
    }

    updNumChec = (event, indexModPay) => {
        let numCheque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { numCheque: numCheque }) : el))
        });
    }

    updEcheanceChec = (d, indexModPay) => {
        let dateChec = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { echeanceChec: dateChec }) : el))
        });
    }

    updDateEncaiss = (d, indexModPay) => {
        let dateEncaiss = d;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { dateEncaiss: dateEncaiss }) : el))
        });
    }

    updTitulaireChec = (event, indexModPay) => {
        let titulaireChec = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { titulaireChec: titulaireChec }) : el))
        });
    }

    updBanque = (event, indexModPay) => {
        let banque = event.target.value;
        this.setState({
            tabModPay: this.state.tabModPay.map((el, id) => (id === indexModPay ? Object.assign(el, { banque: banque }) : el))
        });
    }

    toggleLign = (el, index) => {
        var idRemboursement = el.idRemboursement
        this.setState(prevState => ({
            modal: !prevState.modal,
            idRemboursementASupprimer: idRemboursement,
            indexLignASupprimer: index
        }));
    }

    deleteLign = () => {
        this.state.tabModPay.splice(this.state.indexLignASupprimer, 1)
        let idRemboursementASupprimer = this.state.idRemboursementASupprimer
        this.setState({
            tabIdRemboursementDelete: [...this.state.tabIdRemboursementDelete, idRemboursementASupprimer]
        });
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    nonDeleteLign = () => {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    modif = () => {
        var userPermissions = JSON.parse((localStorage.getItem('userPermissions')));
        var anneeScolaire = userPermissions[0].anneeScolaire
        var userId = userPermissions[0].idUser
        // convert date rembours
        let dateRembours = this.state.dateRembours
        let month = '' + (dateRembours.getMonth() + 1);
        let day = '' + dateRembours.getDate();
        let year = dateRembours.getFullYear();
        if (month.length < 2)
            month = '0' + month;
        if (day.length < 2)
            day = '0' + day;
        var convertDateRembours = [year, month, day].join('-');
        jQuery.ajax({
            url: process.env.REACT_APP_API_URL + 'Back_mediatraining/UpdRemboursement.php?id=' + this.state.idRemboursement,
            type: 'POST',
            data: {
                selectedScol: this.state.selectedScol,
                dateRembours: convertDateRembours,
                eleveId: this.state.selectedApprenant.value,
                recu: this.state.selectedNumRecu.value,
                description: this.state.description,
                mntRebours: this.state.mntRebours,
                type: this.state.selectedScol,
                modPay: this.state.tabModPay,
                tabIdRemboursementDelete: this.state.tabIdRemboursementDelete,
                anneeScolaire: anneeScolaire,
                userId: userId
            },
            dataType: 'json',
            success: (code_html, statut) => {
                if (code_html.Result == "OK") {
                    this.props.history.push('/Remboursement');
                }
                else if (code_html.Result == "KO") {
                    alert("erreur,l'un de vos champs est vide!!");
                }
            }
        });
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Modification remboursement</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="white-box">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Date remboursement</label>
                                            <DatePicker class="form-control ddate" style={{ border: 'none' }}
                                                selected={this.state.dateRembours}
                                                onChange={this.updDateRembours}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <RadioGroup
                                            name="typeScol"
                                            selectedValue={this.state.selectedScol}
                                            onChange={this.updType}>
                                            <label style={{ paddingRight: '5px' }}>
                                                <Radio value='1' />Formation
                                                    </label>
                                            <label style={{ paddingRight: '5px' }}>
                                                <Radio value='2' />Cycle
                                                    </label>
                                        </RadioGroup>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>eleve</label>
                                            <Select
                                                value={this.state.selectedApprenant}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updApprenant}
                                                options={this.state.listApprenant}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Numéro recu</label>
                                            <Select
                                                value={this.state.selectedNumRecu}
                                                isClearable={true}
                                                isSearchable={true}
                                                onChange={this.updNumRecu}
                                                options={this.state.listRecu}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Description</label>
                                            <input type="text" className="form-control" value={this.state.description} onChange={this.updDescript} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Montant à rembourser</label>
                                            <input type="number" className="form-control" value={Number.parseFloat(this.state.mntRebours).toFixed(3)} onChange={this.updMntRebours} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label>Numéro piéce</label>
                                            <input type="number" className="form-control" value={this.state.numPiece} onChange={this.updNumPiece} style={{ borderRadius: '5px', border: 'solid 1px #B3B3B3' }} />
                                        </div>
                                    </div>
                                </div>
                                <div className='row'>
                                    <h3 style={{ paddingLeft: '15px' }}>Mode de paiement :</h3>
                                    <table style={{ marginTop: '10px' }} className="table table-bordered">
                                        <thead>
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Mode paiement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Montant Paiement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Numéro cheque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Echéance cheque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Date encaissement</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Titulaire cheque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Banque</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Suppression</th>
                                            </tr>
                                        </thead>
                                        {this.state.tabModPay.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td className="col-md-3"><Select
                                                        value={el.modPay}
                                                        isClearable={true}
                                                        isSearchable={true}
                                                        onChange={(e) => this.updModPay(e, index)}
                                                        options={this.state.listModPay}
                                                    />  </td>
                                                    <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" value={el.mntModPay} onChange={(e) => this.updMntModPay(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                    {this.state.tabModPay[index].modPay.value == 2 ?
                                                        (<Fragment>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" value={el.numCheque} onChange={(e) => this.updNumChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.echeanceChec} onChange={(e) => this.updEcheanceChec(e, index)} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><DatePicker class="form-control ddate" style={{ border: 'none' }} selected={el.dateEncaiss} onChange={(e) => this.updDateEncaiss(e, index)} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" value={el.titulaireChec} onChange={(e) => this.updTitulaireChec(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" value={el.banque} onChange={(e) => this.updBanque(e, index)} style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                        </Fragment>) :
                                                        (<Fragment>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="number" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-2" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><input type="text" disabled style={{ textAlign: 'center', borderRadius: '5px' }} /></td>
                                                            <td className="col-md-1" style={{ textAlign: 'center' }}><button style={{ backgroundColor: '#D9534F', borderRadius: '5px' }} onClick={() => this.toggleLign(index)}><i style={{ color: 'white' }} className="fas fa-trash-alt"></i></button></td>
                                                        </Fragment>)}
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'flex-start', marginLeft: '15px' }}>
                                    <Button style={{ borderRadius: '5px' }} onClick={this.additionLign} color="secondary"><i className="fas fa-plus-circle"></i>Ajouter ligne</Button>
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px', marginTop: '10px' }}>
                                    <button style={{ width: '15%', borderRadius: '5px' }} type="button" className="btn btn-success" onClick={this.modif}>Confirmer</button>
                                </div>
                            </div>
                            <Modal isOpen={this.state.modal} toggle={this.nonDeleteLign} className={this.props.className} fade={false}>
                                <ModalHeader toggle={this.nonDeleteLign}> <div style={{ display: 'flex', justifyContent: 'center', width: '465px' }}><img width='30%' src="./images/deleteModal.png" alt=".." /></div></ModalHeader>
                                <ModalBody>
                                    <div style={{ fontFamily: 'Montserrat, sans-serif', FontSize: '14px', FontWeight: '700', LineHeight: '18.375px' }}>
                                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                                            <p>Êtes-Vous sûr de vouloir supprimer ce mode de paiement ?</p>
                                        </div>
                                        <div className='hvr-push' style={{ display: 'flex', justifyContent: 'space-around', marginBottom: '15px' }}>
                                            <div><button onClick={() => this.deleteLign()} style={{ backgroundColor: '#00FF7F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-up"></i>Oui</button></div>
                                            <div><button onClick={() => this.nonDeleteLign()} style={{ backgroundColor: '#D9534F', borderRadius: '5px', color: 'white' }}><i style={{ color: 'white' }} className="far fa-thumbs-down"></i>Non</button></div>
                                        </div>
                                    </div>
                                </ModalBody>
                            </Modal>
                            <ToastContainer transition={Flip} enableMultiContainer containerId={'A'} position={toast.POSITION.TOP_RIGHT} autoClose={2500} />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(ModifRemboursement)
