import React, { Component } from 'react'
import axios from 'axios'

class HistoriqueConnexion extends Component {
    constructor(props) {
        super(props);
        this.state = {
            historique: [],
            realisation: []
        }
    }

    componentDidMount() {
        axios.get(process.env.REACT_APP_API_URL + 'Back_mediatraining/HistConnexion.php').then(res => {
            this.setState({
                historique: res.data
            })
        })
    }

    histUser = (realisation) => {
        this.setState({
            realisation: realisation
        }, () => {
            this.setState(prevState => ({
                modal: !prevState.modal,
            }));
        });
    }

    toggle = () => {
        this.setState(prevState => ({
            modal: !prevState.modal,
        }));
    }

    render() {
        return (
            <div id="page-wrapper" style={{ minHeight: '263px', marginLeft: '0px' }}>
                <div className="container-fluid">
                    <div className="row bg-title">
                        <div className="col-lg-12">
                            <h4 className="page-title">Historique</h4>
                        </div>
                        {/* /.col-lg-12 */}
                    </div>
                    {/* /.row */}
                    <div class="row">
                        <div class="col-sm-12">
                            <div class="white-box">
                                <div class="row">
                                    <table class="table">
                                        <thead >
                                            <tr>
                                                <th style={{ textAlign: 'center' }} scope="col">Users</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Date connexion</th>
                                                <th style={{ textAlign: 'center' }} scope="col">Historique</th>
                                            </tr>
                                        </thead>
                                        {this.state.historique.map((el, index) =>
                                            <tbody>
                                                <tr>
                                                    <td style={{ textAlign: 'center' }}><p>{el.user}</p></td>
                                                    <td style={{ textAlign: 'center' }}><p>{dateConnex}</p></td>
                                                    <td style={{ textAlign: 'center' }}><p onClick={() => this.histUser(el.realisation)}><i style={{ color: '#87CEEB', marginRight: '5px' }} class="fas fa-chalkboard-teacher"></i></p></td>
                                                </tr>
                                            </tbody>
                                        )}
                                    </table>
                                </div>
                                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className} fade={false}>
                                    <ModalHeader toggle={this.toggle}><div style={{ display: 'flex', justifyContent: 'space-between' }}><div><i style={{ color: '#87CEEB' }} class="fas fa-chalkboard-teacher"></i></div><div style={{ marginLeft: '5px', fontWeight: 'bold' }}><p>Historique action utilisateur</p></div></div></ModalHeader>
                                    <ModalBody>
                                        <div>
                                            {this.state.realisation.map((el, index) =>
                                                <p>{'Action ' + (index + 1) + " : " + el.action + ' element a' + ' ' + el.dateAction + ' dans la table ' + el.nomTable}</p>
                                            )}
                                        </div>
                                    </ModalBody>
                                </Modal>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default HistoriqueConnexion;
